css:
@-moz-document domain("bluebird-hd.org") {
table [width="100%"] [class="main"] [border="1"] [cellspacing="0"] [cellpadding="5"]{
background-image: url("https://i.imgur.com/ZZbk64E.png")
}
.mainouter a img[src="./themes/TBDev/images/default_avatar.gif"]{
 display: block;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url("https://i.imgur.com/ZZbk64E.png") no-repeat!important;
  width: 100px;
  height: 100px;
  padding-left: 100px; /* Equal to width of new image */
}
*:not(select){
    background:none!important;
	border-color:#444!important;
}
select{
    border-color:#444;
}
body > table:nth-child(4) > tbody,td [src="themes/TBDev/images/news_cellpic_left.gif"], td [src="themes/TBDev/images/news_cellpic_right.gif"], body > table:nth-child(3) > tbody > tr > td{
      display:none!important;
}
font[color="#0F3A4F"]{
    color:#888!important;
}
font[color="green"]{
    color:#66ff66!important;
}
font[color="darkred"]{
    color:#ff0000!important;
}
span[style="color: black"]{
    color:white!important;
}
span[style="color:#3a0249"]{
    color:#8904ae!important;
}
span[style="color:#01301a"]{
    color:#037d44!important;
}
.bmm img{
        position: static!important;
}
.bcap{
        position: static!important;
}
.topnav{
    box-shadow: 5px 5px 1px #333;
    overflow: visible!important;
    background:#222!important;
    top: 0;
    left: 0;
    right:0;
    position: fixed;

}
body{
        margin-top:0px!important;
    padding-top:0px!important;
    background:#111 !important;
    background-image:url("https://transparenttextures.com/patterns/black-twill.png") !important;
}
td.colhead{
    background:#111!important;
}
table #highlighted,  table.main, body > p:nth-child(5) > table.mainouter > tbody > tr > td.outer > table.embedded > tbody, body > p:nth-child(5) > table.mainouter > tbody > tr > td.outer{
    border:1px solid transparent!important;
        background:#222!important;
    border-radius:10px!important;
}
table.fblock{
        background:#222!important;
    border-radius:10px!important;
}

.spoiler-wrap{
    color:#888!important;
    background:#333!important;
}
input{
    color:white!important;
    background:#333!important;
}
 .bgcolor4 td, .logbg_col, option, textarea, #area{
    background:#333!important;
}
td[style="padding: 10px; background: orange"]
{
    background:blue!important;
}
::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #000;
    width: 14px;
    height: 14px;
}
::-webkit-scrollbar-button {
    display: none;
}
::-webkit-scrollbar-thumb {
    background: #555;
}
::-webkit-scrollbar-thumb:not(:active):hover {
    background: #383838;
}
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active {
    background: #5c5c5c;
}
}
