}
.user-main .about .secondary {
    background-color: #414246;
    border-color: #4a4b4e;
}
.user-main .about .secondary dd {
    color: #d3d4da;
}
#share-link {
    background-color: #252629;
}
#share-link input[type=text] {
    width: calc(100% - 21px);
}
div.poll {
    border-color: #4a4b4e;
}
div.poll .poll-info,
div.poll .poll-buttons {
    border-color: #4a4b4e;
}
div.poll .results .bar-back {
    background-color: #303135;
}
div.poll .results .bar {
    background-color: #9a9fa7;
}
.posters a.group-Roblox_Staff:after {
    content: '';
    display: block;
    background-image: url(https://static.rbxcdn.com/images/Logo/logo_O_01302017.svg);
    background-size: 100% 100%;
    width: 16px;
    height: 16px;
    position: absolute;
    margin-top: -12px;
    filter: sepia() saturate(2000%) hue-rotate(-70deg) saturate(800%) drop-shadow(1px 2px 0px rgba(0, 0, 0, 0.2));
}
.posters a:first-of-type:not(:last-of-type).latest.group-Roblox_Staff:after {
    margin-top: -16px;
}
a.hashtag:visited, a.hashtag:hover {
    color: #55fb82;
}
a.hashtag {
    color: #4aec76;
}
}
