css:
@-moz-document domain("4chan.org") {
body {
    background: #f5f5f5;
    color: #000;
    font-family: 'Roboto', 'Helvetica', 'Arial', sans-serif;
    font-size: 14px;
    font-weight: 400;
    line-height: 20px;
    padding: 0;
    margin: 0;
}
.board {
    background: #eff5ff;
    border-top: 1px solid #ccc;
    margin-bottom: 50px;
    padding-bottom: 25px;
}
.is_index .board {
    padding-bottom: 50px
}
a,
a:visited,
.quoteLink,
.quotelink,
.deadlink,
div#boardNavMobile .pageJump a,
.persistentNav .pageJump a,
.summary a.replylink,
div.post div.postInfo span.postNum a:visited,
div.post div.postInfo span.postNum a.replylink {
    color: rgb(83, 109, 254)!important;
    text-decoration: none!important;
}
a:hover,
.quoteLink:hover,
.quotelink:hover,
.deadlink:hover,
.summary a.replylink:hover,
.persistentNav .pageJump a:hover,
div#boardNavMobile .pageJump a:hover,
div.post div.postInfo span.postNum a:hover,
.posteruid .hand:hover {
    color: rgb(83, 109, 254)!important;
    text-decoration: underline!important;
}
.postInfo a.postMenuBtn,
.postInfo a.postMenuBtn:hover {
    color: rgb(83, 109, 254)!important;
    text-decoration: none!important;
}
div.postContainer {
    display: block;
    width: 95%;
    margin: 8px auto 0 auto;
}
.thread {
    margin-top: 10px!important;
    padding-top: 15px;
    border-top: 1px solid #ccc;
}
.thread:first-child {
    padding-top: 0;
    border-top: none;
}
.md-plus-btn {
    border-radius: 50%;
    font-size: 24px;
    height: 56px;
    margin: auto;
    min-width: 56px;
    width: 56px;
    padding: 0;
    overflow: hidden;
    box-shadow: 0 1px 1.5px 0 rgba(0, 0, 0, .12), 0 1px 1px 0 rgba(0, 0, 0, .24);
    position: relative;
    line-height: normal;
    position: fixed;
    right: 25px;
    bottom: 25px;
}
div.boardBanner>div.boardTitle {
    margin-top: 40px;
    font-size: 36px;
    color: #676767;
    margin-bottom: 40px;
    font-family: 'Roboto', 'Helvetica', 'Arial', sans-serif;
    font-weight: normal;
    letter-spacing: 0;
}
:-ms-input-placeholder {
    color: rgba(0, 0, 0, .35)
}
::-webkit-input-placeholder {
    color: rgba(0, 0, 0, .35)
}
:-moz-placeholder {
    color: rgba(0, 0, 0, .35)
}
::-moz-placeholder {
    color: rgba(0, 0, 0, .35)
}
#togglePostFormLink,
.ad-plea,
#blotter tfoot td,
#ctrl-top,
div.post div.postInfo span.postNum,
div.pagelist div.pages {
    color: transparent
}
table.postForm>tbody>tr>td:first-child,
.thread-stats {
    color: #000
}
.postInfo .backlink a.quotelink,
.postInfo .backlink a.quotelink:hover {
    color: rgb(83, 109, 254)!important
}
.ws input[type="text"],
.nws input[type="text"] {
    border: none!important;
    border-bottom: 1px solid rgba(0, 0, 0, .12)!important;
    font-size: 14px!important;
    font-family: "Helvetica", "Arial", sans-serif;
    padding: 4px!important;
    -webkit-transition: border-bottom-color 0.2s;
    transition: border-bottom-color 0.2s;
}
.ws input[type="text"]:focus,
.ws #quickReply input[type="submit"]:focus {
    border-bottom: 1px solid rgb(99, 130, 255)!important
}
.nws input[type="text"]:focus,
.nws #quickReply input[type="submit"]:focus {
    border-bottom: 1px solid rgb(99, 130, 255)!important
}
.tomorrow .extPanel,
.recaptcha_input_area #recaptcha_response_field {
    border: 0!important
}
.ws input[type="submit"],
.nws input[type="submit"],
button {
    border: none;
    border-radius: 2px;
    position: relative;
    height: 28px;
    min-width: 64px;
    display: inline-block;
    text-transform: uppercase;
    outline: none;
    cursor: pointer;
    background: rgb(99, 130, 255);
    color: #fff;
    font-size: 14px;
    font-family: 'Roboto', 'Helvetica', 'Arial', sans-serif;
    line-height: 28px;
    margin-left: 8px;
}
.nws input[type="submit"],
.nws button {
    background: rgb(99, 130, 255)
}
#qrCaptchaContainerAlt {
    margin-bottom: 8px
}
#postForm input[type="text"] {
    margin: 4px 0
}
#postForm td:first-child {
    text-align: right;
    padding-right: 10px;
}
.ws textarea,
.nws textarea {
    border: 0!important
}
.ws textarea:focus,
.nws textarea:focus {
    border: 0!important
}
table#postForm td {
    background: transparent;
    border: 0;
    font-weight: normal;
}
#toggleMsgBtn {
    margin-left: 5px!important
}
.rules {
    text-align: center
}
#qrHeader {
    background: #676767!important;
    color: #fff!important;
}
#qrHeader a {
    color: #fff!important
}
#search-box {
    height: inherit;
    line-height: inherit;
    margin: 0;
    padding: inherit;
}
#ctrl-top {
    padding-bottom: 10px;
    border-top: 1px solid #ddd;
    padding-top: 10px;
    text-align: center;
}
.reply:target,
.reply.highlight {
    background: #eee!important;
    padding: 10px!important;
    border: 0!important;
}
.navLinks+hr,
.open-qr-wrap,
.board hr,
#bannerCnt,
#ctrl-top+hr,
#ctrl-top>hr {
    display: none
}
.navLinks {
    padding-bottom: 8px;
    color: transparent;
    width: 95%;
    margin: auto;
}
.navLinks label {
    color: rgb(83, 109, 254)
}
.navLinks label+span {
    color: #000
}
.navLinksBot {
    margin-top: 30px;
    text-align: left;
}
div#boardNavDesktop,
div#boardNavDesktopFoot,
div#boardNavMobile {
    background: #fff;
    padding: 5px 10px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .08), 0 3px 1px -2px rgba(0, 0, 0, .06), 0 1px 5px 0 rgba(0, 0, 0, .04);
    margin-bottom: 25px;
    color: transparent!important;
    font-size: 14px;
}
div#boardNavMobile {
    color: #000;
    border: 0;
}
div#boardNavDesktopFoot {
    margin-bottom: 0;
    margin-top: 25px;
    padding-top: 25px;
    padding-bottom: 25px;
}
div#boardNavDesktop a,
div#boardNavDesktopFoot a {
    margin-left: -3px;
    margin-right: -3px;
    padding: 0;
}
.persistentNav .pageJump a {
    margin: 0;
    padding-right: 5px;
}
div.pagelist {
    background: #fff;
    margin-top: 25px;
    margin-bottom: 25px;
    border: 0;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    margin-left: 25px;
    padding: 8px;
    border-radius: 2px;
}
div#absbot {
    background: #fff;
    color: #999;
}
div#absbot #footer-links a {
    text-decoration: none!important;
    color: rgb(83, 109, 254)!important;
}
div#absbot #footer-links a:hover {
    text-decoration: underline!important
}
.bottomCtrl {
    display: none
}
.ad-plea-bottom+hr {
    display: none
}
div#boardNavDesktopFoot {
    margin-bottom: 0
}
div.post,
.extPanel,
div.reply,
.dd-menu ul {
    background: #fff;
    border-radius: 2px;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    border: none;
}
div.pagelist div.cataloglink {
    border: 0
}
div.post {
    max-width: 75%;
    margin: 0;
    padding: 10px;
}
.dd-menu li {
    padding: 6px 10px!important;
    border: 0!important;
}
.dd-menu li:hover {
    background-color: #eee!important
}
#quote-preview {
    border: 0!important;
    padding: 10px!important;
}
div.op {
    display: block;
    max-width: none;
}
.fileText {
    color: #999;
    font-size: 12px;
    margin-top: 2px;
}
.op .fileText {
    margin-top: -5px;
    margin-bottom: 5px;
}
.fileText a {
    text-decoration: none;
}
.posteruid,
.dateTime {
    color: #999
}
.postInfo input[type="checkbox"] {
    display: none
}
div.sideArrows {
    display: none
}
hr {
    border: 0;
    border-bottom: 1px solid #ddd;
}
div.post:after {
    display: block;
    content: ' ';
    clear: both;
}
div.post div.file .fileThumb img {
    object-fit: cover
}
/*1*/

.postMessage {
    margin-left: 20px;
    margin-top: 5px;
}
#boardNavDesktop::after {
    content: ' ';
    display: block;
    clear: both;
}
span.summary {
    border-radius: 0 0 2px 2px;
    background-color: #f5f5f5;
    width: 95%;
    display: block;
    margin: -1px auto 0 auto;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    border-top: 1px solid rgba(0, 0, 0, 0.1);
    text-indent: 10px;
    padding: 10px 0;
}
#togglePostFormLink > a {
    border: none;
    border-radius: 2px;
    position: relative;
    height: 28px;
    min-width: 64px;
    display: inline-block;
    text-transform: uppercase;
    outline: none !important;
    cursor: pointer;
    background: #2489DA !important;
    color: #fff !important;
    font-size: 14px;
    font-family: 'Roboto', 'Helvetica', 'Arial', sans-serif !important;
    line-height: 28px;
    padding: 5px;
}
#togglePostFormLink > a:hover {
    text-decoration: none !important;
}
.pageSwitcherForm > input[type="submit"] {
    border: none;
    border-radius: 2px;
    position: relative;
    height: 28px;
    min-width: 64px;
    display: inline-block;
    text-transform: uppercase;
    outline: none;
    cursor: pointer;
    background: rgb(244, 67, 54);
    color: #fff;
    font-size: 14px;
    font-family: 'Roboto', 'Helvetica', 'Arial', sans-serif;
    line-height: 28px;
    margin-left: 8px;
}
/*ADDED 4CHANX Support*/

.yotsuba:root #header-bar.dialog {
    background-color: rgba(255, 255, 255, 0.85);
}
.yotsuba:root .qr-link {
    border-color: rgb(204, 204, 204) rgb(204, 204, 204) rgb(204, 204, 204);
    background: linear-gradient(#eff5ff, #e1e0e0) repeat scroll 0% 0% transparent;
}
.yotsuba:root .qr-link:hover {
    background: #eff5ff;
}
.yotsuba:root .dialog {
    background-color: #eff5ff;
    border-color: #000;
}
a.qr-link {
    border: none;
    border-radius: 2px;
    position: relative;
    height: 28px;
    min-width: 64px;
    display: inline-block;
    text-transform: uppercase;
    outline: none !important;
    cursor: pointer;
    background: #2489DA !important;
    color: #fff !important;
    font-size: 14px;
    font-family: 'Roboto', 'Helvetica', 'Arial', sans-serif !important;
    line-height: 28px;
}
a.qr-link:hover {
    text-decoration: none !important;
}
:root.yotsuba #header-bar.dialog {
    background-color: rgb(229, 229, 229) !important;
}
:root.yotsuba #header-bar,
:root.yotsuba #notifications {
    color: #CCC !important;
}
:root.yotsuba .dialog {
    border-color: #CCCCCC !important;
}
:root.yotsuba div.boardTitle {
    text-shadow: 2px 2px 2px #AFAFAF !important;
}
:root.yotsuba .suboption-list > div:last-of-type {
    background-color: #F5F5F5 !important;
}
#fourchanx-settings {
    background: #F5F5F5 !important;
}
:root.yotsuba #fourchanx-settings fieldset,
:root.yotsuba .section-main div::before {
    border-color: #C1C1C1 !important;
}
:root.yotsuba .dialog {
    background-color: #E5E5E5 !important;
}
input[type=text]:focus,
input[type=password]:focus,
input:not([type]):focus,
textarea:focus {
    border: 1px solid #1CBAEA!important;
}
:root.yotsuba .field:focus,
:root.yotsuba .field.focus {
    border-color: #1CBAEA !important;
}
div.postContainer {
    width: 100% !important;
}
.board>.thread>div.postContainer {
    width: 95% !important
}
div.post {
    max-width: 100% !important;
}
.catalog-link {
    overflow: hidden !important;
}
.catalog-post {
    transition: margin .3s ease-in-out;
}
