css:
@-moz-document domain("animekompi.web.id") {
gj {
    color: #dfdfdf;
    background-color: #2c2c36;
}
body {
    background: #23232d;
    color: #dfdfdf;
}
#content {
    background: #23232d;
    color: #dfdfdf;
    border-bottom: 10px solid #23232d;
}
.postbody .boxed .right .lts ul li .dtl {
    background: #2c2c36;
}
.postbody .boxed .right .lts ul li .dtl h2 {
    color: #dfdfdf;
}
.postbody p {
    color: #dfdfdf !important;
    z-index: 99999;
}
.postbody .boxed .right .lts ul li .dtl span {
    color: #dfdfdf;
}
.postbody .boxed .right .lts ul li .thumb {
    border: 1px solid #2c2c36;
}
.recommended {
    background: #2c2c36;
}
.recommended a {
    color: #dfdfdf;
}
.wp-pagenavi {
    background: #2c2c36;
}
.wp-pagenavi a,
.wp-pagenavi span {
    background: #23232d;
    border: 1px solid #23232d;
}
.wp-pagenavi a:hover,
.wp-pagenavi span.current {
    border-color: #23232d;
    ;
}
#sidebar .section h3 {
    border: 3px solid #2c2c36;
    background: #2c2c36;
}
#sidebar .section ul li {
    border: 1px solid #23232d;
}
#sidebar .section ul {
    border: 1px solid #23232d;
}
.ppr li .right {
    background: #2c2c36;
}
.ppr li .right .latest a {
    color: #dfdfdf;
}
.cads .mainads {
    border: 1px solid #23232d;
}
.cads .addads {
    border: 1px solid #23232d;
}
.ppr li .bor {
    border: 1px solid #23232d;
}
.postbody .boxed .right .lts ul li .dtl h1 {
    color: #dfdfdf;
}
[title~="shadow_osx"] {
    border: 1px solid #23232d;
}
.epsc {
    color: #dfdfdf;
}
.comment-body {
    background-color: #2c2c36;
    border: 1px solid #23232d;
}
.commentlist li .comment-meta {
    background: #23232d;
}
.comment-author.vcard {
    background: #23232d;
}
.comment-author cite {
    color: #dfdfdf;
}
.commentlist li .comment-author .fn a {
    color: #dfdfdf;
}
.animeinfo .right .related li .rights {
    background: #2c2c36;
}
.animeinfo .right .related li .rights .cat a {
    color: #dfdfdf;
}
.animeinfo {
    border-top: 1px solid #23232d;
    border-bottom: 1px solid #23232d;
}
div[style] {
    background-color: #23232d !important;
    border: 1px solid #23232d !important;
    color: #dfdfdf !important;
}
.commentlist li .reply a {
    background-color: #23232d;
    border: 1px solid #23232d;
    color: #dfdfdf;
}
#respond #comment {
    -webkit-box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.12) inset;
    box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.12) inset;
    background: #2c2c36;
    background: rgb(44, 44, 54);
    border: 1px solid #c9c9c9;
    border: 1px solid rgba(0, 0, 0, 0.2);
}
#respond input {
    background: #2c2c36;
    border: 1px solid #23232d;
}
#respond #submit {
    background: #2c2c36;
    border: 1px solid #23232d;
    color: #dfdfdf;
}
.shadow_osx {
    border: 1px solid #23232d;
}
blockquote,
q {
    background: #23232d;
    border-left: 3px solid #2c2c36;
}
hr {
    display: block;
    height: 1px;
    border: 0;
    border-top: 4px solid #2c2c36;
    margin: 1em 0;
    padding: 0;
}
.onpop li span {
    color: #dfdfdf;
}
.onpop li span a {
    color: #dfdfdf;
}
.onpop li img {
    border: 2px solid #2c2c36;
}
.page h1 {
    border-bottom: 2px solid #2c2c36;
}
.genres li a {
    background: #2c2c36;
    border: 1px solid #2c2c36;
}
.genres li a:hover {
    background: #23232d;
    color: #FFF;
    text-decoration: none;
    border: 1px solid #0176c9;
    box-shadow: 0 1px 1px #0176c9;
}
.movlist ul li a {
    color: #dfdfdf;
}
.movlist span {
    border-bottom: 2px solid #2c2c36;
}
.listres li img {
    border: 1px solid #2c2c36;
}
.listres li {
    border-bottom: 2px solid #2c2c36;
}
.listres li .rtf .epf {
    background: #2c2c36;
}
.listres li .rtf .epf .bot {
    color: #dfdfdf;
}
.listres h2 a {
    color: #dfdfdf;
}

.animeinfos .listinfo ul li:nth-child(even) {
    background: #212129;
}

.animeinfos .listinfo ul li:nth-child(odd) {
    background: #2c2c36;
}

.animeinfos .listinfo ul li {
	color: #dfdfdf;
    border: 1px solid #2c2c36;
}

.animeinfos .listinfo ul li b {
	color: #dfdfdf;
}

.animeinfos .listinfo ul li a {
    color: #dfdfdf;
}

.animeinfos .listinfo {
	border: 1px solid #2c2c36;
    background: #23232d;
}

.animeinfos .episodelist ul li:nth-child(odd) {
    background: #2c2c36;
}
.animeinfos .episodelist ul li:nth-child(even) {
    background: #212129;
}
.animeinfos .episodelist ul li a {
    color: #dfdfdf;
}

.animeinfos .episodelist ul li .lefttitle {
    border-right: 1px solid #23232d;
}

.animeinfos .episodelist ul li .leftoff {
	border-right: 1px solid #23232d;
}

.animeinfos .episodelist ul li .rightoff {
	border-right: 1px solid #23232d;
}

.animeinfos .episodelist ul li:hover a {
    background: none;
    color: #fff;
}

.animeinfos .desc .latestest {
	border: 1px solid #2c2c36;
}

.animeinfos .desc .latestest .latestepisode {
	color: #dfdfdf;
}
}
