css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("everything2.com") {
body {font-family:Helvetica,Arial,sans-serif!important;}
h1#e2logo {background:none!important;font-size:4em!important;line-height:1.5em;height:100px!important;}
h1#e2logo a {color:#fff!important;padding:0px!important;text-decoration:none;text-transform:lowercase;}
#sidebar {position:absolute;right:20px;float:none!important;margin:0px!important;padding-top:5px;}
div.nodelet h2 {font-size:1.3em!important;color:#ddd!important;line-height:1.8em;cursor:pointer;text-transform:lowercase;}
div.nodelet_content {display:none;}
div#nodelet_170070 div.nodelet_content {display:block;}
div#notifications {position:relative;color:#666!important;}
div#notifications a {padding-right:2px;}
div#notifications a:first-child {padding-right:15px;}
div#notifications p#weblogs {position:absolute;top:0px;right:0px;margin:0px!important;}
div#notifications p#servertime {color:#999;position:absolute;top:0px;left:500px;margin:0px!important;}
div#notifications p#servertime br {display:none;}
div#notifications p#servertime a {color:#98a9b3!important;}  
p#printlink {display:none;}
div#loglinks {width:100%!important;float:none!important;border:none!important;max-width:100%!important;padding:0px!important;margin-bottom:15px!important;}
.nodetitle { border-bottom: 1px #ddd solid!important; margin: 20px 0px 5px!important;font-size:2em!important; } 
#mainbody {max-width:960px;margin-top:5px!important;}
.wu_footer td {border:none!important;}
.writeup_title { border: none!important; background-color: #eee!important; }
.writeup_text { background-color: #fcfcfc!important; }
.wu_footer { background-color: #eee!important; }
#softlinks td {border:none!important;text-align:left!important;padding-left:10px!important;}
#cooluserpicks, #staff_picks, #creamofthecool, #softlinks td, .nodelet {background-color:#fff!important;}
}
