css:
@-moz-document domain("dinfilme.ro")
{
#main {
    background-color: #252527;
}

h1, h2, h3, h4, h5, h6 {
    color: #cecece;   
}

body {
    color: #ababab;
}

a, a:visited, a:hover, a:focus, a:active {
    color: #c3c3c3;
}

.hsc-button.hsc-buttonskin-none a.hsc-link span {
    background-color: #202021;
}

a#hsc-btn-hideshow {
    color: #5d5d5d;
}

div#inline_banner {
    background-color: #1d1d1f;
    border: 1px dotted #4c4c4c;
}
h4.widget-title {
    color: #60626d;
}
.column.nine p a {
    color: #ababab;
}
footer.site-footer {
    background-color: #202021;
}
footer.site-footer, footer.site-footer a, footer.site-footer ul.meta-social li a {
    color: #494952;
}
footer.site-footer h1, footer.site-footer h2, footer.site-footer h3, footer.site-footer h4, footer.site-footer h5, footer.site-footer h6 {
    color: #2e3035;
}
.button, input.button, input[type="button"], input[type="reset"], input[type="submit"] {
    background-color: #d65f31;
    color: #e2e2e2 !important;
}
input:not([type="button"]), textarea, textarea.plain {
    color: #8e8e8e;
}
div#recent-posts-5 li {
    border-top: 1px dotted #3a3a3a;
}
div#recent-posts-5 h4.widget-title {
    border-bottom: 1px solid #3a3a3a;
}
.blog.masonry-style article.three .blog-excerpt, .blog.masonry-style article.four .blog-excerpt, .blog-section.masonry-style article.three .blog-excerpt, .blog-section.masonry-style article.four .blog-excerpt {
    background-color: #202021;
}
@media handheld, only screen and (min-width: 48em){
.blog.masonry-style article.three .blog-excerpt-inner:after, .blog.masonry-style article.four .blog-excerpt-inner:after, .blog-section.masonry-style article.three .blog-excerpt-inner:after, .blog-section.masonry-style article.four .blog-excerpt-inner:after {
    position: absolute;
    right: 0;
    bottom: 0;
    left: 0;
    margin: 0 0.556em 0.556em 0.556em;
    height: 0.333em;
    background: rgba(255, 255, 255, 0);
    background: -webkit-linear-gradient(top, rgba(255, 255, 255, 0), rgb(32, 32, 33));
    background: linear-gradient(top, rgba(255, 255, 255, 0), rgba(255, 255, 255, 1));
    content: " ";
}
}
@media screen and (max-width: 480px){
.entry-content h3 {
    color: #929292;
}
}
@media screen and (max-width: 480px){
.entry-content h3 {
    color: #cfdcdc;
}
}
#vot {
    border: dotted 1px #484848;
    background-color: #252527;
}
section.row.section {
    background-color: #1d1d1d!important;
}
hr {
    border-top: 1px solid #404040;
}
blockquote {
    border-left: 2px solid #404040;
}
blockquote, q {
    color: #cecece
}
}
