css:
@-moz-document url(https://mcstacker.bimbimma.com/) {

	body {
		background-color: #222227;
		color: #8e8e8e;
	}

	textarea, input, button, select {
		background: #131313;
		color: #8e8e8e;
		outline: none;
		border: 1px solid #2d2d2d;
	}

	textarea {
		max-width: 100%;
		min-width: 100%;
		min-height: 100px;
		max-height: 800px;
	}

	.mButton {
		background-color: #131313;
		color: #8e8e8e;
	}
	.navButton {
		color: #1356d0;
		background-color: #131313;
	}

	a {
		color: #fff;
		text-decoration: underline;
		transition: all 0.3s;
	}

	a:hover {
		color: rgba(60, 60, 60, 0.39);
	}
}
