css:
/* Ipernity neutral dark */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ipernity.com")

{

/* V1.04 by Spotomy, Finland */
	
/*/////////////////////////////////////// COMMON /////////////////////////////////////////*/


/* ipernity light blue: #6699d0 */
/* ipernity dark blue: #356098 */
	
/*text&background*/

html, body, tbody, thead, th, tr, blockquote, font, form, footer {
	color: #666 !important;
	background: #222 !important;
	background-color: #222 !important;
}

p, h1, h2, h3, h4, h5, h6 { /* eg. headline "Your news in a flash" */
	color: #888 !important;
}

td.tcat, td.thead, td.vbmenu_control, center {
	background: #222 !important;
}

strong, small { /* some minor headlines in the side boxes */
	color: #777 !important;
}

select, input, span, code, textarea, pre, label, div, dd, td {
	color: #aaa !important;
}

.v2 * { /* Border anywhere, not specificallly defined, will be this color */
	border-color: #393939 !important;
}

/* --- links --- */

a {
	color: #6699d0 !important; /* ipernity color */
	outline: 0 !important; /* the usual dotted outline */
}

a.ellip:hover {
	filter: brightness(130%) !important; 
	text-decoration: none !important;
}

a > strong:hover {
	filter: brightness(130%) !important;
	text-decoration: none !important;
}

a:visited {
	color: #6699d0 !important; /* same as unvisited */
}

pre, codecolor { /* highlight */
	background: #222 !important;
}

.widgetComments { /* Comment box on image page */
	background-color: #222 !important;
	color: #000;
}

.v2 .button { /* All buttons */
	background-image: none !important;
	background-color: #444 !important;
	opacity: 1 !important;
	border: none !important;
	border-radius: 5px !important;
	text-shadow: none !important;
	box-shadow: 0 1px 3px #222 !important;
	color: none !important;
}

.v2 .button:hover { /* All buttons hovered */
	filter: brightness(120%) !important;
	border: 0 !important;
	outline: 0 !important;
}

.v2 .button.lightened { /* Toolbox in Your albums page and New aerticle box in Your articles page */
	background-color: #444 !important;
	color: #888 !important;
	height: 15px !important;
	padding-right: 13px !important;
	padding-left: 11px !important;
}

.v2 .button.submit, .v2 .button.confirm { /* Blue button */
	background-color: #369 !important;
	color: #abc !important;
}

.v2 .button.cancel, .v2 .button.reset { /* Toolbox in Your albums page and New aerticle box in Your articles page */
	background-color: #333 !important;
	color: #666 !important;
}

.v2 .small.button { /* Small OK button */
	background-color: #494949 !important;
	color: #999 !important;
	margin-bottom: 5px !important;
}

hr.divider { /* All divider lines, used to be dotted */
	opacity: 0.5 !important;
	border-style: solid !important;
}

span.unread, span.new, div.unread, div.new { /* "NEW" badge */
	background-color: #373 !important;
	color: #bbb !important;
	font-size: 6pt !important;
	padding-left: 3px !important;
	padding-right: 3px !important;
	margin-bottom: 2.5px !important;
	border-radius: 2px !important;
}

.v2 div.lightbox, .v2 a#showLightBox { /* Lightbox */
	background: none !important;
	background-color: #222 !important;
	padding-bottom: 5px !important;
}

.v2 div.lightbox > .DocNav.Lightbox.Hover:hover { /* Lightbox hovered */
	background-color: #555 !important;
	outline: none !important;
	filter: invert(100%) !important;
}

.v2 a#showLightBox:hover { /* Lightbox hovered */
	background: none !important;
	background-color: #333 !important;
	outline: none !important;
}
	
.Share.S0, .Share.S1, .Share.S2, .Share.S3, .Share.S7, .Share.S31 { /* Content "visibile by" icons */
	opacity: 0.5 !important;
}

s.License.CR, s.License.CC, s.License.NL { /* License type icons */
	opacity: 0.3 !important;
}

s.License.ND, s.License.BY { /* License type icons */
	filter: brightness(30%) !important;
}

s.Misc.Stats, s.Lightbox.Fave, s.Lightbox.Comments { /* Items details icons: view number, comments number etc */
	opacity: 0.4 !important;
}

div.oneClick { /* Content visible to whom -tool etc */
	color: #666 !important;
}

div.oneClick:hover { /* Content visible to whom tool hovered */
	color: #aaa !important;
	background-color: #222 !important;
}

div.oneClick.edit { /* When menu is visible */
	color: #aaa !important;
	background-color: #222 !important;
}

div.oneClick.edit:hover { /* When menu is visible but button is hovered */
	color: #aaa !important;
	background-color: #222 !important;
}

div.oneClick.highlight { /* Content visible to whom tool hovered */
	color: #aaa !important;
	background-color: #222 !important;
}

div.oneClick.modified { /* Content visible to whom tool after modification */
	color: #666 !important;
	background-color: #222 !important;
}

div.oneClick.modified:hover { /* Content visible to whom tool after modification, hovered */
	color: #aaa !important;
	background-color: #222 !important;
}

div.oneClick:hover > s.Nav.OpenSmall { /* Content visible to whom tool, edit box down arrow */
	filter: none !important;
	filter: brightness(150%) !important;
}

/* Content visible to whom -tool background when edited */
.layout.two.left .doclist.large .oneClick.edit {
	color: #888 !important;
	background-color: #222 !important;
}

/* Acquaintances popup menu -tool background when edited */
.layout.two.left .networklist .memberdetails .oneClick.edit {
	color: #888 !important;
	background-color: #393939 !important;
}

.v2 .picto { /* Menu that appears below some buttons */
	position: relative !important;
	margin-top: 22px !important;
	width: auto !important;
}

div.eip > ul { /* Content visible to whom popup menu when edited */
	/* Used to be eip.noPicto for visible to whom menu only */
	background-color: #444 !important;
}

div.eip.noPicto > ul { /* Content visible to whom popup menu when edited */
	-webkit-box-shadow: 0 0 5px #191919;
}

div.eip > ul[style] { /* Acquaintances popup menu */
	-webkit-box-shadow: 0 0 5px #191919;
}

div.eip > ul.picto.items.arrowed.side-top.pos-left { /* Acquainteances popup menu */
	border: 1px solid #222;
}

/* Popup menu selections that have a line atop */
div.eip > ul.picto.items.arrowed.side-top.pos-left > li.border {
	border-top: 2px solid #393939;
}

div.eip.noPicto > ul.items { /* Visible to whom popup menu border */
	border: 1px solid #222;
}

div.eip > ul > li { /* Visible to whom popup menu text */
	color: #777 !important;
}

div.eip > ul > li > s { /* Visible to whom popup menu icon */
	filter: brightness(150%) !important;
}

div.eip > ul > li.selected { /* Visible to whom popup menu selected text */
	color: #999 !important;
}

div.eip > ul > li:hover { /* Visible to whom popup menu hovered lines */
	background-color: #555 !important;
	color: #999 !important;
}

div.eip :after { /* Visible to whom popup menu hovered lines */
	filter: brightness(27%) !important;
}

#hovercard.shadowed { /* Other users' pop-up box */
	background-color: #333 !important;
	box-shadow: 0 0 10px #111;
}

#hovercard.shadowed > .card > .buttons { /* Other users' pop-up box */
	background-color: #333 !important;
}

.v2 .emptylist { /* No content yet background */
	background: none !important;
}

.v2 .emptylist > h2 { /* "No article suggested yet" text */
	color: #444 !important;
}

.v2 .emptylist > h2 > a { /* "Would you like to write your first article" */
	color: #393939 !important;
}

.v2 .emptylist > p { /* "To add an article to your favorites.." */
	color: #555 !important;
}

.v2 .emptylist > p > s { /* Star after "To add an article to your favorites.." */
	display: inline-block !important;
}

.v2 .emptylist > p > a { /* "Invite friends or other link" */
	color: #356098 !important;
}

.v2 span.hot { /* Badge for hot images etc. */
	background-color: #873 !important;
	color: #fd7 !important;
	padding-left: 2px !important;
}

#dialog_uedit { /* Add user to your contacts. Class = dialog */
	background-color: #222 !important;
	border: 1px solid #222 !important;
	-webkit-box-shadow: 0 0 9px #000 !important;
}

#dialog_uedit .toptitle { /* Add user to your contacts > blue top bar */
	background-color: #171717 !important;
	border-top-left-radius: 10px !important;
	border-top-right-radius: 10px !important;
}

#dialog_uedit .member { /* Add user to your contacts > member details area */
	border: 3px solid #1b1b1b !important;
	background-color: #222 !important;
}

#dialog_uedit .buttons { /* "Add to your contacts" button */
	border-top: 1px solid #333 !important;
}

.v2 input, .v2 dd.input > input, .v2 dd.input > textarea { /* input fields */
	-moz-appearance: none !important;
	background-color: #292929 !important;
	border: 1px solid #171717 !important;
	border-bottom: 1px solid #444 !important;
	border-right: 1px solid #444 !important;
	padding-bottom: 3px !important;
	margin-bottom: 0px !important;
}

input:focus, textarea:focus, select:focus { /* Search active */
	outline: 1px solid #356098 !important;
}

.v2 dd.input > select, .v2 select#doc_share, .v2 select.question, .v2 select[class=""] { /* select fields */
	-moz-appearance: none !important;
	background-image: url('http://i.imgur.com/TJRBOgt.jpg') !important;
	background-blend-mode: multiply !important;
	background-position: center right !important;
	background-repeat: no-repeat !important;
	background-color: #292929 !important;
	border: 1px solid #171717 !important;
	border-bottom: 1px solid #444 !important;
	border-right: 1px solid #444 !important;
	padding-left: 3px !important;
	padding-right: 25px !important;
	padding-bottom: 2px !important;
}

.v2 .autocomplete-tip { /* Suggestion tip that appears beneath some inputs */
	background-color: #443 !important;
}

.v2 .autocomplete { /* Suggestion line that appears beneath some inputs */
	background-color: #444 !important;
}

.v2 .autocomplete > ul > li { /* Suggestion lines that appears beneath some inputs */
	border-bottom: 1px solid #333 !important;
}

.v2 .autocomplete > ul > li.selected { /* Suggestion lines that appears beneath some inputs, hovered */
	background-color: #555 !important;
}

.v2 .autocomplete > ul > li > em { /* Possibly highlighted part of the suggestion line */
	background: none !important;
	color: #ddd !important;
}

.v2 #ab_tip { /* input helper */
	background-color: #171717 !important;
}

.v2 #ab_tip > em { /* input helper */
	background-color: #171717 !important;
}

.v2 select > option, .v2 select > optgroup, .v2 select {
	color: #888 !important;
	padding: 3px !important;
	padding-left: 6px !important;
	padding-bottom: 6px !important;
}

.v2 select > optgroup > option {
	color: #888 !important;
	padding: 3px !important;
	padding-left: 6px !important;
	padding-top: 8px !important;
}

/* This doesn't work. Menu items are partly system wide and hovering cannot be changed */
.v2 select > option:hover {
	background-color: #444 !important;
	color: #aaa !important;
}

.v2 dd.input > select:focus, .v2 select#doc_share:focus { /* select fields focused */
	outline: none !important;
}

.v2 dd.input > select > option, .v2 select#doc_share > option { /* selection menu option dropdown */
	background-color: #292929 !important;
	color: #888 !important;
}

/* search where menu optional licence type dropdown */
.v2 dd.input > select > optgroup {
	background-color: #292929 !important;
	color: #111 !important;
	padding-left: 8px !important;
	font-size: 11pt !important;
	border: none !important;
	margin-bottom: 10px !important;
}

/* search where menu optional licence type dropdown */
.v2 dd.input > select > optgroup > option {
	background-color: #292929 !important;
	color: #888 !important;
	border: none !important;
}

.v2 input[type="checkbox"], .v2 input[type="radio"] { /* All tickboxes */
	/*filter: invert(0) !important;*/
    border-top: 1px solid #444 !important;
    border-bottom: 1px solid #111 !important;
    border-left: 1px solid #444 !important;
    border-right: 1px solid #111 !important;
	width: 12px !important;
	height: 12px !important;
}


.v2 input[type="checkbox"]:hover, .v2 input[type="radio"]:hover { /* All tickboxes hovered */
    filter:brightness(130%) !important;
}


.v2 input[type="checkbox"]:checked, .v2 input[type="radio"]:checked { /* All tickboxes ticked */
	filter: invert(25%) !important;
    border-bottom: 1px solid #555 !important;
    border-right: 1px solid #555 !important;
    border-top: 1px solid #000 !important;
    border-left: 1px solid #000 !important;
    filter:brightness(150%) !important;
}

.v2 div.greybox { /* Box slightly lighter than background on user account settings pages */
	background-color: #292929 !important;
	border-color: #393939 !important;
}

/* The page (=user) requested is no longer accessible */
.v2 .layout.one > .errbox {
	background: none !important;
	background-color: #443 !important;
}

/* The page (=user) requested is no longer accessible */
.v2 .layout.one > .errbox > .error > h1 {
	color: #997 !important;
}

/* The page (=user) requested is no longer accessible */
.v2 .layout.one > .errbox > .error > form {
	background-color: #443 !important;
	
}

/* The page (=user) requested is no longer accessible */
.v2 .layout.one > .errbox > .error > form > input {
	height: auto !important;
	background-color: #443 !important;
	color: #997 !important;
	border-color: #332 !important;
}

/* The page (=user) requested is no longer accessible hovered */
.v2 .layout.one > .errbox > .error > form > input:hover {
	color: #cca !important;
	border-color: #554 !important;
}

.v2 #tools[class="optionsBar"] > #tools-menu-btn { /* Tools button eg. at the top of your albums page */
	background-color: #393939 !important;
}

.v2 #tools[class="optionsBar"] > #tools-menu-btn:hover { /* Tools menu hovered */
	filter:brightness(130%) !important;
}

.v2 #tools-menu { /* Tools menu eg. at the top of your albums page */
	background-color: #333 !important;
	box-shadow: 0 1px 7px #111 !important;
}

.v2 #tools-menu::after { /* Tools menu arrow at the top */
	filter:brightness(23%) !important;
}

.v2 #tools-menu > li> a:hover { /* Hovered item in the Tools menu */
	background-image: none !important;
	background-color: #333 !important;
	filter:brightness(130%) !important;
}

.v2 #tools-menu > li.divider { /* Separator line in the Tools menu */
	border-top: 2px solid #292929 !important;
}

/* Footer at the bottom of every page */
#foot dl {
	border-top: 2px solid #292929 !important;
}

.v2 > .dialog { /* Common dialog box settings */
	background-color: #222 !important;
	box-shadow: 0 1px 11px #000 !important;
}


/* --- Photos/articles > "Authorizations, license change" dialog box --- */


.v2 > div#overlay, .v2 div.overlay { /* Background overlay that covers everything behind dialog box */
	background-color: #222 !important;
	opacity: 0.6 !important;
}

.v2 > div[id$="-props-edit"] > .tabs > a.on { /* Active tab */
	background-color: #222 !important;
	border-bottom-color: #222 !important;
}

.v2 > div[id$="-props-edit"] > .tabs > a:not(.on) { /* Unctive tab */
	background-color: #222 !important;
	border-bottom: 0  !important;
	position: relative !important;
	top: -1px !important;
	color: #666 !important;
}

.v2 > div[id$="-props-edit"] > .tabs > a:not(.on):hover { /* Unctive tab hovered */
	filter:brightness(120%) !important;
}

.v2 > div[id$="-props-edit"] .tab-content { /* Main content area */
	background-color: #222 !important;
	border-color: #222 !important;
}

.v2 > div[id$="-props-edit"] .window-inn > form > p { /* Main content area */
	background-color: #222 !important;
}

.v2 > div[id$="-props-edit"] .window-inn > form > .tab-content > div > img { /* Creative commons logo */
	filter: invert(80%) !important;
}

.v2 div#dialog_default { /* Default dialog popup window */
	box-shadow: 0 0 10px #111;
	background-color: #444 !important;
	border: none !important;
}

.v2 div#dialog_default span[style="color:red"] { /* Default dialog > warning color */
	color: #f66 !important;
}

.v2 div#dialog_default .button.delete { /* Default dialog > ok button */
	background-color: #555 !important;
	color: #aaa !important;
}

.v2 div#dialog_default .button.reset { /* Default dialog > cancel button */
	background-color: #444 !important;
	color: #777 !important;
}

.v2 div#dialog_default .button:active { /* Default dialog > clicked buttons */
	filter:brightness(200%) !important;
}

/* --- Top menu --- */


/* --- Search --- */

.v2 textarea, .v2 input[type="text"], .v2 input[type="password"]  { /* Search at the top */
	border: 1px solid #555 !important;
	border-radius: 2px !important;
	background-color: #2b2b2b !important;
	-moz-appearance: none !important;
	
}

.v2 .search-results.shadowed.arrowed { /* Search help balloon */
	background: none !important;
	background-color: #444 !important;
	-webkit-box-shadow: 0 0 9px #111;
	border: 1px solid #111;
	padding: 0 !important;
	width: auto !important;
	max-width: 220px !important;
}

.v2 .search-results.shadowed.arrowed::after { /* Search help balloon arrow on the top */
	filter: brightness(40%) !important;
}

.v2 .search-results.shadowed.arrowed > .service { /* Search help balloon title */
	background-color: #666 !important;
	padding: 6px !important;
	color: #bbb !important;
}

.v2 .search-results.shadowed.arrowed > .table.row.result { /* Search help balloon result items */
	border-top: 1px solid #333;
	padding: 6px !important;
	padding-right: 0 !important;
	width: auto !important;
	max-width: 214px !important;
	display: inline-block !important;
}

.v2 .search-results.shadowed.arrowed > .table.row.result:hover { /* Search help balloon result items hovered */
	background-color: #555 !important;
}

/* Search help balloon result item name box */
.v2 .search-results.shadowed.arrowed > .table.row.result > .noimg[style] {
	padding-right: 0px !important;
}

/* Search help balloon result item name text */
.v2 .search-results.shadowed.arrowed > .table.row.result > .noimg > div:first-child {
	padding-right: 0 !important;
	white-space: pre-wrap !important;
	max-width: 210px !important;
}

/* Search help balloon result item time */
.v2 .search-results.shadowed.arrowed > .table.row.result > div > .ellip > small {
	color: #888 !important;
}

/* Search help balloon "More results" */
.v2 .search-results.shadowed.arrowed > .row.more {
	background-color: #171717 !important;
	color: #666 !important;
}

/* Search help balloon "More results" hovered */
.v2 .search-results.shadowed.arrowed > .row.more:hover {
	background-color: #111 !important;
	color: #999 !important;
}

/* Search help balloon categories at the bottom */
.v2 .search-results.shadowed.arrowed > .row.other {
	background-color: #393939 !important;
	color: #999 !important;
	border-color: #292929 !important;
}

/* Search help balloon categories at the bottom, hovered */
.v2 .search-results.shadowed.arrowed > .row.other:hover {
	filter: brightness(120%) !important;
}

/* Search help balloon categories at the bottom, clicked */
.v2 .search-results.shadowed.arrowed > .row.other:active {
	filter: brightness(200%) !important;
}

/* --- Search results page --- */


/* This prevents photos and their details from separating in wide screen view */
.v2 .layout.two.left .main #photos > li[style="margin-bottom: 20px;"] { /* Photo search results */
	border-bottom: 1px solid #191919 !important;
}

.v2 .layout.two.left.w600 > .main > #searchForm {
	background-color: #292929 !important;
	border: 1px solid #171717 !important;
	border-right: 1px solid #333 !important;
	border-bottom: 1px solid #333 !important;
}

.v2 .layout.two.left.w600 > .main > #searchForm > form {
	background-color: #292929 !important;
}

.v2 .layout.two.left.w600 > .main > #searchForm > form > .query { /* input field */
	background-color: #333 !important;
	border: 1px solid #222 !important;
	border-right: 1px solid #393939 !important;
	border-bottom: 1px solid #393939 !important;
	padding-left: 5px !important;
}

.v2 .layout.two.left.w600 > .main > #searchForm > form select { /* search menus */
	background-color: #333 !important;
	height: 25px !important;
	border: 1px solid #222 !important;
	border-right: 1px solid #393939 !important;
	border-bottom: 1px solid #393939 !important;
	padding-bottom: 2px !important;
	color: #888 !important;
	-moz-appearance: none !important;
	background-image: url('http://i.imgur.com/TJRBOgt.jpg') !important;
	background-blend-mode: multiply !important;
	background-position: center right !important;
	background-repeat: no-repeat !important;
}

/* --- Back to upper menu --- */

.v2 #hmenu .divider span { /* Upper menu dividers */
	width: 1px;
	height: 15px;
	border-left: 2px solid #000 !important;
}

.v2 #htools > a[title=Mailbox] { /* Mail icon */
	filter: brightness(0.6) !important;
}

.v2 #htools > a[title=Mailbox]:hover { /* Mail icon hovered */
	filter: brightness(0.8) !important;
}

.v2 #htools > a[title=Mailbox] > s > span { /* Mail icon incoming alert */
	filter: brightness(150%) !important;
}

.v2 #hloguedin .btns > * { /* User menu top right */
	border: 1px solid #333 !important;
	outline: 1px solid #222 !important;
	background-image: none !important;
	background-color: #151515 !important;
}

.v2 #hloguedin > .btns > .menu > .Misc.Menu.vmid.onHover { /* User menu top right icon */
	opacity: 0.4 !important;
}

.v2 #userboxmenu { /* User box that openes from the previous menu above */
	color: #000 !important;
	background-color: #333 !important;
	border: 1px solid #000 !important;
	-webkit-box-shadow: 0 0 22px !important
}

.v2 #userboxmenu > .infos > a { /* User box's picture */
	border: 3px solid #999 !important;
	margin-left: 8px !important;
	height: 46px !important;
}

.v2 #userboxmenu > .warnbox { /* User box's warning box */
	background-color: #333 !important;
}

.v2 #userboxmenu > .warnbox > .orange { /* User box "important" text */
	color: #b63 !important;
}

.v2 #userboxmenu > .divider { /* User box divider */
	margin-left: 0px !important;
	margin-right: 0px !important;
	border-top: 1px solid #000 !important;
}

.v2 #userboxmenu > .options.menu > a:hover { /* User box menu items hovered */
	background-image: none !important;
	background-color: #444 !important;
}

.v2 #userboxmenu > .logout { /* User box logout */
	margin-left: 0px !important;
	margin-right: 0px !important;
	margin-bottom: 0px !important;
	margin-top: 8px !important;
	height: 15px !important;
	background-color: #333 !important;
	vertical-align: text-bottom !important;
	padding-top: 0px !important;
	padding-bottom: 11px !important;
}

.v2 #userboxmenu > .logout > .button { /* User box logout */
	color: #999 !important;
	padding-bottom: 4px !important;
}

/* Main menu: */

.breadcrumb { /* menu common button color */
	background-image: none !important;
	background-color: #444 !important;
	border-right: 0px !important;
