css:
@-moz-document url-prefix("http://dwatchseries.to/"), url-prefix("https://dwatchseries.to/"), url-prefix("https://www1.swatchseries.to/") {
/* MAIN PAGE, BEGINNING */

/* left and right background, and logo header, and search box and borders on the inside */
body, div#header, .ui-autocomplete-input, .search-box input[type="text"]{
	
	background-color: black !important;
	border-top: 1px solid #000 !important;
	border-left: 1px solid #000 !important;
	
}

/* ad about adblock and update bookmars and second color for lastest episodes container */
.rotating-item{
	
	background-color: gray !important;
    
}
	

/* right blue ribbon and title containers as in last updated episodes calendar day and border of serach box */
.home-page, .block-left-home-title, h2, .block-right-home-title, .rounded-corners{
	
	background: #232323 !important;
	border-color: #232323 !important;
	
}

/* footer down bar*/
.footer{
	
	background: #141313  !important;
	
}

/* background last updated and most popular and notification drop down (bell) */
.home-page-left, .calendar-outliner, .block-right-home-inside, .notification-dropdown{
	
	background: #303030 !important;
	
}

/* MAIN PAGE, END */

/* PAGE WHERE EACH SEASON HAS ITS EPISODES, BEGINING */

/* blue ribbon on each season page and  */
.bk-grey-long{
	
	background: #232323 !important;
	
}

/* background of the episode list */
.listings{
	
	background-color: gray  !important;
	
}

/* background of the border of the episode list */
.fullwrap{
	
	background-color: #303030  !important;
	
}

/* order of seasons */
select#change_sort_order_select{
	
	background-color: #594F4F !important;
	
}

/* episode not watched (not clicked) */
.episode_not_watched{
	
	background-color: #725656 !important;

}

/* PAGE WHERE EACH SEASON HAS ITS EPISODES, END */

/* number of season in season pages or whenever it is shown and series and episodes titles everywhere
div#left{
	
	color: #b7b7b7 !important;
	
}
*/


/* background of genres */
ul.pagination{
	
	background-color: #303030  !important;
	
}
}

@-moz-document url-prefix("http://dwatchseries.to/episode"), url-prefix("https://dwatchseries.to/episode"), url-prefix("https://www1.swatchseries.to/episode") {
/* background of the border of he episode information */

.bg-tr{
	
	background-color: #232323 !important;
	
}

/* background of the episode information and links and comment section */
.bg-casing-tr, tbody, .modal-content{
	
	background-color: #303030  !important;
	
}

/* background "watch this link" and select domain thing. next and previous*/
.watchlink, select#domains_list, a.npbutton{
	
	background: #594F4F !important;
	
}

/* background of "download free episode" */
.freeEpisode{
	
	color: #A25555 !important;
											
}

/* captcha, cha cha and "post as" thing*/
.rc-anchor-content, .rc-anchor-normal-footer, .alt button{
	
	
	background-color: #808080 !important;
	
}
}

@-moz-document url-prefix("http://dwatchseries.to/my-watchlist"), url-prefix("https://dwatchseries.to/my-watchlist"), url-prefix("https://www1.swatchseries.to/my-watchlist") {
/* background of the show information */

.tracked-show-container, .tab-content, ul.listings, h2 {
	
	background-color: #303030  !important;
	
}
	
.episode_not_watched, li.episode_watched .bg-casing-tr{
	
	background-color: gray !important;
    
}

.bg-tr{
	
	background: #232323 !important;
	
}

/* episode watched (clicked) */
.episode_watched{
	
	background-color: #725656 !important;

}
}

@-moz-document url-prefix("http://dwatchseries.to/genres"), url-prefix("https://dwatchseries.to/genres"), url-prefix("https://www1.swatchseries.to/genres") {
/* bottom genres section */
.sp-leader-bottom{

	width: 817px !important;
	background-color: #303030 !important;

}
}

@-moz-document url-prefix("http://dwatchseries.to/new"), url-prefix("https://dwatchseries.to/new"), url-prefix("https://www1.swatchseries.to/new") {
/* bottom new section */
.sp-leader-bottom{

	width: 817px !important;
	background-color: #303030 !important;

}
}

@-moz-document url-prefix("http://dwatchseries.to/tvschedule"), url-prefix("https://dwatchseries.to/tvschedule"), url-prefix("https://www1.swatchseries.to/tvschedule") {
/* up schedule section */
.sp-leader{

	width: 817px !important;
	background-color: #303030 !important;

}
}

@-moz-document url-prefix("http://dwatchseries.to/contact"), url-prefix("https://dwatchseries.to/contact"), url-prefix("https://www1.swatchseries.to/contact") {
textarea, input, email, select{
	
	background-color: #303030!important;
	
}
}

@-moz-document url-prefix("http://dwatchseries.to/serie"), url-prefix("https://dwatchseries.to/serie"), url-prefix("https://www1.swatchseries.to/serie") {
.category-item.episode_not_watched, .more-info-links, .category-item-ad.episode_not_watched, .episode_not_watched{
	
	background-color: #b7b7b7 !important;

}

.more-info-links{
	
	color: #666 !important;
	
}

/* bottom series  section */
.sp-leader-bottom{

	width: 817px !important;
	background-color: #303030 !important;

}

/* captcha, cha cha */
.modal-content{
	
	background-color: #303030 !important;
	
}
}
