css:
@-moz-document domain("translate.googleusercontent.com"), domain("translate.google.com"), domain("translate.google.com.au"), domain("piaotian.com"), domain("ptwxz.com") {

#tl, #tr, .list, div.bottom, div.top, body > div:nth-child(6) > table:nth-child(1),
#main #guild, #main #shop, .toplink, #main #feit2, #main > center:nth-child(9), #Commenddiv {
    display: none !important;
}

body, .mainbody, .list, #main #content {
    width: 100% !important;
    min-width: 100% !important;
    max-width: 100% !important;
}

div.title > h1, .mainbody, .centent ul li, #main, #main #content, .bottomlink {
    margin: 0px !important;
    padding: 0px !important;
}

#main {
    padding-left: 5px !important;
    padding-right: 10px !important;
}

div.title {                                   /*  Index > Title > spacing  */
    padding-bottom: 20px;
}

.mainbody, .list {                            /*  Index > Table > width fix  */
    width: 99% !important;
    min-width: 99% !important;
    max-width: 99% !important;
}

.centent ul li {                              /*  Index > Table > Cell > size  */
    padding-left: 5px !important;
    padding-right: 5px !important;
    line-height: 1.25 !important;
    width: 32% !important;
    height: 2.5em !important;
}

a {                                           /*  Index > table > Cell > text  */
    line-height: 1.25 !important;
}

#main #content {                              /*  Content > bottom padding  */
    padding-bottom: 500px !important;
}

.fonts_mesne, #main #content H1, h1 {         /*  Content > text size  */
    font-size: 30px;
    line-height: 1.25;
}




/*  Color scheme  */

html, body, .centent ul li, .colors1 {
    background-color: #FFFFFF !important;
}

.fonts_mesne, #main #content H1, a, h1, body, #list dd a:link {
    color: #000000 !important;
}








