css:
@-moz-document  url-prefix('http://www.otakufr.com')
{
  body
  {
    background-image: linear-gradient( to top, #19191c,#19191c)!important;
    font-family: cursive!important;
    font-size: 15px !important;
  }

  .wrap,#sct_footer
  {
    background-image: linear-gradient( to top,#040405,#040405)!important;
    -webkit-box-shadow: -1px -1px 197px 17px rgba(0,0,0,1)!important;
    -moz-box-shadow: -1px -1px 197px 17px rgba(0,0,0,1)!important;
    box-shadow: -1px -1px 197px 17px rgba(0,0,0,1)!important;
    background: none !important;
  }

  #sct_content h1, #sct_content h2
  {
    background-image: linear-gradient( to bottom, #303030,#303030)!important;
    font-family: 'Brush Script Std',cursive !important;
    font-size: 27px !important;
  }

  .wpa_pag.anm_lts_eps.tbn h2
  {
    border-left: 3px solid #303030!important;
    border-right: 3px solid #303030!important;
  }

  body,.wpa_pag.anm_lts_eps.tbn a.anm
  {
    color: white !important;
  }

  .nde
  {
    font-size: 12px !important;
  }

  .nde:hover
  {
    -webkit-box-shadow: inset 1px 8px 30px 0px rgba(0,0,0,1);
    -moz-box-shadow: inset 1px 8px 30px 0px rgba(0,0,0,1);
    box-shadow: inset 1px 8px 30px 0px rgba(0,0,0,1);
    border-radius: 2%;
    cursor: pointer;
  }

  #sct_menu_area
  {
    background-image: linear-gradient( to bottom, #303030,#303030)!important;
    border-bottom: none;
  }

  .wpa_pag.anm_lts_eps.tbn a.anm
  {
    border-bottom: none !important;
    background-color: transparent !important;
  }

  #sct_series_index a
  {
    margin-top: 1px;
    background-color: black !important;
  }

  #sct_sidebar .widget,#sct_sidebar h2
  {
    background-image: radial-gradient( #303030,#303030)!important;
    margin-left: 2px ! important;
  }

  #sct_anm_lst_wgt_hlt .tab_head a:hover, #sct_anm_lst_wgt_hlt .tab_head a.sel
  {
    background-image: linear-gradient( to bottom, #303030,#303030) !important;
  }

  #sct_anm_lst_wgt_hlt a.ttl,#sct_anm_lst_wgt_hlt .tab_head a
  {
    color: #99A3A4 !important;
  }

  #groupcover
  {
    background-color: black;
  }

  #sct_anime_list_all li a,.wpa_pag b.val,.wpa_pag.anm_det a
  {
    font-size: 15px !important;
    color: white !important;
  }

  #sct_content h1,#sct_content h2
  {
    margin-left: 0px !important;
    margin-bottom: 5px !important;
  }

  .wpa_pag ul.lst li:hover
  {
    background: #1C2833!important;
  }

  .cluetip-jtip ,.cluetip-jtip .cluetip-inner
  {
    background-image: linear-gradient( to top,#303030,#3d3d3d)!important;
    color: white !important;
  }

  .wpa_pop.anm_det .anm_ifo
  {
    border: none;
    background: transparent;
  }

  .wpa_pop.anm_det .anm_ifo .cvr_ara
  {
    border: none;
  }

  .cluetip-jtip ,.cluetip-jtip .cluetip-inner
  {
    font-size: 15px;
    font-family: arial;
  }

  .cluetip-jtip .ui-cluetip-header
  {
    background-image: linear-gradient( to bottom, #303030,#303030)!important;
  }

  .wpa_pag.anm_lst .det
  {
    font-size: 14px;
  }

  #sct_content h1 a, #sct_content h2 a
  {
    color: white !important;
  }

  #sct_content h2 a
  {
    font-size: 20px;
  }

  .wpa_pag.anm_lts_eps.tbn .sub_sts
  {
    background: none !important;
  }

  .wpa_pag .wpa_nav
  {
    background-color: transparent !important;
  }
}
