.adContainer,
    #heart-mt-button,
	#ad-block-holder,
	#goProButton,
	.msgs-in-context-paper-tear {
        visibility: hidden;
        display: none;
        position: absolute;
    }
   /* #pinnedContent {
        top: 0px;
    }

    #sideNavigation {
        padding-top: 0px;
    } */
	.app-search-container {
    background-color: #232323;
    -box-shadow: 4px 4px 6px rgba(0,0,0,0.30) !important;
    box-shadow: 4px 4px 6px rgba(0,0,0,0.30) !important;
    -webkit-box-shadow: 4px 4px 6px rgba(0,0,0,0.30) !important;
}

    #messages-view-dropdown-selector,
    #singleMessageContainer .save-draft-button-holder,
    #singleMessageContainer .addTemplateIcon {
        background-color: #313539;
        border-color: #4a5363;
    }
.msgs-in-context-overlay,
.msgs-in-context-content, 
.search-results-container,
.messageThread,
#favoritesContainer{
    background-color: #313539;
    border-color: #313539;
    color:#AAA9A9;
}
#favoritesPanelHeader .contactPanelHeaderText, .favoriteMessageHolder {
    border-bottom: 1px solid #AB8B65;
}
    .thread-header-dropdown-icon:before {
        color: #FFF;
    }

    body,
    #pinnedContent,
    #singleMessageContainer .messageThread,
    .textResponse,
    #singleMessageContainer .responseArea,
    #threadListHolder,
	#powerViewThreadsHolder .threadConversationHolder{
        background-color: #313539;
        border-color: #313539;      
        font-family: Vollkorn, Arial, Helvetica, sans-serif;
    }
/*
    .progress {
        background-image: linear-gradient(to bottom, #9A9A9A, #979797);
    } */
/*Contact List*/
/*Contact Active */
#navBarTabs > .active > a, /*Active Background*/
#navBarTabs > .active > a:hover, /*Active Hover*/
#navBarTabs li.active a .threadNameOrNumber, /*Active Name*/
#navBarTabs li.active a .msgCountAndTimeStampInTab /*Active Time*/{
	background-color: #292C30;
	border-color: #AB8B65;
	color: #AB8B65;
}
/*Contact Hover */
#navBarTabs .thread-tab:hover, /*Hover Background*/
#navBarTabs li a:hover > .threadNameOrNumber, #navBarTabs li a:focus > .threadNameOrNumber, /*Hover Name*/
#navBarTabs li a:hover > .msgCountAndTimeStampInTab, #navBarTabs li a:focus > .msgCountAndTimeStampInTab /*Hover Time*/{
	background-color: #292C30;
	border-color: #AB8B65;
	color: #AB8B65;
}
/*Contact Default*/
#navBarTabs .threadNameOrNumber, #navBarTabs .thread-tab {
    font-family: "Unica One", Arial, Helvetica, sans-serif;
    text-transform: uppercase;
    font-size: 18px;
	font-weight: 400;
	color: #AAA9A9;
    background-color: #292C30;
    -o-transition: color .2s;
  	-ms-transition: color .2s;
  	-moz-transition: color .2s;
 	 -webkit-transition: color .2s;
 	 transition: color .2s;
     transition-timing-function:linear;
}
/* Conversation Box */
.messageThread {
    border: 1px solid #45494E;
}
.messageThread a {
    color: #FFF;
    text-decoration: none;
    -o-transition: color .2s;
  	-ms-transition: color .2s;
  	-moz-transition: color .2s;
 	 -webkit-transition: color .2s;
 	 transition: color .2s;
     transition-timing-function:linear;
}
.messageThread a:hover, .contactPanelHeaderText {
    color: #AB8B65;
}
.shared .btnicon:hover {
    background-color: #AB8B65;
}
.sharemediabutton:hover,
.shareCMSmediabutton:hover{
	background-color:#AB8B65;
}
.mediaActionsToolbar .unshared { /*Share Button */
    color:#FFF;
}
.mediaActionsToolbar .shared { /*Share Button Clicked */
    background: #AB8B65;
    color:#FFF;
}
/*TOP Name and Number */
.contentPanelHeader, .contentPanelHeaderText, #singleMessageContainer .contentPanelHeader, .msgs-in-context-header, .win-os .contentPanelHeaderNum, .win-os .contentPanelHeaderText {
    background-color: #292C30;
    border-color: #292C30;
    color: #C0996B;
    font-family: "Unica One", Arial, Helvetica, sans-serif;
    text-transform: uppercase;
}
.contentPanelHeader, #singleMessageContainer .contentPanelHeader {
    	-webkit-box-shadow: 0 6px 6px -2px rgba(0,0,0,0.30);
   		-moz-box-shadow: 0 6px 6px -2px rgba(0,0,0,0.30);
        box-shadow: 0 6px 6px -2px rgba(0,0,0,0.30);
}
.thread-header-tool .dropdown-toggle, .contentPanelHeaderText, .contentPanelHeaderNum {
    color: #C0996B;
}
/*Load More Messages*/
.loadMoreMessages {
    color: #fff;
    background-color: #3E3E3E;
    text-transform: uppercase;
    font-weight: bold;
}
.loadMoreMessages:hover {
    background-color:#3C3D3D;
}
/*Multi-Select Texts*/
.thread-item-wrapper:hover, .thread-item-wrapper.selected {
    background-color: rgba(192,153,107,0.07);
}
.multi-msg-action-btn-wrapper,.multi-msg-action-container {
	background-color: #292C30;
    color: #AAA9A9;
    border-top: 0px;
}
.message-selector-box.checked { /*Check Box Checked*/
    fill: #AB8B65;
}
/*Time Stamp in chat*/
:not(#fwd-msgs-email-body-prev) > .threadItem .timestampWithPopover:hover {
    color: #AB8B65;
}
/* Recived Message Box */
.receivedText {
	background-color: #24292D;
    color: #AAA9A9;
    border-color: #24292D !important;
}
.receivedText:after {
	border-color: rgba(36, 41, 45, 0);
	border-right-color: #24292d;
}
/* Sent Message Box */
.sentText, .favoriteMessageHolder .threadItem, .favoriteMessageHolder .threadItem > span {
	background-color: #2B3136;
    color: #AAA9A9;
    border-color: #24292D !important;
}
.sentText:after {
	border-color: rgba(43, 49, 54, 0);
	border-left-color: #2B3136;
}
.url-preview-holder {
    border-left: 2px solid #AB8B65;
}
/* Writing Box */
.textResponse {
    border: 1px solid;
    border-color: #45494E;
    color: #aaa9a9;
    background-color: #2E3135;
   /* -box-shadow: inset -10px 0px 25px 2px rgba(0,0,0,0.30);
    box-shadow: inset -10px 0px 25px 2px rgba(0,0,0,0.30);
    -webkit-box-shadow: inset -10px 0px 25px 2px rgba(0,0,0,0.30);*/
    overflow: auto;
}
.charsLeft {
    color: #AAA9A9;
}
/* Buttons */
.emoticonSelector.btn,.scheduleSelector.btn,.new-emoji-selector,.sendButton.mightyButton,.mms-feature.btn,.select-msgs-thread-tool,.refreshThreadTool.thread-header-tool,.multi-msg-action-btn, .mediaActionsToolbar .shared .btnicon {
    filter: invert(100%);
}
.thread-header-tool > .material-svg, .msg-result-direction-indicator {
    fill: #CFCFCF;
}
    .fa-th-list:before,
    .fa-caret-down:before,
    .fa-file-text:before,
    .fa-angle-down:before,
    .fa-save:before,
    .fa-floppy-o:before,
	.fa-star,
	.fa-th-large{
        color: #CFCFCF;
    }
.mighty-app-button {
    background-color: #3E3E3E;
    font-family: Vollkorn, Arial, Helvetica, sans-serif;
    text-transform: uppercase;
    font-weight: bold;
}
.mighty-app-button:hover {
    background-color: #3C3D3D;
}
/*Scroll Bar (webkit only)*/
::-webkit-scrollbar {
    background-color: rgb(41, 44, 48);
}
::-webkit-scrollbar-thumb {
    background-color: rgba(105, 105, 105, 0.5);
    -webkit-box-shadow: 0 0 1px rgba(255,255,255,.5);
}
#messageThread,.messageThread {
	border-right: 1px solid;
    border-color: #2B3136 !important;
}
/*Hide Scroll Bars unless mouseover*/
.threadConversationHolder {
    overflow-y: auto;
}
#threadListHolder {
    overflow-y: hidden;
}
#threadListHolder:hover,
#threadListHolder:focus {
    overflow-y: scroll;
}
/*========================Search Results=================================*/
.top-nav-search-results-wrapper, .search-results-container {
    background-color: #292C30;
    color: #AAA9A9;
}
.search-result:hover:not(.empty-search-results) { /*Result Hover*/
    background-color: #292C30; 
}
.search-result:not(.empty-search-results) { /*Result*/
    border-color:#45494E;
    background-color: #2B3136; 
    font-family: Vollkorn, Arial, Helvetica, sans-serif;
}
/*========================Auto-Hide Left Side Bar========================*/
  #sideNavigation
  {
    z-index: 10 !important;
    -moz-transition-duration: .2s !important;
    -webkit-transition-duration: .2s !important;
    position: fixed !important;
    width: 200px !important;
    height: 100% !important;
    padding-left: 30px !important;
    padding-right: 10px !important;
    background-color: #292C30 !important;
    opacity: 0 !important;
    left: -235px !important;
    border-right: #AB8B65 5px solid !important;
    margin-left: 30px !important;
  }

  #sideNavigation:hover
  {
    -webkit-transition-duration: .2s !important;
    -moz-transition-duration: .2s !important;
    background-color: #292C30 !important;
    position: fixed !important;
    z-index: 10 !important;
    opacity: 1 !important;
    -box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
    box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
    -webkit-box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
    left: -30px !important;
  }

  #pinnedContent
  {
    left: 0px;
  }
.mainMenuItem {
    font-family: "Unica One", Arial, Helvetica, sans-serif;
    font-size: 18px;
	font-weight: 400;
    color: #AAA9A9;
    -o-transition: color .2s;
  	-ms-transition: color .2s;
  	-moz-transition: color .2s;
 	 -webkit-transition: color .2s;
 	 transition: color .2s;
     transition-timing-function:linear;
}
.mainMenuItem:hover, .activeLeftNavBar, .activeLeftNavBar:hover {
    color: #AB8B65;
} /* .mainMenuItem .activeLeftNavBar */
.mainMenuItem:hover .sideBarIcon, .mainMenuItem .sideBarIcon{
    -webkit-filter: hue-rotate(210deg);
    filter: hue-rotate(210deg);
}
}
