css:
@-moz-document url-prefix("https://apigee.com/"){
*{
	letter-spacing:0.2 !important;
}

/*    ACTION BUTTON   */

.app-view .context-bar-actions button {
	background-color: #0b9bd3;
	color: white;
	font-weight:bold;
	/*text-transform:uppercase;*/
	border-radius:3px;
	padding:6px 15px;
	border:none;
}
.app-view .context-bar-actions button:hover {
	background-color: #135a9e;
}
.app-view .context-bar-lower {
	margin: 0px 0px 10px 0;
}


/*    TABLES   */

.list-container .table>thead>tr>th,
.ue-list-headers .header-title,
.flex-list .header-title{
	color: #000 !important;
	font-weight: bold !important;
}
.list-container .table>thead>tr>th:hover,
.ue-list-headers .header-title:hover,
.flex-list .header-title:hover{
	color: #0b9bd3 !important;
}

.ue-list-headers,
.list-container .table>thead tr,
.flex-list .list-headers{
	height: 36px;
	background: white;
}
.ue-list-headers .headers-each,
.flex-list .dropdown {
	padding: 10px 15px 0px;
}
.list-container .table>tbody>tr>td:first-child,
.list-container .table>thead>tr>th:first-child {
	border-left: 1px solid #ddd;
}
.list-container .table>tbody>tr>td:last-child,
.list-container .table>thead>tr>th:last-child {
	border-right: 1px solid #ddd;
}
.list-container .table>thead>tr>th {
	border-top: 1px solid #ddd !important;
}
.list-container .table>tbody>tr:last-child>td {
	border-bottom: 1px solid #ddd;
}
.ue-list-headers,
.list-headers{
	border: 1px solid #ddd;
	border-bottom: none;
}
.list-container .table-hover>tbody>tr:hover,
.app-view .alm-entity-list .flex-list .list-content .wrapper-collapsed-row:hover {
	background-color: #e7f5fb; /*#EDFAFF;*/
}
.row-actions .apic-icon:not(.disabled):hover,
.list-container tr button:hover{
	color:#0b9bd3 !important;
}

.list-container .table>tbody>tr>td,
.list-container .table>tbody>tr>td>a,
.list-element{
	font-size:15px !important;
	/*letter-spacing:-2px !important;*/
}
.list-container .table>tbody>tr>td.modified {
	text-transform:lowercase;
}
.arrow-up:before{
	content: "\25B2" !important; /* 25B2,2B06 */ 
}
.arrow-down:before{
	content: "\25BC" !important; /* 25BC,2B07 */
}

.deploymentIndicator {
    width: 12px !important;
    height: 12px !important;
}

.deploymentIndicator.deployed{
    border: 1px solid #078850 !important;
}
.trafficBars div:not(.activated){
    border: 1px solid #dedede !important;
}


/*    BREADCRUMBS   */

.breadcrumb li,
.breadcrumb li a,
.app-view .context-bar>ol.breadcrumb>li,
.context-bar .breadcrumb{
	font-size: 22px !important;
	font-weight:normal;
	color: #aaa !important;
}
.breadcrumb li a{
	color:#707070 !important;
}
.breadcrumb li a:hover{
	color:#0b9bd3 !important;
	border-bottom:1.5px solid #0b9bd3 !important;
	padding-bottom:3px !important;
}
.app-view .context-bar>ol.breadcrumb>li:not(.active) {
	color: #000;
}
.app-view .context-bar>ol.breadcrumb>li:last-child,
.context-bar ul li:last-child{
	font-weight: bold;
	color: black !important;
}
.app-view .context-bar>ol.breadcrumb {
	padding: 13px 20px;
}

.app-view .context-bar>ol.breadcrumb>li+li::before {
	color: #aaa;
}
.app-view .context-bar>ol.breadcrumb>li+li::before{
    margin-right:10px;
    margin-left:0;
}


/*    CARDS     */

.app-cta-block {
	border: 2px solid white;
}
.app-cta-block:hover {
	background-color: #e7f5fb;
	border: 2px solid #0b9bd3;
}
.app-cta-block:hover .apic-icon[class$="-xl"],
.app-cta-block:hover h3,
.app-cta-block:hover p {
	color: #0b9bd3;
}
.app-description {
	color: #666;
}

/*     NAV    */
#navbar li{
	cursor:hand;
}
#navbar nav ul li span.menu-item {
    left: 65px;
    top: 21px;
    font-size: 15px;
}

#navbar nav ul li:hover,
#navbar nav div.submenu ul li a:hover {
	color: #DD3303;
	background-color:rgba(221, 51, 3, .05);
}
#navbar nav div.submenu ul li.active-page a {
	background-color: white;
	color: #DD3303;
	/*border-right:5px solid #DD3303;*/
	padding-left:20px;
}
#navbar nav ul li[data-state=active] {
	background-color: #DD3303;
	color: #fff;
}
.selectedMenuIcon-rightBar {
	display: none;
}
#navbar nav div.submenu ul li.active-page a {
	font-weight: bold;
}
#submenu-close{
	display:none;
}
#submenu div:first-child{
	text-align:left !important;
}
#navbar nav div.submenu>div>h3{
	margin-left:20px;
	font-size:18px;
}
#submenu-desc{
	display:none;
}
#navbar nav div.submenu{
	width:184px;
}
#navbar div.navbar-head{
	border-bottom:none;
	border-right: 1px solid #cdcecf;
}
/*     PAGINATION    */
.alm-pagination .btn {
    padding: 2px 10px;
    font-size: 16px;
}
.alm-pagination-text{
	font-weight:normal !important;
}

/*     FILTER    */
filtering input{
	font-size:16px !important;
}

/*     WELCOME    */
.well{
	display:none;
}
.app-cta-blocks{
	margin-top:50px;
}
