css:
@-moz-document domain("dmhy.org") {
html {
    box-sizing: border-box;
    font-size: 62.5%;
}
html,
body {
    overflow-x: hidden;
    background-color: #f5f5f5;
}
a {
    text-decoration: none;
    color: #575959;
}
a:hover,
a:focus {
    text-decoration: none;
    border-bottom: 0 !important;
    color: #6f86d6;
}
::selection {
    background-color: #718ffd;
    color: #f5f5f5;
}
.keyword {
    background-color: #64b5f6;
    color: #fafafa;
    border-radius: 3px;
}
/* mark: global */
pangu ,
.headerleft img ,
#expand-button ,
[id *= "_ad"], 
[id *= "ad_"], 
[class *= "_ad"], 
[class *= "ad_"], 
[class *= "-ad"], 
[class *= "ad-"], 
[id *= "AD"], 
[class *= "AD"]
{
    display: none !important;
}
/* mark: ad img remove */
.table {
    display: block;
    border: none;
    padding: 0;
    box-shadow: 0 1px 6px 0px #607d8b2b;
}
.bg {
    margin: 0 0;
    min-width: 100rem;
}
/* mark: background */
.bg > .header {
    /* border-top: solid 18px #6f86d6; */
    /* background-image: linear-gradient(30deg, #89f7fe 0%, #66a6ff 100%); */
    display: flex;
    align-items: center;
    height: 14rem;
    /* padding: 0 4rem; */
    margin: 0 auto;
    width: 130rem;
    background-image: linear-gradient(to bottom, #7dabe0 0%, #6f86d6 100%);
}
.bg > .header::before {
    display: block;
    content: '';
    position: absolute;
    width: 100%;
    min-width: 96rem;
    height: 14rem;
    left: 50%;
    background-image: linear-gradient(to bottom, #7dabe0 0%, #6f86d6 100%);
    transform: translateX(-50%);
    z-index: 0;
}
.headerleft {
    display: flex;
    align-items: center;
    width: 24rem;
    height: 14rem;
    margin-left: 1rem;
    margin-right: 2rem;
    background-color: transparent;
    z-index: 5;
}
.headerright {
    position: absolute;
    padding: 0 0.5rem;
    top: 0;
    right: 0;
}
.header,
.headerright .links {
    background-color: transparent;
}
.headerright .links a {
    font-weight: 300;
    color: #f5f5f5;
}
.top_sort,
.top_sort .menu {
    max-width: 130rem;
    /* margin: 0 auto; */
    background-color: #48c8ef00;
    letter-spacing: 1px;
    font-family: Roboto, "Noto Sans TC", "Microsoft JhengHei", "Microsoft YaHei", sans-serif;
    z-index: 5;
    /* background-image: linear-gradient(to top, #6f86d6 0%, #6a85b6 100%); */
}

.top_sort .menu .nav font {
    color: #f5f5f5;
    font-family: inherit;
    font-weight: 300;
    font-size: 1.6rem;
}
.top_sort .menu ul li {
    width: 8rem;
}
.top_sort .menu ul li a {
    padding: 2px 0;
    -webkit-transition: background-color 200ms ease-in-out;
    -moz-transition: background-color 200ms ease-in-out;
    transition: background-color 200ms ease-in-out;
}
.top_sort .menu ul li a:hover,
.top_sort .menu ul li:hover a {
    background-color: #586db9;
}
/* .top_sort .menu ul li ul {
    transform: translateY( -10px );
    -webkit-transition: transform 150ms ease-in-out;
    -moz-transition: transform 150ms ease-in-out;
    transition: transform 150ms ease-in-out;
} */
.top_sort .menu ul li:hover ul {
    width: 10rem;
}
.top_sort .menu ul li:hover ul::before {
    display: block;
    position: absolute;
    content: '';
    width: 100%;
    bottom: 0;
    left: 0;
    height: calc( 100% - 1rem );
    box-shadow: 0 4px 12px -1px #4c636e62;
}
.top_sort .menu .nav > li > ul > li {
    width: 10rem;
    background-color: #fff;
}
.top_sort .menu .nav > li > ul > li:first-child {
    margin-top: 1rem;
}
.top_sort .menu ul li:hover ul li a {
    background-color: #fff;
    padding: 5px 10px;
    border-left: 0px solid;
    -webkit-transition: background-color 240ms ease-in-out;
    -moz-transition: background-color 240ms ease-in-out;
    transition: background-color 240ms ease-in-out;
}
.top_sort .menu ul li ul li::before {
    display: block;
    position: absolute;
    content: '';
    width: 4px;
    height: 0;
    background-color: #6f86d6;
    top: 50%;
    transform: translateY(-50%);
    -webkit-transition: height 200ms ease-in-out;
    -moz-transition: height 200ms ease-in-out;
    transition: height 200ms ease-in-out;
}
.top_sort .menu ul li:hover ul li:hover::before {
    height: 75%;
}
.top_sort .menu ul li:hover ul li a:hover {
    background-color: #f4f6ff;
}
.top_sort .menu ul li:hover ul li a font {
    display: block;
    color: #777979;
    font-size: 1.4rem;
    font-weight: 300;
    font-family: inherit;
    -webkit-transition: transform 150ms cubic-bezier(0.68, -0.55, 0.27, 1.55);
    -moz-transition: transform 150ms cubic-bezier(0.68, -0.55, 0.27, 1.55);
    transition: transform 150ms cubic-bezier(0.68, -0.55, 0.27, 1.55);
}
.top_sort .menu ul li:hover ul li a:hover font {
    color: #777979;
    transform: translateX( 4px );
}
.quick_search {
    position: absolute;
    right: 0;
    top: -91px;
    padding-right: 0;
    background: transparent;
    border: 0 !important;
    width: auto;
}
.quick_search .quick_input {
    max-width: 25rem;
    min-width: 10rem;
    /* border-radius: 2px; */
    background-color: #6377be;
    border: 1px solid #899ce0;
    color: #9fafe6;
    padding: 5px 3px;
    transition: border-color 200ms ease-in-out;
}
.quick_search .quick_input:hover,
.quick_search .quick_input:focus {
    border-color: #9fafe6;
    outline: none;
}
.quick_search .quick_input:focus {
    box-shadow: 0 1px 4px #6f86d659;
}
.quick_search form > a {
    position: absolute;
    left: -38px;
    top: 50%;
    color: #c8d8f8;
    transform: translateY( -50% );
}
.quick_search .formButton {
    display: inline-block;
    color: #c8d8f8;
    font-size: 1.4rem;
    border: 0;
    background: transparent;
    padding: 0;
    margin: 0;
    cursor: pointer;
}
#AdvSearch {
    position: absolute;
    background-color: #fff;
    width: 30rem;
    left: -3.4rem;
    top: 4.2rem;
    box-shadow: 0 4px 12px -1px #607d8b52;
}
#AdvSearch p {
    float: left;
    margin: 0.5rem 1.5rem;
}
#AdvSearch p:first-child {
    margin-top: 1.5rem;
}
#AdvSearch p:last-child {
    margin-bottom: 1.5rem;
}
/* mark: header */
.main {
    position: relative;
    background-color: #f5f5f5;
    padding: 5px 0;
    max-width: 130rem;
    margin: 0 auto;
}
#mini_jmd {
    overflow: hidden;
    background: #fff;
    /* border-radius: 2px; */
    width: 100% !important;
    max-width: none;
    min-width: 96rem;
    padding-left: 0;
    padding-right: 0;
    padding-top: 0;
    padding-bottom: 10px;
    margin: 10px auto;
}
.nav_title {
    box-sizing: content-box;
    background-color: #6f86d6;
    padding: 6px 12px;
    /* margin-bottom: 10px; */
    /* border-radius: 2px; */
    font-weight: 300;
    font-size: 1.3rem;
}
.nav_title a:hover,
.nav_title a:focus {
    color: #c8d8f8;
}
#mini_jmd .nav_title > a {
    color: #c8d8f8;
    background-color: #6377be;
    padding: 3px 4px;
    border-radius: 2px;
}
#mini_jmd .nav_title > a:hover,
#mini_jmd .nav_title > a:focus {
    color: inherit;
    background-color: #586db9;
}
.nav_title #bd-control {
    font-weight: 500;
    font-size: 1.4rem;
}
.nav_title #bd-control span {
    display: inline-block;
}
.nav_title #bd-control span::after {
    display: inline-block;
    content: '\f123' !important;
    font-family: 'Ionicons';
    transition: transform 350ms cubic-bezier(0.68, -0.55, 0.27, 1.55);
}
.nav_title #bd-control span.on::after {
    transform: rotateX( 180deg );
}
table.jmd,
table.jmd_base {
    border-spacing: 0;
    /* width: auto !important; */
}
/* table.jmd {
    display: none;
} */
/* table.jmd_base {
    display: block !important;
} */
table.jmd tr th,
table.jmd_base tr th {
    background-color: transparent !important;
    font-weight: 500;
    font-size: 1.3rem;
    color: #5f76c7  !important;
    width: 6rem !important;
}
table.jmd tr td,
table.jmd_base tr td {
    padding: 4px 2px !important;
    word-spacing: 2px;
}
table.jmd tbody tr,
table.jmd_base tbody tr{
    display: block;
    padding: 0 12px;
    padding-left: 4px;
}
table.jmd tbody > tr,
table.jmd tbody > tr.even,
table.jmd tbody > tr.odd,
table.jmd_base tbody > tr,
table.jmd_base tbody > tr.even,
table.jmd_base tbody > tr.odd {
    background-color: inherit;
}
table.jmd tbody > tr.today {
    background-color: #ecf2fe !important;
}
/* table.jmd tbody > tr.today th {
    color: #ff85d3 !important;
} */
table.jmd tr td a,
table.jmd_base tr td a {
    display: -webkit-inline-box;
    border: 0 !important;
    background-color: #899ce0;
    color: #f9f9f9;
    line-height: 1.8rem;
    font-size: 1.3rem;
    font-weight: 400;
    text-shadow: 0px 1px 2px #586db9;
    padding: 4px 8px !important;
    margin: 4px !important;
    transition: 300ms ease-in-out;
}
table.jmd tr td a:hover,
table.jmd tr td a:focus,
table.jmd_base tr td a:hover,
table.jmd_base tr td a:focus {
    color: #fff;
    background-color: #6f86d6;
}
/* mark: frame */
#msdl_ad + .clear div a img {
    display: none;
}
#msdl_ad + .clear > div > a {
    position: relative;
    height: 3rem;
    background-color: #6f86d6;
    /* border-radius: 2px; */
    margin-right: 1rem;
    -webkit-transition: background-color 200ms ease-in-out ,color 200ms ease-in-out;
    -moz-transition: background-color 200ms ease-in-out ,color 200ms ease-in-out;
    transition: background-color 200ms ease-in-out ,color 200ms ease-in-out;
}
#msdl_ad + .clear > div > a::before {
    display: block;
    position: absolute;
    content: '\f218';
    font-family: 'Ionicons';
    font-size: 2rem;
    color: #f5f5f5;
    left: 0.8rem;
    top: 50%;
    transform: translateY(-50%);
}
#msdl_ad + .clear > div > a::after {
    display: block;
    position: absolute;
    content: '張貼分享';
    width: calc( 100% - 2.5rem );
    line-height: 1;
    font-family: inherit;
    font-size: 1.7rem;
    font-weight: 300;
    color: #f5f5f5;
    left: calc( 50% + 1.8rem );
    top: 50%;
    transform: translate( -50% ,-50%);
}
#msdl_ad + .clear > div > a:hover,
#msdl_ad + .clear > div > a:focus,
#msdl_ad + .clear > div > a:visited {
    background-color: #586db9;
}
#msdl_ad + .clear > div > a:hover::before,
#msdl_ad + .clear > div > a:hover::after {
    color: #fff;
}
/* mark: add button */
.announce {
    position: relative;
    padding-top: 2px;
    margin-bottom: 13px;
}
.announce::before {
    display: inline-block;
    position: absolute;
    content: '\f3ba';
    font-family: 'Ionicons';
    color: #6f86d6;
    font-size: 2rem;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
}
.announce .inner {
    background-image: none;
}
marquee#announce_marquee a {
    color: #899ce0;
    font-weight: 300;
}
/* mark: announce */
.main > .table .nav_title .fr a > img {
    display: none;
}
.main > .table .nav_title .fr a:last-child {
    position: relative;
    float: right;
    padding-left: 1.8rem;
}
.main > .table .nav_title .fr a:last-child::before {
    display: inline-block;
    position: absolute;
    content: '\f09e';
    font-family: 'FontAwesome';
    font-size: 1.7rem;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
}
/* mark: rss button */
table.tablesorter {
    display: inline-block;
    border-spacing: 0;
    position: relative;
}
table.tablesorter thead {
    display: inherit;
    position: relative;
    width: 100%;
}
table.tablesorter  tr {
    display: inline-flex;
    width: 100%;
    align-items: center;
}
table.tablesorter  tr th {
    background-color: #899ce0;
    font-weight: 300;
    padding: 0;
    height: 2.2rem;
    line-height: 2.2rem;
}
table.tablesorter tbody td.title {
    position: relative;
    padding: 1.5rem 0;
    font-size: 1.5rem;
}
table.tablesorter tbody td.title span:last-child {
    position: absolute;
    left: 0;
    top: 50%;
    visibility: hidden;
    transform: translateY( -50% );
}
table.tablesorter  tr th[width = "96"],
table.tablesorter tbody td[width = "98"] {
    flex-basis: 6%;
    padding-left: 2%;
    padding-right: 1%;
}
table.tablesorter tr .header:nth-child(2),
table.tablesorter tbody td[width = "6%"] {
    flex-basis: 6%;
    text-align: justify;
}
table.tablesorter tr .header:nth-child(3),
table.tablesorter tbody td.title {
    flex-basis: 48%;
    padding-left: 1%;   
    padding-right: 1%; 
}
table.tablesorter tr th[width = "4%"],
table.tablesorter tbody td.title + td {
    flex-basis: 4%;
}
table.tablesorter tr th[width = "4%"] + th,
table.tablesorter tbody td.title + td + td {
    flex-basis: 6%;
    text-align: end;
}
table.tablesorter tr th[class *= "digit"],
table.tablesorter tbody td.title + td + td + td,
table.tablesorter tbody td.title + td + td + td + td,
table.tablesorter tbody td.title + td + td + td + td + td {
    flex-basis: 5%;
    text-align: end;
}
table.tablesorter tr th:last-child,
table.tablesorter tbody td.title + td + td + td + td + td + td {
    flex-basis: 8%;
    padding-right: 2%;
    text-align: end;
}
table.tablesorter tbody td.title + td + td + td + td + td + td a {
    padding-left: 1rem;
}
table.tablesorter tbody td {
    padding: 0;
    word-break: break-word;
}
table.tablesorter tbody tr td font {
    color: #5f76c7;
    font-weight: 400;
    font-size: 1.3rem;
}
table.tablesorter tbody tr.even {
    background-color: #fafafa;
}
table.tablesorter tbody tr.even td {
    background-color: transparent;
}
table#topic_list tr td span.tag {
    background-color: #c8d8f8;
    border-color: #899ce0;
    margin-right: .5rem;
}
table#topic_list tr td span.tag a {
    color: #5f76c7;
    font-size: 1.2rem;
}
.arrow-magnet {
    display: block !important;
    position: relative;
    background: transparent;
}
.arrow-magnet::before {
    display: block;
    position: absolute;
    content: '\f019';
    font-family: 'FontAwesome';
    font-size: 1.8rem;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
}
.btl_1 {
    color: #2196F3;
}
.bts_1 {
    color: #4CAF50;
}

/* mark: download icon*/
.footer {
    color: #575959;
}
.footer a {
    color: #575959 !important;
}
.footer + div + div a {
    color: #575959 !important;
}
.top {
    position: fixed !important;
    padding: 0 !important;
    right: 2rem;
    bottom: 4rem;
    width: 4rem;
    height: 4rem;
}
.top > a {
    position: absolute;
    width: 100%;
    height: 100%;
    opacity: 0.8;
    left: 0;
    bottom: 0;
    background-color: #6f86d6;
    color: transparent !important;
    box-shadow: 0 1px 6px 0px #52657a67;
    -webkit-transition: background-color 200ms ease-in-out ,opacity 200ms ease-in-out;
    -moz-transition: background-color 200ms ease-in-out ,opacity 200ms ease-in-out;
    transition: background-color 200ms ease-in-out ,opacity 200ms ease-in-out;
}
.top a::before {
    position: absolute;
    display: block;
    content: '\f3d8';
    font-family: 'Ionicons';
    font-size: 3rem;
    color: #f5f5f5;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
}
.top:hover a {
    background-color: #586db9;
    opacity: 1;    
}
/* mark: back_to_top button */
.ui-corner-all ,
.user-sidebar .box ,
.box.ui-corner-all {
    border-radius: 0;
    border: 0;
}
.topics_bk {
    position: relative;
    background-color: transparent;
    padding: 0;
}
.user-sidebar {
    margin-right: 1.5rem;
    width: 26rem;
    background-color: #fff;
    box-shadow: 0 1px 6px 0px #607d8b2b;
}
.topic-main {
    width: calc( 100% - 27.5rem );
    box-shadow: 0 1px 6px 0px #607d8b2b;
}
.user-sidebar .box {
    margin: 0;
    background-color: #fff;
}
.box.ui-corner-all {
    margin: 0;
    padding: 1rem 1.5rem;
}
.topic-main .box {
    padding: 1rem 2rem;
    background-color: #fff;
}
.topic-main {
    font-size: 1.6rem;
}
.topic-title h3 {
    font-size: 2rem;
    font-weight: 300;
}
.topic-title div.resource-info {
    position: absolute;
    left: 0;
    top: 13rem;
    font-size: 1.4rem;
    font-weight: 300;
    width: 26rem;
    margin: 0;
    padding: 0 1.5rem;
}
.topic-title div.resource-info li {
    margin: 0.5rem 0;
}
.avatar, .topics_cult {
    color: #6f86d6;
    font-weight: 300;
}
.avatar.box {
    margin-top: 0.5rem;
}
.avatar.box:first-child {
    margin-bottom: 20rem;
}
.avatar:first-child p:nth-child(2) {
    float: left;
}
.avatar:first-child p:last-child {
    float: right;
}
.avatar .img-box {
    margin-bottom: 1rem;
}
.avatar .img-box img {
    border-radius: 100%;
}
/* mark: page sidebar */
.file_list {
    border-color: #6f86d6;
    padding: 1rem 0.5rem;
}
.file_list::-webkit-scrollbar {
    width: 1.2rem;
}
.file_list::-webkit-scrollbar-thumb {
    background-color: #899ce0;
}
.file_list::-webkit-scrollbar-thumb:hover ,
.file_list::-webkit-scrollbar-thumb:active {
    background-color: #586db9;
}
.file_list ul {
    background-color: #fff;
    padding-left: 3rem;
}
.file_list li {
    position: relative;
    background-color: transparent;
    margin-bottom: 0.5rem;
    font-size: 1.3rem;
    color: #575959;
}
.file_list li img {
    display: none;
}
.bt_file_size {
    float: right;
}
.file_list li img + span::before {
    display: block;
    position: absolute;
    content: '';
    text-align: center;
    line-height: 1;
    width: 2rem;
    font-family: 'FontAwesome';
    font-size: 1.8rem;
    color: #6f86d6;
    left: -2.5rem;
    top: 50%;
    transform: translateY( -50% );
}
.file_list li img[alt *= "unknow"] + span::before { content: '\f016'; }
.file_list li img[alt *= "jpg"] + span::before { content: '\f1c5'; }
.file_list li img[alt *= "png"] + span::before { content: '\f1c5'; }
.file_list li img[alt *= "mkv"] + span::before { content: '\f1c8'; }
.file_list li img[alt *= "mp4"] + span::before { content: '\f1c8'; }
.file_list li img[alt *= "txt"] + span::before { content: '\f0f6'; }
.file_list li img[alt *= "ass"] + span::before { content: '\f0f6'; }
.file_list li img[alt *= "srt"] + span::before { content: '\f0f6'; }
.file_list li img[alt *= "pdf"] + span::before { content: '\f1c1'; }
.file_list li img[alt *= "mp3"] + span::before { content: '\f1c7'; }
.file_list li img[alt *= "wav"] + span::before { content: '\f1c7'; }
.file_list li img[alt *= "rar"] + span::before { content: '\f1c6'; }
.file_list li img[alt *= "7z" ] + span::before { content: '\f1c6'; }
.file_list li img[alt *= "zip"] + span::before { content: '\f1c6'; }
.file_list li img[alt *= "exe"] + span::before { content: '\f1c9'; }
/* mark: download list */

.weekly_list > tbody td:last-child {
    padding-left: 1.5rem;
}
.weekly_list_b {
    position: relative;
    margin: 0 2rem !important;
    margin-bottom: 5rem !important;
}
.weekly_list_b::before {
    display: inline-block;
    position: absolute;
    content: '';
    background-image: inherit;
    background-repeat: round;
    width: 100%;
    height: 100%;
    z-index: 5;
}
.weekly_list_b::after {
    display: inline-block;
    position: absolute;
    content: '';
    background-image: inherit;
    background-repeat: round;
    opacity: 0;
    width: calc( 100% + 2px );
    height: calc( 100% + 0.5rem );
    left: 50%;
    top: calc( 50% + 5px );
    -webkit-filter: blur( 10px ); 
       -moz-filter: blur( 10px );
        -ms-filter: blur( 10px );    
            filter: blur( 10px );
    box-shadow: 0 0px 0px 2px #16253b;
    z-index: 0;
    transform: translate( -50%, -50% );
    transition: opacity 200ms ease-in-out;
}
.weekly_list_b:hover::after{
    opacity: 0.6;
}
