css:
@-moz-document url-prefix("https://www.evernote.com/") {
.GJDCG5CMNB {
  max-width: none;
}
.focus-NotesView-NotesView {
  width: 500px;
}
.GJDCG5CBQB.GJDCG5CFQB {
  margin-left: 500px;
}
.GJDCG5CENB.GJDCG5CLLB {
  display: none;
}
.NotesView-ScrollWindow > div > div {
  padding: 5px;
  width: 50%;
  float: left;
}
.NotesView-ScrollWindow > div > div:nth-of-type(-n+3) {
  padding-top: 0;
}
.focus-NotesView-Note {
  height: 220px;
}
.focus-NotesView-Note .focus-NotesView-Note-snippetDivider {
  display: none;
}
.focus-NotesView-Note .focus-NotesView-Note-snippetThumbnail {
  position: absolute;
  top: 25%;
  right: 0;
  max-width: none;
  max-height: none;
  width: 100%;
}
.focus-NotesView-Note .focus-NotesView-Note-snippetContent {
  left: 1px;
  top: 1px;
  right: 1px;
  bottom: 1px;
}
.focus-NotesView-Note-noteTitle.qa-title {
  padding-top: 6px;
}
.focus-NotesView-Note .focus-NotesView-Note-date {
  padding-bottom: 7px;
}
.focus-NotesView-Note.focus-NotesView-Note-hasThumbnail .qa-snippet {
  height: 90px;
  border-bottom: 3px solid #FFF;
  overflow: hidden;
}
.focus-NotesView-Note .focus-NotesView-Note-snippetContent > div {
  margin: 0;
  width: 100% !important;
  padding-left: 12px;
  padding-right: 7px;
  background-color: #FFF;
}
.focus-NotesView-Note:hover .focus-NotesView-Note-snippetContent * {
  color: inherit !important;
}
.focus-NotesView-Note .focus-NotesView-Note-selectOverlay {
  border: 1px solid #e3e3e3 !important;
  transition: border .3s ease-in-out;
}
.focus-NotesView-Note.focus-NotesView-Note-selected .focus-NotesView-Note-selectOverlay {
  border: 1px solid #30BE63 !important;
  border-left: 5px solid #30BE63 !important;
  z-index: 1;
}
.focus-NotesView-Note .focus-NotesView-Note-hoverOverlay {
  display: none;
}
.focus-NotesView-Note .focus-NotesView-Note-snippetHoverContainer {
  top: unset;
  bottom: 11px;
  left: 1px;
  right: unset;
  background-color: #e3e3e3;
  padding: 0px 0px 7px 15px;
  transition: background-color .3s ease-in-out, opacity .3s ease-in-out;
  z-index: 2;
}
.focus-NotesView-Note.focus-NotesView-Note-selected .focus-NotesView-Note-snippetHoverContainer {
  background-color: #2dbe60;
}
.GJDCG5CMQB {
  width: 500px;
}
}
