css:
@-moz-document domain("www.zhihu.com"){
body{
    background: url("data:image/png;base64,");
}
.TopstoryItem {
    position: relative;
    padding: 16px 20px;
    border-radius: 10px;
    transition-duration: 0.35s;
}
.Card.TopstoryItem.TopstoryItem--experimentExpand:hover {
    box-shadow: 1px 5px 8px #e2e2e2;
}
.Card {
    margin-bottom: 10px;
    background: #fff;
    overflow: hidden;
    border-radius: 2px;
    -webkit-box-shadow: 0 1px 3px rgba(26,26,26,.1);
    box-shadow: 0 1px 3px rgba(26,26,26,.1);
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    border-radius: 10px;
    transition-duration: 0.4s;
}
.Card:hover {
    box-shadow: 1px 5px 6px #e8e8e8;
}

[class*="advert"]{
	display:none
}
