css:
@-moz-document domain("skijumping.pl") {
body.modal-open
{
	position: static;
}

body
{
	background-image: none !important;
	background-color: #282828 !important;
	/**
	background-image: url(data:image/png;base64,)!important;
	/**/
}

.row.row-m, nav.navbar.bootsnav, .container, nav.navbar.bootsnav ul.nav > li > a, .post-head, .featured-inner, .post-body, .add-inner, .youtube-wrapper, .archive-wrapper, nav.navbar.bootsnav li.dropdown ul.dropdown-menu, .btn-default, .panel_inner, .post-footer, .post_details_block, .comment-form, .comments-container, .comments-rules-container, .panel, .panel-body, #hill-country-index .hill .info.info-light, #hill .info.info-light, #hill-country-index .hill .info.info-dark, #hill .info.info-dark
{
	background-color: #222;
}

/* bg of selection */
nav.navbar li.dropdown ul.dropdown-menu > li a:hover, nav.navbar.navbar-transparent ul.nav > li > a:hover, nav.navbar.no-background ul.nav > li > a:hover, nav.navbar ul.nav li.scroll.active > a, nav.navbar.navbar-dark ul.nav li.dropdown ul.dropdown-menu > li > a:hover, nav.navbar ul.nav li.dropdown.on > a, nav.navbar-dark ul.nav li.dropdown.on > a, .btn-danger, nav.navbar.bootsnav ul.nav > li > a:hover, nav.navbar.bootsnav ul.nav > li.dropdown.on > a, nav.navbar.bootsnav ul.nav > li.dropdown.on > a:hover, nav.navbar li.dropdown ul.dropdown-menu > li.active a:hover
{
	color: #222 !important;
	background-color: #eb7 !important;
}

nav.navbar.bootsnav ul.dropdown-menu.megamenu-content .content ul.menu-col li a:hover, .side .widget ul.link li a:hover, .side .widget ul.link li a:focus, .check-list li::before, ul.cart-list > li > h6 > a, .attr-nav > ul > li > a:hover, .attr-nav > ul > li > a:focus, nav.navbar-sidebar ul.nav li.dropdown.on > a, nav.navbar-sidebar .dropdown .megamenu-content .col-menu.on .title, nav.navbar-sidebar ul.nav li.dropdown ul.dropdown-menu li a:hover, nav.navbar ul.nav li.dropdown.on > a, nav.navbar.navbar-inverse ul.nav li.dropdown.on > a, nav.navbar-sidebar ul.nav li.dropdown.on ul.dropdown-menu li.dropdown.on > a, nav.navbar .dropdown .megamenu-content .col-menu.on .title, nav.navbar ul.nav > li > a:hover, nav.navbar ul.nav > li.active > a:hover, nav.navbar ul.nav li.active > a, .post-category, .nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover, #scores-tabs .scores-header, .comment-form .btn-news:hover, .comment-form .btn-news:focus, .comment-form .btn-news:active, .comment-form .btn-news, .header-live-now, .header-live-now .container, .trending, button, input[type="color"], input[type="reset"], input[type="button"], input[type="submit"]
{
	background-color: #6f1a1a!important;
	border-color: #777 !important;
}

.header-live-now, .container
{
	background-image: none;
}

.comments-list .comment-avatar, .label
{
	background-color: #62573d !important;
}

/* full white */
h1, h2, h3, h4, h5, .post-head .title, .panel_header h4, .attr-nav > ul > li > a, nav.navbar.bootsnav ul.nav > li > a, nav.navbar.bootsnav li.dropdown ul.dropdown-menu > li > a, .comment-form h3, .btn-primary:hover, .btn:hover, .btn:focus, .btn.focus, #hill-country-index .hill .info span, #hill .info span
{
	color: #fff;
}

a, cite.comment-author
{
	color: #7b7;
}

tr.player_pl > td, tr.player_pl > td > a, article:hover .post-info h3 a, .news-list-item:hover .post-info-2 h5 a, .news-list-item:hover .post-info-2 h4 a, tr.jasne_polacy td, tr.ciemne_polacy td, a.linkified, .post_details_inner p a, #player-country-index .player:hover h4, #player-country-index .player:hover .info span, font
{
	color: #c55 !important;
}

.post-head .title, a:hover, tr.player_pl > td > a:hover, .comment-box .comment-content h5, .comments-rules-container h3, .post_details_block h1, .comments-container h3
{
	color: #eb7 !important;
}

.authar-info i
{
	color: #eb7 !important;
}

#player-country-index .player .info span, #player .info span, .btn-default, p, .menu-services li::before, .menu-services li a, body, .comment-content p, .btn
{
	color: #ccc;
}

#player-country-index .player .info.info-light, #player .info.info-light, .competition-calendar-table tr.light, .tabs-wrapper, table.wyniki tr.ciemne td, table.wyniki tbody > tr.ciemne_polacy td, #player .wyniki tr:nth-child(2n+1) td
{
	background-color: #222;
	color: #ccc;
}

#player-country-index .player .info.info-dark, #player .info.info-dark, .competition-calendar-table tr.dark, .table-striped > tbody > tr:nth-of-type(2n+1), table.wyniki tr.jasne td, #player .wyniki tr:nth-child(2n) td
{
	background-color: #333;
	color: #ccc;
}

table.wyniki tr.jasne_polacy td
{
	background-color: #333;
	color: #ccc;
}

table.wyniki, .post_details_inner, nav.navbar.bootsnav, nav.navbar.bootsnav ul.nav > li, .featured-inner, .post-inner, .add-inner, .weather-wrapper, .tabs-wrapper, .youtube-wrapper, .posts, .panel_inner, .archive-wrapper, .weather-wrapper-2, hr, .comment-form, .comments-header, .comments-container, .comments-rules-container, .panel_header, nav.navbar.bootsnav li.dropdown ul.dropdown-menu > li > a, .post-head, .bord-right, .news-list-item, .post-footer, .panel-group .panel-heading + .panel-collapse > .panel-body, .panel-group .panel-heading + .panel-collapse > .list-group, .btn-default
{
	border-color: #555 !important;
}

.post-head .title, .panel_header h4
{
	border-color: #c55;
}

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td
{
	border: none;
}

td.success, td.info
{
	color: #222;
}

footer, .header-top
{
	background-color: #282828 !important;
	background-image: none;
}

.table > thead > tr > td.success, .table > tbody > tr > td.success, .table > tfoot > tr > td.success, .table > thead > tr > th.success, .table > tbody > tr > th.success, .table > tfoot > tr > th.success, .table > thead > tr.success > td, .table > tbody > tr.success > td, .table > tfoot > tr.success > td, .table > thead > tr.success > th, .table > tbody > tr.success > th, .table > tfoot > tr.success > th
{
	background-color: #489;
	color: #ccc;
	font-weight: 900 !important;
}

.table > thead > tr > td.info, .table > tbody > tr > td.info, .table > tfoot > tr > td.info, .table > thead > tr > th.info, .table > tbody > tr > th.info, .table > tfoot > tr > th.info, .table > thead > tr.info > td, .table > tbody > tr.info > td, .table > tfoot > tr.info > td, .table > thead > tr.info > th, .table > tbody > tr.info > th, .table > tfoot > tr.info > th
{
	background-color: #379;
	color: #ccc;
	font-weight: 900 !important;
}

.header-live-now ul li:first-child
{
	text-shadow: none;
}

.header-live-now ul li .links, .header-live-now ul li .links a
{
	color: #fac832;
	font-weight: 900;
}

.header-live-now ul li .links a
{
	text-decoration-style: solid;
	text-decoration-line: overline underline;
	text-decoration-color: #ff3636;
}
}
