css:
@-moz-document domain(kingkong.com.tw),
               domain(passport.lv-play.com)
{
body,
.view-index,
.view-all-live {
	background-color: #000;
}
body,
.view-index,
.view-all-live {
	background-color: #000;
}
/*.view {
    margin-top: 84px;
}*/
.head {
    margin-top: 0px !important;
}
.view-draw-wrap,
.view-activity-pubg,
.view-mu,
.view-live .theme-fullscreen,
.view.view-live.theme-game-lol.theme-fullscreen,
.view-live-rank{
    margin-top: 60px;
}
/*body, .view-index, .view-all-live {
	開頭播放區 上方高度調整
    margin-top: 0px;
}*/
/*------------------------------------------------------------------------------------------------------------------------------------首頁*/
a.header-item-con.header-logo-link {
    /*金剛logo*/
    width: 100px;
background:url(data:image/svg+xml;base64,);
    background-size: 100px;
    background-repeat: no-repeat;
    background-position: 0 9px;
    opacity:.4;
}
a.header-item-con.header-logo-link:hover {
    /*金剛logo hover*/
    width: 100px;
background:url(data:image/svg+xml;base64,);
    background-size: 100px;
    background-repeat: no-repeat;
    background-position: 0 9px;
    opacity:1;
}
.header a.header-item-con {
   /*logo 位置調整*/
     margin-bottom: -24px;
}
.header .header-logo-link .header-logo {
	/*原始logo*/
    display:none;
}
svg.beta-header {
	/*beta 字*/
    display: none;
}
.header .category-topbar {
    /*導覽列 分類展開*/
    background: #24252c;
    box-shadow: 0px 3px 15px -1px rgba(0,0,0,.8);
}
.header .category-topbar .category-topbar-title {
    /*導覽列 分類展開 大標題*/
    color: #ffdb30;
}
.header .category-topbar a.category-item {
    /*導覽列 分類展開 單一分類*/
    color: #fff;
    border: 1px solid #42424e;
    border-radius:20px;
}
.header .category-topbar a.category-item:hover {
    /*導覽列 分類展開 單一分類 hover*/
    color: #000;
}
.header .category-topbar .view-all-category {
    /*導覽列 分類展開 全部分類*/
    border-radius: 20px;
}
.header-user-info .user-topbar-nickname {
    /*導覽列 個人展開 名稱*/
    color: #ffda00;
}
.header-user-info .user-wallet-title {
    /*導覽列 個人展開 我的錢包標題*/
    color: #fff;
}
.header-user-info .user-wallet-item {
    /*導覽列 個人展開 我的錢包小字*/
    color: #b1b1b1;
}
.header-user-info .user-wallet {
    /*導覽列 個人展開 分隔線*/
    border-top: solid 1px #42424e;
    border-bottom: solid 1px #42424e;
}
.header-user-info li {
    /*導覽列 個人展開 圓角*/
    border-radius: 20px;
}
.header-user-info li:hover {
    /*導覽列 個人展開 圓角*/
    border-radius: 10px;
}
.banner.active {
/*Banner 外框*/
	border: 2px solid #24252c;
}
.banner-fixed-arrow {
/*隱藏Banner右下三角形*/
    display: none;
}
.fixed-news-content {
    /*最新消息 背景*/
    background: #24242c;
}
.fixed-news-content .news-index-title .news-more-btn {
    /*最新消息 更多 按鈕*/
	border-radius: 20px;
    border: 1px solid #ffda00;
    color: #ffda00;
}
.fixed-news-content .news-index-title .news-more-btn:after {
    /*最新消息 更多 旁邊三角形*/
    border-left: 8px solid #ffda00;
}
.fixed-news-content .news-index-title {
    /*最新消息 水平線*/
    border-bottom: 1px solid #42424e;
}
.fixed-news-content .news-index-title h3 {
    /*最新消息 大標題*/
	color: #ffda11;
}
.index-news-wrap li a p {
    /*最新消息 單條標題*/
	color: #fff;
    transition: .2s;
}
.index-news-wrap li a span {
    /*最新消息 單條時間*/
	    color: #727282;
}
.index-news-wrap li:hover p {
    /*最新消息 單條標題 hover*/
    color: #ffda11;
    margin-left: 6px;
    transition: .2s;
}
.index-news-wrap li:hover span {
    /*最新消息 單條標題 hover*/
    color: #aba899;
}
a.have-news-banner:hover {
	/*最新消息 上方Banner hover*/
    outline: 1px solid #ffda00;
}
.view.view-lang-text.view-activity #content h2 {
	/*最新消息 內文 文字*/
    color: #fff;
}
.view.view-lang-text.view-activity #content p font {
	/*最新消息 內文 文字*/
    color: #fff !important;
}
.banner {
	border: 2px solid rgba(0,0,0,0);
}
.banner a {
/*Banner hover樣式設計-1*/
    transition: all 160ms;
}
.banner a:hover {
/*Banner hover樣式設計-2*/
    transition: all 160ms;
}
.swiper-container-horizontal>.swiper-pagination-bullets .swiper-pagination-bullet {
	/*Banner 間距*/
    margin: 0 12px 0 0px;
}
.swiper-pagination-bullet {
	/*Banner 按鈕*/
    width: 40px;
    height: 1px;
    border-radius: 2px;
}
.banner .swiper-button-prev,
.banner .swiper-button-next {
	/*banner 左右按鍵*/
    border: 1px solid #ffda00;
    border-radius: 30px;
    background-size: 52px !important;
    background-position: -1px -1px !important;
}
.kkplayer-intoRoom {
/*開頭播放區"進入直播間"按鈕*/
    background: rgba(80,75,0,.7);
    border-radius: 500px;
}
.kkplayer-actions a {
/*開頭播放區"重新載入"按鈕*/
    border-radius: 500px;
}
.videos {
/*開頭播放區塊背景*/
	background: url(http://);
/*開頭播放區塊下方高度*/
    margin-bottom: -20px;
}
.home-sky.hide-bg {
    cursor: auto !important;
}
.home-sky.hide-bg:before {
    /*刊頭廣告包版 隱藏！*/
    content: " ";
    background: rgba(0,0,0,0.7);
    opacity: 1;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    position: absolute;
}
a.activity-icon.j-click-statistic {
    /*播放區小廣告*/
    display: none !important;
}
.banner .swiper-pagination-bullet-active {
/*播放區塊小圓點 改黃*/
    background: #ffda11 !important;
}
.banner .swiper-button-prev,
.banner .swiper-button-next{
/*播放區塊 左右按鍵 改黑*/
    background: rgba(0,0,0,.7);
}
.bg-avatar {
/*開頭播放區 主播頭像 陰影隱藏*/
    display: none;
}
.video-container {
	padding-top: 20px;
}
/*.video-container .video-content {
    開頭播放區 押陰影 做漸層
    box-shadow: 0 0 80px #000;
}*/
.video-container .avatar-content .avatar {
/*開頭播放區 主播頭像 圓形*/
	border-radius: 500px;
}
.video-content {
/*開頭播放區塊內直播間背景*/
    background: #24252c;
}
.video-container .video-list {
/*開頭播放區塊內直播間選單背景*/
    background: #1a1a1f;
}
.video-container .desc{
/*開頭播放區塊內直播間文字斷行*/
	word-break: break-all;
}
/*標題頭樣式修改
.content-title h3 {
    color: #ffda11;
    width: 170px;
    background: #1a1a1f;
    padding-right: 50px;
    border-radius: 10px 50px 0px 0px;
	background-image: url(http://) !important;
}*/
.content-title h3 {
/*標題頭樣式修改*/
    letter-spacing: 1px;
    font-weight: 500;
	background-image: url(http://)!important;
    background: #ffda00;
    transform: skewX(-15deg);
    border-radius: 3px;
    border: 2px solid #ffe651;
    box-shadow: 0 1px 13px #ffda00;
    width: 14%;
}
.content-title h3::before {
/*標題頭樣式 加回左邊那條線*/
    border-left: 4px solid #000000;
    content: "";
    width: 0px;
    position: fixed;
    height: 70%;
    margin-left: 10px;
    margin-top: 5px;
}
.content-title h3 span {
/*標題頭樣式 字高調整*/
    line-height: 36px;
    color:#000;
}
.content-title .is-showmore a.load-more {
/*查看更多.更多按鈕*/
	color: #FFF;
    line-height: 25px;
    border-radius: 100px;
    padding-left: 15px;
    padding-right: 25px;
    padding-bottom: 4px;
    border: 1px solid #9c9c9c;
    box-shadow: 0 0 0 rgba(255, 255, 255, 0);
    transition: all 200ms cubic-bezier(0.39, 0.58, 0.57, 1);
}
.content-title a.load-more:hover {
/*查看更多.更多按鈕 hover*/
    border: 1px solid #ffda00;
	box-shadow: 0 0 20px rgba(255, 218, 0, .8);
}
.hot-anchor-box {
/*"熱門遊戲"、"分類"底版*/
	color: #FFF;
    background: #24252c;
}
.detail-title .anchor-direction{
/*遊戲名稱文字*/
	color: #FFF;
}
li.scan-qrcode,
li.recharge {
/*右側選單 下載、儲值*/
    display: none;
}
.index-guide-container {
/*右側選單*/
    right: 20px;
    bottom: 60px;
}
.index-guide-container li {
/*右側選單 按鈕*/
	background: #1a1a1f;
}
.index-guide-container li,
.index-guide-container li.return-top.active {
/*右側選單 圓形化*/
    opacity: 1;
    border-radius: 500px;
    background: rgba(89,89,105,.75);
    transition: all 300ms ease-in-out;
    padding-bottom: 52px;
}
.index-guide-container li.return-top.active:hover {
/*右側選單 圓形化 hover*/
    opacity: 1;
    transition: all 300ms ease-in-out;
    background: #ffda11;
    padding-bottom: 100px;
}
li.recharge-guide {
    display:none;
}
.index-guide-container .recharge-guide:before {
/*右側選單 儲值*/
    content: " ";
    height: 50px;
    width: 50px;
    position: absolute;
	margin-left: -14.6px;
    margin-top: 11px;
    background-size: 30px;
    background-image: url(data:image/svg+xml;base64,);
    background-repeat: no-repeat;
}
.recharge-guide:hover::before {
/*右側選單 儲值 hover*/
    content: " ";
    height: 50px;
    width: 50px;
    position: absolute;
	margin-left: -14.6px;
    margin-top: 11px;
    background-size: 30px;
    background-image: url(data:image/svg+xml;base64,);
    background-repeat: no-repeat;
}
.index-guide-container li.recharge-guide svg {
    display: none;
}
/*.end-counts::after {
生存人數調整
    content: "生存中";
    font-size: 10px;
    padding: 10px;
    background: #000;
    color: #fff;
    width: 100%;
    height: 8px;
    line-height: 8px;
    position: fixed;
}*/
.end-counts span {
    display: inherit;
    line-height: 28px;
    font-size: 14px;
    font-weight: bolder;
    color: #282828;
    text-align: right;
    padding-right: 10px;
}
.end-counts {
    width: 40px;
    height: 28px;
    background-image: url(http://);
    background: linear-gradient(130deg,#ffda00,#ff9900);
    transform: skewX(-10deg);
    border-radius: 3px;
    margin-top: 4px;
    margin-right: 3px;
}
.end-total {
/*決賽圈*/
    margin-top: 3px;
	width: 80px;
    height: 100%;
    background-image: url(http://);
    transform: skewX(-10deg);
}
.end-total::after {
    content: "決賽中";
    background: linear-gradient(130deg,#ff1328,#790016);
    color: #FFF;
    font-size: 13px;
    letter-spacing: .5px;
    font-weight: bolder;
    width: 90px;
    height: 30px;
    line-height: 30px;
    padding: 5px 17px;
    padding-right: 20px;
    border-radius: 3px;
}
.isDraw {
/*抽獎*/
    width: 70px;
    background-image: url(https://);
}
.isDraw::after {
/*抽獎*/
	content: "抽獎";
    background: linear-gradient(130deg,#ff136a,#ff7b9a);
    color: #FFF;
    font-size: 13px;
    letter-spacing: .5px;
    font-weight: bolder;
    width: 100px;
    height: 30px;
    line-height: 47px;
    padding: 4px 8px;
    margin-left: 10px;
    border-radius: 3px;
    box-shadow: 0 2px 5px rgba(255, 80, 135, 0.5);
}
.list-anchor-detail .anchor-tag .anchor-tag-item {
    /*主播標籤*/
    color: #7e7e7e;
    background: #151617;
}
.video-container .video-list .item .video-name:before {
/*熱門區 標題 去標題前細線*/
    display:none;
}
.video-container .video-list .item .video-name {
/*熱門區 標題*/
	padding: 10px 25px;
    border-radius: 100px;
    transition: all 250ms;
}
.video-container .video-list .item .video-name{
    transform: scale(.5) translateY(-210px);
}
.video-container .video-list .item:hover .video-name{
/*熱門區 標題 hover*/
    transform: translateY(-120px);
}
.video-container .video-list .item:after {
/*熱門區 標題 向下箭頭*/
    bottom: 102%;
    left: 20px;
    transform: scale(.5) translateX(100px);
    transition: all 300ms;
}
.video-container .video-list .item:hover:after {
/*熱門區 標題 向下箭頭 hover*/
    transform: translateY(0px);
}
.detail-title .viewers {
/*熱度 icon 調整*/
	padding-left: 16px;
    background-image: url(data:image/svg+xml;base64,);
	background-size: 13px;
    background-repeat: no-repeat;
    background-position-y: 2px;
}
.video-container .video-list .item .heat {
/*熱門區 熱度 icon 調整*/
    fill-opacity: 0;
    background-size: 15px;
    background-position: 1px 1px;
    background-repeat: no-repeat;
    background-image: url(data:image/svg+xml;base64,);
}
.hot-bg .default-bg-img,
.hot-live-bg.lazy-load-bg {
/*載入中背景圖 修改 香蕉皮*/
    background-image:url(http://);
    background-image: url(data:image/svg+xml;base64,);
}
/*------------------------------------------------------------------------------------------------------------------------------------最新消息*/
.news-title-icon h3::before {
    border-left: 4px solid #000000;
    content: "";
    width: 0px;
    position: fixed;
    height: 70%;
    margin-left: -30px;
    margin-top: 5px;
}
.news-title-icon h3 {
    letter-spacing: 1px;
    font-weight: 500;
    line-height: 36px;
    background-image: url(http://)!important;
    background: #ffda00;
    transform: skewX(-15deg);
    border-radius: 3px;
    border: 2px solid #ffe651;
    box-shadow: 0 1px 13px #ffda00;
    width: 14%;
}
a.header-item-con {
	/*導航列 連結 突然蹦出來的底線*/
    text-decoration: none;
}
.topic-news {
    /*底色*/
	background: #24252c;
}
.topic-news .current-place a:hover {
	/*最新消息 hover*/
    color: #ffda00;
}
.topic-news .topic-title {
    /*水平線*/
    border-bottom: 1px solid #42424e;
}
.news-wrap-content,
.news-wrap .same-page-content {
    /*底色*/
	background: #24252c;
}
.news-wrap-content .news-title-box li a.news-detail-btn {
    /*單條消息 標題*/
	color: #fff;
}
.news-wrap-content .news-title-box li a.news-detail-btn:hover {
	/*單條消息 標題 hover*/
    color: #ffda00;
}
.news-wrap-content .news-title-box li {
    /*單條消息 水平線*/
    border-bottom: 1px solid #42424e;
    height: 60px;
    line-height: 60px;
}
.news-wrap-content .news-title-box li .start-time {
    /*單條消息 日期*/
    line-height: 60px;
}
/*------------------------------------------------------------------------------------------------------------------------------------導覽列*/
.header{
/*導覽列底版*/
	background: #24252c;
	border-bottom: 0px;
    box-shadow: 0 2px 3px rgba(5, 5, 6, .5);
}
.header a.header-index-link {
/*字距調整*/
    letter-spacing: .04rem;
}
.header a.header-menu.active {
/*所在位置顯示*/
    background: #151617;
}
.header a.header-menu:hover.active {
/*所在位置顯示 hover*/
    background: #ffda00;
    border-bottom: 0px;
    color: #000;
}
.header a.header-menu:hover span {
    color: #000;
    font-weight: 900;
}
.header a.header-menu {
/*所在位置顯示 未觸發*/
    background: rgb(255, 218, 0, 0);
    transition: all .2s;
}
.header a.header-menu:hover {
/*所在位置顯示 hover*/
    background: rgb(255, 218, 0, 1);
    border-bottom: 0px;
    color: #000;
    transition: all .2s;
}
.header a.header-menu:after {
/*所在位置顯示 底下的線 去掉*/
    display: none;
}
.search input {
/*搜尋列圓角化*/
    color: #fff;
	border-radius: 500px;
}
.search .search-btn {
/*搜尋icon修改*/
    background-image: url(data:image/svg+xml;base64,);
    background-size: 100% 100%;
    transition: all 300ms ease-out;
    transform: scale(.9);
}
.search input:focus+.search-btn {
        background-image: url(data:image/svg+xml;base64,);
    transform: scale(1.05);
	transition: all 300ms cubic-bezier(0.18, 5, 0.6, 2);
}
/*隱藏下載*/
.header-item.download,
.lead-download {
	display: none;
}
.header-follow svg {
/*追蹤icon修改*/
    width: 22px;
    height: 22px;
    margin-left: -5px;
    margin-bottom: 3px;
    fill-opacity: 0;
    stroke-opacity: 0;
    background-image: url(data:image/svg+xml;base64,);
	transition: all 300ms ease-out;
    transform: scale(.9);
}
.header-item.header-follow:hover svg{
    background-image: url(data:image/svg+xml;base64,);
	transition: all 300ms cubic-bezier(0.18, 5, 0.6, 2);
    transform: scale(1.05);
}
.header .header-pull-down {
/*"追蹤"下滑選單 陰影去除*/
	box-shadow: 0 0 0 0;
}
.follow-item.header-pull-down {
/*"追蹤"下滑選單 位置調整*/
    left: -234px;
}
.header .header-pull-down .header-pull-down-container {
/*"追蹤"下滑選單*/
	background: #24252c;
    box-shadow: 0px 3px 15px -1px rgba(0,0,0,.8);
}
.header-follow ul li:hover .follow-anchor-intro .follow-live-title,
.header-follow ul li:hover .follow-anchor-intro .follow-nickname {
/*"追蹤"下滑選單 hover*/
    color: #252525;
}
.follow-item.header-pull-down {
/*"追蹤"下滑選單 下滑淡出淡入*/
    pointer-events: none;
	display: block;
    opacity: 0;
    transform: translateY(-5px);
    transition-duration: 200ms;
    transition-property: opacity, transform;
    transition-timing-function: ease-in-out;
}
.header-item.header-follow:hover .follow-item.header-pull-down {
/*"追蹤"下滑選單 下滑淡出淡入 hover*/
    pointer-events: auto;
    opacity: 1;
    transform: translateY(0);
}
.header-follow ul a{
/*"追蹤"下滑選單 子項目*/
    border-bottom: 0px;
}
.header-follow ul li.view-all,
.header-follow ul li.view-all:hover {
/*"追蹤"下滑選單 子項目 查看全部 背景*/
    background-color: rgba(0,0,0,0);
	padding-bottom: 12px;
}

.header-follow ul li.view-all a.view-all-follow {
/*"追蹤"下滑選單 子項目 查看全部 按鈕*/
	border-radius: 20px;
    background-color: rgba(0,0,0,0);
    border: solid 1px rgba(255, 218, 17, .5);
	color: #ffdb0f;
}
.header-follow ul li.view-all a.view-all-follow:hover {
/*"追蹤"下滑選單 子項目 查看全部 按鈕 hover*/
    background-color: #ffda11;
    color: #252525;
}
.header-follow ul li.no-record {
/*"追蹤"下滑選單 尚未追蹤 文字*/
    color: #FFF;
    font-size: 15px;
    border-bottom: 0px;
}
.header-follow .follow-anchor-intro .follow-nickname {
/*"追蹤"下滑選單 主播暱稱*/
	color: #ffda11;
    width: 150px;
    font-size: 13px;
}
.header-follow .live-status {
/*"追蹤"下滑選單 直播中、結束時間 */
    top: 32%;
    font-size: 10px;
