css:
@-moz-document domain("berkutschi.com") {
body, header, div, .container, tr, p, .meta, .contain-to-grid, table, .left-off-canvas-menu, .card, .main-news .current-events, .main-news .current-events .event-item
{
	background: none #222;
	color: #ccc;
	border-color: #777;
}

h1, .tabs-content, .top-bar, .top-bar-section li, .top-bar-section li a, .contain-to-grid, .button, input, .tab-bar, .tabs dd > a, .tabs .tab-title > a, .panel, input:not([type]), input[type="text"], input[type="password"], input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="month"], input[type="week"], input[type="email"], input[type="number"], input[type="search"], input[type="tel"], input[type="time"], input[type="url"], input[type="color"], textarea
{
	background: none #333;
	color: #ccc;
	border-color: #777;
}

table tr.even, table tr.alt, table tr:nth-of-type(2n), .value, flippy.fancy flippy-front, .tabs dd.active > a, .tabs .tab-title.active > a
{
	background: #333;
	border-color: #777;
}

td, table tr th, table tr td
{
	color: #ccc;
	border-color: #777;
}

a
{
	color: #ac4;
	border-color: #777;
}

table thead tr th, table thead tr td, ul.off-canvas-list li a
{
	color: #eee;
}

/* hovery */
.tabs dd > a:hover, .tabs .tab-title > a:hover, ul.off-canvas-list li a:hover
{
	background-color: #db4;
	color: #222;
}

.green
{
	color: #5c5;
}

.red
{
	color: #c55;
	;
}
}
