css:
@-moz-document domain(aa.ssdi.di.fct.unl.pt) {
    



html,body {
background:#2A2A2A;
font-family:verdana,sans-serif;
margin-top:0px;
overflow:auto;
}
a {
 color:#ADD8E6;
 text-decoration:none;
}
a:hover {
 text-decoration:underline;
 }
a.sidebar {
  color:#ADD8E6;
  text-decoration:none;
  }
a.sidebar:hover{
   text-decoration:underline;
  }
p {
  color:white;
  font-size:85%;
  margin-bottom:0.5em;
  margin-left:1.5em;
  margin-right:0.5em;  
  }
li{
  font-size:95%;
  margin-left:1.5em;
  margin-right:0.5em;   
  }
p.main{
 margin-left:1em;
 margin-right:1em;
}
#wrap {
    background:transparent;
    margin-left:3%;
    margin-right:3%;
    width:94%;
    position:absolute;  
    min-height: 100%;
    height: auto !important;
    height: 100%;
         
    }
#nav {
    background:transparent;
    color:#770000;
    text-align:center;
    font-weight:bold;
        
    }
#header {  font-family: Arial,Helvetica,sans-serif;
  font-size: 170%;
  font-weight:bold;
  text-align: left;
  text-indent: 5%; 
  padding-top:4%;
  padding-bottom:4%;
  color: #FFFFFF;  
  margin-bottom:1%;
  margin-top:1%;
  background: #2A2A2A;
  border:2px  ridge #818181; 
  }
#main {
	background:#2A2A2A;
    float:right;
    width:83%;
    padding:0; 
    margin:0;   
    position:relative;
    }

#push {
	height: 4em;
	}

#footer {    
   height: 4em; 
   float:right;  
   background:transparent;
   text-align:center;
    padding:0.5em;   
    text-indent:0%;
    width:83%;
    padding:0; 
    margin:0;   
    position:relative;

    }
#sidebar {
    background:transparent;
    color:white; 
    font-weight:bold;
    float:left;
    width:15%;
    padding:0%;
    margin:0;    
    position:relative;
    }
#right_item{
float:right;
width:20%;
}
#half_div{
padding:0;
float:left;
display:inline;
width:45%;
margin:1%;
}
img.right_item{
 width:95%; 
 display: inline;
 border:0;
 margin:0.2em;
 }
#item{
  background:#2A2A2A;
  float:left;
  margin-bottom:0.5em;
  margin-left:0.5em;  
  border:2px solid #818181;
  width:95%;
  display:block;
}
#itemwide{
  background:#F0F0F0;
  float:left;
  margin:1%;
  border:2px solid #818181;
  width:97%;
}
#sideline {
  font-size:90%;
  margin-left:0.5em;  
  margin-bottom:0.5em;
}
#center {
text-align:center; 
}
#sidetitle {
  font-size:100%;
  margin:1%;
  margin-bottom:0.5em;
  margin-top:1em;
  color:#818181;
  text-align:center;
  }
p.hidden {display: none;}
p.box {padding-left: 2%;
  
  padding-right: 2%;
  padding-bottom: 2%;
  padding-top: 2%;
  background-color: #002c8c;
  color: #ffffff;
  border: medium #2d00ff;
  font-weight:bold;}

p.centered {  text-align: center;}

p.code {  font-family: Courier New,Courier,monospace;
  margin-top: 0px; margin-bottom: 0px; 
  line-height: normal;
  font-size:100%;}

code {  font-family: Courier New,Courier,monospace;
  margin-top: 0px; margin-bottom: 0px; 
  line-height: normal;
  font-size:100%;}
  
dd.code {  font-family: Courier New,Courier,monospace;
  margin-top: 0px; margin-bottom: 0px;
  line-height: normal;}

h1 { 
  font-size: 130%;
  font-weight:bold;
  text-align: left;  
  color: #ADD8E6;
  margin:1%;
  margin-left:0.5em;
  }
 
h2 { 
  font-size: 100%;
  font-weight:bold;
  text-align: left;    
  color: #ADD8E6;
  margin-left:1em;
  margin-bottom:0.5em;
  margin-top:0.8em;
  }
h3 {
  font-size: 85%;
  font-weight:bold;
  text-align: left;    
  color: #FFFFFF;
  margin-left:1.5em;
  margin-bottom:0.2em;
  margin-top:0em;}
h3.anteriores {background-color: #cFcFcF;}
h4 { font-family: Arial,Helvetica,sans-serif;
  font-size: 100%;
  font-weight:bold;
  text-align: left;
  padding-top:2%;
  padding-bottom:0%;    
  color: black;
  text-transform: none;}

 table {
  margin-left:auto; margin-right:auto;
  border-width: 1px;
  border-spacing: 2px;
  border-style: outset; 
  border-color: #808080;
  border-collapse: separate; 
  background-color: #ffffff; 
  text-align: center;
  }
table th {
  border-width: 1px;
  border-spacing: 2px;
  border-style: outset; 
  border-color: #808080;
}

table td {
  border-width: 1px;
  border-spacing: 2px;
  border-style: outset; 
  border-color: #808080;
}
table tr {
  border-width: 1px;
  border-spacing: 2px;
  border-style: outset; 
  border-color: #808080;
}

table.pauta {
 width:90%;
  text-transform: capitalize;
 }
table.pauta th {
  background-color: #505050;
  color:white;
}
ul {
  font-size:95%;
  list-style-type: disk;}
ul.navbar {
 float:left;
 width:20%;
 }
dl {
  padding-left: 5%;
  padding-right: 5%;
  font-size:85%;
  color: white;
  }

dl dt {margin-top:0.5em;font-weight:bold;}
dl.aviso dd {  font-family: Arial,Helvetica,sans-serif;  
  font-style: italic;  
  padding-left: 2%;
  padding-right: 2%;}

dl.aviso dt {  font-family: Arial,Helvetica,sans-serif; 
  font-weight:bold;}

dl.grande dt { font-size:120%;}


}
