css:
@-moz-document url-prefix("https://www.facebook.com/")
{
  ._5vb_, ._5vb_ #contentCol { 
    background-image: url('https://static1.squarespace.com/static/55c8073fe4b02a74ffe18e48/t/589968c9403c66bb1dafbe65/1486448907681/?format=original') !important;
    background-repeat: no-repeat !important;
    background-size:cover !important;
    background-attachment: fixed !important;
  }

  ._2s1x ._2s1y {
    background: rgba(62,165,145,0.7) !important;
    background-color: rgba(62,165,145,0.7) !important;
    border-bottom: 1px solid #283f67 !important;
    color: #fff !important;
}
._5vb_ ._5pr2.fbChatSidebar, .fbCalendar ._5pr2.fbChatSidebar, ._1t4w ._5pr2.fbChatSidebar, .timelineLayout ._5pr2.fbChatSidebar {
    box-shadow: 1px 0 0 #00000000 inset !important;
    background: rgba(48, 132, 117,0.3) !important;
}
  .fbNub._50mz .fbNubFlyoutBody {
    background-image: url('https://static1.squarespace.com/static/55c8073…/t/589968c…/1486448907681/?format=original') !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
    border: 0;
}/*聊天背景圖片*/
.fbNub._50mz.highlightTab .fbNubButton, .fbNub._50mz .fbNubFlyoutTitlebar {
    background: rgba(62,165,145,0.8) !important;
    box-shadow: 0 1px 1px 0 rgba(39, 39, 39, 0.2) !important;
    color: #ef1b1b !important;
    height: 26px !important;
    padding-right: 3px !important;
    z-index: 2 !important;
}/*聊天top顏色*/


._552h {
    border-top: 1px solid #434b56 !important;
    border-top-width: 0 !important;
    cursor: text !important;
    max-height: 90px !important;
    min-height: 16px !important;
    overflow-x: hidden !important;
    overflow-y: auto !important;
    padding: 10px 8px 6px !important;
    position: relative !important;
    background: rgba(62,165,145,0.5) !important;
}
html ._4lh, ._4lh .fbTimelineTimePeriod, ._4lh .fbTimelineSectionExpandPager .uiMorePagerLoader, ._4lh .fbTimelineCapsule li.anchorUnit:last-child, ._4lh .fbTimelineSectionLoading .loadingIndicator {
    background: url(https://images5.alphacoders.com/878/878916.jpg) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
    background-repeat: no-repeat !important;
}


/*去廣告*/
._1-ia, .pagelet:not(:empty)~.pagelet ._1-ia, .sidebarMode .pagelet:not(:empty)~#pagelet_rhc_ticker_card~.pagelet ._1-ia, .sidebarMode #pagelet_rhc_ticker_card~.pagelet:not(:empty)~.pagelet ._1-ia {
    padding-top: 12px !important;
    display: none !important;
}
/*去廣告*/
._45mq {
    margin-top: 12px !important;
    display: none !important;
}


}
