css:
@-moz-document domain("michaelmclaughlin.info"), domain("mclaughlinsoftware.com") {
div.main div.dropdownbox {
    background: white !important;
    border-radius: 3px;
    padding-top: 10px;
    padding-bottom: 1px;
    box-shadow: 1px 1px 5px .1px;
    margin-bottom: 10px;
    padding-left: 10px !important;
    padding-right: 10px !important;
}
div.main {
    background: #edede7;
    padding: 15px;
    box-shadow: 1px 1px 5px .1px;
    margin-bottom: 10px;
}
p.dropdownclick {
    font-size: 20px;
}
div.main div.dropdownbox>div {
    margin-top: 0px;
}
div.scroller-body {
    border: none;
    overflow-x: visible;
}
div.options a {
    background: white;
    box-shadow: 1px 1px 1px 1px;
    margin-right: 10px;
}
div.options a:hover {
    background: #d8d8d8;
}
div.options a:active {
    background: #92d0e8;
}
/* following came from mobile version */

code,
kbd,
pre,
tt {
    border: 1px solid #ffd478;
    background-color: #f2fdc7;
    padding: .1px;
    /* I didn't like 5px padding, so I changed it. */
}
#sidebar>ul>li.page_item,
#sidebar>ul>li.page_item>a {
    border-bottom: 1px solid rgba(0, 0, 0, .12);
    border-top: 1px solid rgba(255, 255, 255, .06);
    font-family: 'Open Sans', sans-serif;
    text-shadow: rgba(0, 0, 0, .3) 0 -1px 0;
    display: block !important;
    -o-text-overflow: ellipsis;
    -ms-text-overflow: ellipsis;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: default;
    padding-top: 2px;
    padding-bottom: 5px;
    padding-left: 2px;
    font-size: 100%;
    text-shadow: rgba(255, 255, 255, .2) 0 1px 0;
    line-height: 200%;
    font-weight: bold;
    font-size: 1em;
    position: relative;
    margin: 0;
}
#sidebar>ul>li.page_item {
    padding: 0 !important;
}
#sidebar>ul>li.page_item a {
    text-decoration: none;
    display: block;
}
#sidebar>ul>li.page_item>ul {
    display: none;
    position: absolute;
    min-width: 160px;
    z-index: 1;
    top: -5px;
    left: -180px;
    padding: 5px;
    background: white;
    box-shadow: 1px 1px 8px .1px black;
}
#sidebar>ul>li.page_item>ul>li {
    border-bottom: 1px solid rgba(0, 0, 0, .12);
    border-top: 1px solid rgba(255, 255, 255, .06);
}
#sidebar>ul>li.page_item:hover>ul {
    display: block;
}
}
