css:
@-moz-document domain("showbie.com") {
body {
background-color: #252525;
}

.ui-form {
    background-color: #2f2f2f;
}
.l-navbar__text {
    color: #ffffff;
    font-weight: bolder;
}
.ui-button-list .ui-button+.ui-button {
    background-color: #3c3c3c;
}
.ui-form .ui-select, .ui-form input:not([type=checkbox]):not([type=radio]), .ui-form label, .ui-form textarea {
    color: #fff;
}
body, html {
    background: #212121;
}
.x-list__button {
    background-color: #2f3334;
}
.x-list--help .x-list__link {
    color: #fff;
    text-decoration: none;
    background-color: #2f3334;
}
.sbe-help .help__button:not(:first-child) .help__button-content, .sbe-help .help__version, .x-list__item+.x-list__label:last-child {
    box-shadow: inset 0 1px 0 0 rgba(7,2,20,.1);
    background-color: #333333;
}
.x-list--help .x-list__content {
    color: #fff;
}

.ui-help__menu::after {
    visibility: hidden;
}
.x-list__button:focus, .x-list__button:focus .status-icon__accessory-icon, .x-list__button:hover, .x-list__button:hover .status-icon__accessory-icon, .x-list__link:focus, .x-list__link:focus .status-icon__accessory, .x-list__link:hover, .x-list__link:hover .status-icon__accessory {
    background: #2f3334;
}
.ui-button--primary {
    background-color: #a95959;
    color: #fff;
}
.showbie-sign-up__popover.l-popover {
    width: 360px;
    min-height: 440px;
    background-color: #333333;
}
.ui-button-list--stretch .ui-button {
    background-color: #3c3c3c;
}

.sbe-navbar {
    background: #333333;
}
.sbe-navbar a.active, .sbe-navbar button.active {
    background: #252525;
}
.sbe-navbar .sbe-navbar--profile {
    box-shadow: -1px 0 0 0 #333333;
}

.sbe-navbar .navbar__group--left {
    box-shadow: inset -1px 0 0 0 #333333;
}

.sbe-avatar__image {
    border-radius: 31%;
}
.sbe-navbar a:hover, .sbe-navbar button:hover {
    background: #252525;
}
.master-panel__header {
    background: #252525;
}
.master-panel__header>.ui__nav-header .nav-header__title {
    color: #fff;
}
.ui__icon-button>svg {
    fill: #ffffff;
}
.sbe-navbar {
    box-shadow: inset 0 -1px 0 0 #252525;
}
.panel__master-content {
    background-color: #252525;
}
.panelheader {
    background: #252525;
    color: #fff;
}
.panelitem article {
    background-color: #333333;
}
.panelitem__title {
    color: #fff;
}
.folder--fresh, .is-leaf-cell.assignment--fresh {
    border-right: 4px solid #ffffff;
}
.show-master-panel .panel__detail {
    background-color: #252525;
}
.show-master-panel .sidebar-reload {
    background-color: #333333;
}

.sbe-popover-header {
    background-color: #333333;
}

.ui__nav-header .nav-header__title {
    color: #fff;
}
element.style {
    max-height: 773px;
    background-color: #292929;
}
.sbe-popover .sbe-popover-body--form, .sbe-styleguide-container .sbe-popover-body--form {
    background-color: #2626269e;
}
.x-list__item:last-child .x-list__content {
    box-shadow: none;
    background-color: #333;
}
.x-list__link {
    color: #ffffff;
}
.x-list--nav .x-list__content {
    background-color: #333;
}
.profile__fullname {
    color: #fff;
}
*, ::after, ::before {
    color: #fff;
}
.ember-modal-overlay.translucent {
    background-color: rgba(0, 0, 0, 0.33);
    opacity: 1;
}
.sbe-popover .sbe-popover__list, .sbe-styleguide-container .sbe-popover__list {
    background: #333;
}
.folder-details .panel__detail-scroll-content {
    background-color: #252525;
}
.ui__detail-header {
    background: #252525;
}

.sbe-post-form__padding {
    background-color: #252525;
}
.sbe-post-form__action:only-child {
    margin-right: inherit;
    color: #fff;
}

.sbe-actions-menu .sbe-actions-menu__wrapper {
    background-color: #252525;
}
.sbe-actions-menu__tether .x-list {
    background-color: #333;
}
.sbe-actions-menu__tether::after {
	visibility: hidden;
}
.sbe-post-cell--audio:hover .post-container, .sbe-post-cell--file:hover .post-container, .sbe-post-cell--image:hover .post-container, .sbe-post-cell--video:hover .post-container {
    background-color: #333333;
}
h1, input, p {
    margin: 0;
    color: #fff;
}
.sbe-post-cell--audio+li, .sbe-post-cell--file+li, .sbe-post-cell--image+li, .sbe-post-cell--video+li {
    border-top: 1px solid #252525;
}
.sbe-post-cell--audio, .sbe-post-cell--file, .sbe-post-cell--image, .sbe-post-cell--video {
    margin-top: 1rem;
    border-top: 1px solid #252525;
}
.sbe-post-cell--comment .post__content, .sbe-post-cell--grade .post__content {
    flex-basis: 0%;
    background-color: #333;
}
.sbe-post-cell--comment .tail, .sbe-post-cell--grade .tail, .sbe-post-cell--voice-note .tail {
    stroke: transparent;
    fill: #333333;
}
.sbe-post-cell--comment.is-shared .post__content, .sbe-post-cell--grade.is-shared .post__content, .sbe-post-cell--voice-note.is-shared .post__content {
    background-color: #444444;
}
.sbe-post-cell--comment.is-shared .tail, .sbe-post-cell--grade.is-shared .tail, .sbe-post-cell--voice-note.is-shared .tail {
    stroke: transparent;
    fill: #444444;
}
.sbe-post-form__comments .comments__input {
    background-color: #333;
}
.sbe-post-form__comments::before {
	visibility: hidden;
}
.panel__master--assignments .panelitem.active article, .panel__master--classes .panelitem.active article, .panel__master--folders .panelitem.active article {
    background: #515151;
}
.panel__master--assignments .panelitem.active, .panel__master--classes .panelitem.active, .panel__master--folders .panelitem.active {
    background: #515151;
    box-shadow: inset 0 1px 0 0 #515151;
}
.ui__back-link>a>svg {
    fill: #ffffff;
}
.panel__master {
    background: #252525;
}
.sbe-actions-menu .sbe-actions-menu__wrapper>svg {
    fill: #ffffff;
}
.ui-help__toggle>svg {
    fill: #333;
}
.panel__detail-content .panel__scrollable {
    background-color: #252525;
}

.panel__detail {
    background-size: auto;
    background-color: #252525;
}
.x-uploader-filename-input-wrapper .x-uploader-filename-input {
    color: #333;
}
}
