css:
@-moz-document domain("www.gamezer.com") {
#auth_form {
    width: 100%;
    max-width: 400px;
    min-width: 320px;
    min-height: 580px;
    margin-top: 0px;
    padding: 0px 0px;
}

#auth {
    top: 0;
    right: 0;
    min-height: 702px;
    max-height: 2000px;
    background-image: url(https://d.top4top.net/p_820sq2ml1.png);
    background-size: cover;
}

#auth_logo {
    opacity: 0.9;
}
#auth_geo_content {
    color: #909090;
}

body {
    background-color: #07182b;
    background-image: url(https://e.top4top.net/p_820vjiaw2.png);
}

.size2 .centerBlock {
    background-color: rgba(60, 40, 171, 0);
}
#BLAZE_PreloaderBase {
    background-color: rgb(1, 12, 25);
}
#BLAZE_PreloaderBar {
    background-color: #dedede;
}
.standby_overlay {
    background-color: #07182b;
    background-image: url(https://e.top4top.net/p_820vjiaw2.png);

}

.woody {
    background-color: rgba(25, 25, 25,0);
    background-size: 0px 0px;
    box-shadow: rgba(110, 110, 110, 0) 1px 1px 1px, rgba(110, 110, 110, 0) 1px 1px 1px inset;
    border-width: 0px;
}

#sign {
    color: #ffffff;
    background-color: rgba(9, 9, 19, 0.8);
    border: 1px solid #454586;
}
#sign_login {
    background-color: rgba(10, 9, 19, 0);
}
.switched #sign_login {
    background-color: rgba(84, 85, 86, 0.58);
    border: 1px solid #616161;
}
#sign_reg {
    background-color: rgba(84, 85, 86, 0.58);
    border: 1px solid #616161;
}
.switched #sign_reg {
    background-color: rgba(16, 16, 50, 0);
	border: none;
}


.switched #sign_login .sign_button_text, #sign_reg .sign_button_text {
    color: #fff;
}

#sign_stats {
    color: #fff;
}
#sign_login .sign_button_text,
.switched #sign_reg .sign_button_text {
    color: #fff;
}

.authform .input {
    color: #f7f7f7;
    border: 1px solid #345680;
}
.input_block.authform div {
    color: #797979;
}
#login_access_code_text {
    color: #353535;
    background-color: #eaeaea;
}
#sign a {
    color: #cacaca;
    background-color: rgba(16, 29, 88, 0.76);
    border: 1px solid #4251ab;
}
.desktop #sign a:hover {
    color: #f5f5f5;
    background-color: rgba(21, 38, 117, 0.76);
    border: 1px solid #6575d4;
}
input[type=checkbox] + label.signlabel {
    border: 1px solid #999;
    background-color: #dadada;
}
.button.ui,
.button_block.ui {
    background-color: rgba(44, 74, 109, 0.7);
}
.desktop .button.ui:hover,
.desktop .button_block.ui:hover {
    background-color: rgba(53, 91, 134, 0.7);
}

#sign_footer_info {
    color: #828282;
}
#sign_footer_info a {
    color: #828282;
}
.desktop #sign_footer_info a:hover {
    color: #bdbdbd;
}
#sign_standby .standby_container {
    border-color: #f7f7f7;
}
#sign_standby .standby {
    border-top: 4px solid #f7f7f7;
}
body.desktop::-webkit-scrollbar-thumb {
    background-color: #565656;
}
body.desktop::-webkit-scrollbar-track {
    background-color: #171717;
}
body.desktop::-webkit-scrollbar-track-piece {
    background-color: #171717;
}
#HeaderLeft, #HeaderRight {
    color: #dbecfd;
}
/*=========<CenterBlock>==========*/
.DashIcon {
    width: 100px;
    height: 100px;
    background-size: 400px 500px;
    box-shadow: rgba(0, 0, 0, 0.5) 0px 0px 0px;
    background-image: url(https://b.top4top.net/p_818ybtc64.png);
    margin: 5px auto 20px;
    border-radius: 0%;
    background-repeat: no-repeat;
}
.DashItemBlock {
    color: #e0dfa2;
}
.centerBlock {
    background-size: cover;
    background-image: url(https://b.top4top.net/p_820wnon95.png);
    border-radius: 6px;
    background-position: center center;
    background-repeat: no-repeat;
}
.size3 .centerBlock {
    background-color: rgba(9, 23, 39,0);
}
.gamelist #DashSection_Background {
    opacity: 0.2;
}
#Block_FHeader {
    background-color: rgb(18, 52, 84);
}
.desktop .scroller::-webkit-scrollbar {
    display: none;
}

/*---<StartGame>---*/

.Dash_StartBlock_Title.setup {
    border-radius: 5px;
    color: #9aadbf;
    background-color: rgb(18, 52, 84);
}
.InvitationGame {
    color: #e2e9f3;
    background-color: rgb(25, 50, 76);
    border: 1px solid #264b71 !important;
}
.InvitationGame:hover {
    background-color: rgb(33, 65, 99);
    border: 1px solid #316192 !important;
}
.Dash_StartBlock_SinglePlayer,
.multiplayer .Dash_StartBlock_Multiplayer {
    background-color: rgba(33, 65, 99, 0.4);
    border: solid 1px #5e7a96;
}
.SButton {
    color: #e8f3ff;
    border: solid 1px #27405a;
    background: rgb(9, 35, 62);
    background: -webkit-linear-gradient(rgb(17, 52, 94), rgb(9, 35, 62), rgb(13, 46, 86));
    background: -o-linear-gradient(rgb(17, 52, 94), rgb(9, 35, 62), rgb(13, 46, 86));
    background: -moz-linear-gradient(rgb(17, 52, 94), rgb(9, 35, 62), rgb(13, 46, 86));
    background: linear-gradient(rgb(17, 52, 94), rgb(9, 35, 62), rgb(13, 46, 86));
}
.SButton:hover {
    color: #fff;
    border: solid 1px #385b7f;
}
.BotOption div {
    opacity: 0.8;
}
.DashSwitchBlockGummy {
    background-color: rgba(33, 65, 99, 0.4);
    border: solid 1px #27405a;
}
/*---<Settings>---*/
.GameIcon.billiards {
    height:45px;
    width:45px;
    background-position: -28px -427px;
    border-radius:80%;
    position: relative;
   	left: -5px;
    top: 3px;
    opacity: 0.8;
}
.GameIcon.chess {
    height:45px;
    width:45px;
    background-position: -128px -427px;
    border-radius:80%;
    position: relative;
   	left: -5px;
    top: 3px;
    opacity: 0.8;
}
.GameIcon.checkers {
    height:45px;
    width:45px;
    background-position: -227px -427px;
    border-radius:80%;
    position: relative;
   	left: -5px;
    top: 3px;
    opacity: 0.8;
}
.DashSect {
    background-color: rgba(17, 42, 72, 0);
    border: 1px solid rgba(72, 101, 136, 0);
    box-shadow: 0px 2px 10px rgba(66, 66, 66, 0.4);
    -moz-box-shadow: 0px 2px 10px rgba(66, 66, 66, 0.4);
    -webkit-box-shadow: 0px 2px 10px rgba(66, 66, 66, 0);
}
select {
    color: rgb(255, 255, 255);
    background-color: rgba(17, 42, 72, 0.9);
    border-color: rgb(72, 101, 136);
}
.DashSect .border {
    border: 1px solid #4c6488;
}
.Dash_StartBlock_Setup select {
    color: #adadad;
}
.Dash_StartBlock_Setup option {
    color: #a9a9a9;
    background-color: #0b2844;
}
select option {
    color: #a9a9a9;
    background-color: #0b2844;
}
.SettingsRow select {
    background-color: rgb(22,22,22);
    color: #b5b5b5;
    background-image: url(https://f.top4top.net/p_818lxe211.png);
    border-color: rgb(22,22,22);
}

.SettingsButton {
    background-color: #2c649a;
}
.desktop .SettingsButton:hover {
    background-color: #447fb9;
}

/*---<History>---*/

.HistoryItemContainer {
    border: 1px solid #002346;
    background-color: rgba(0, 14, 29, 0.46);
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.4);
    -moz-box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.4);
    -webkit-box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.4);
}
.HS_GameName {
    color: #c8e2fd;
}
.HS_GameName b {
    color: #0087ff;
}
/*---<RecordedGames>---*/

.input {
    color: #cccccc;
    background-color: rgba(32, 65, 97, 0.43);
}
/*---<Forum>---*/
.ForumSection, .ForumTopic, .ForumPost {
    border: 1px solid #013365;
    background-color: rgba(0, 14, 29, 0.6);
}
.ForumInput_Title, .ForumInput_Post {
    background-color: rgba(13, 56, 101, 0.58);;
}
/*---<Inventory>---*/

.InventoryItemContainer {
    border: 1px solid #002346;
    background-color: rgba(0, 14, 29, 0.46);
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.4);
    -moz-box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.4);
    -webkit-box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.4);
}
.IVContent b {
    color: #017bef;
}
.IVExists {
    left: -1px;
    bottom: -1px;
    background-color: rgb(22, 22, 22);
}
.IVAct {
    background-color: rgba(15, 28, 88, 0.45);
}
.IVConfirmTitle {
    color: #82a4ff;
}
.IVConfirmButton,
.IVCloseButton {
    background-color: #1d3c8e;
}
.IVConfirmButton {
    color: #28f540;;
}
.IVCloseButton {
    color: #d0d0d0;
}
/*---<Store>---*/

.StoreItemBlock {
    border: 1px solid #002346;
    background-color: rgba(0, 14, 29, 0.6);
}
.StoreSummary {
    color: #67b17e;
}
.StoreOrderNum {
    color: #dcdcdc;
}
.order_warning {
    color: #daa458;
}
.StoreOrder .StoreItemBlock {
    color: #e8f3ff;
}
.StoreItem.Option .StoreItemBlock {
    color: #99c6ff;
}
.StoreItemBlockCompact {
    color: #99c6ff;
    border: 1px solid #002346;
    background-color: rgba(0, 14, 29, 0.6);
}
.clabel {
    background-color: rgba(255, 255, 255, 0.8);
    border-color: rgb(0, 54, 121) !important;
}
.clabel:hover {
    background-color: rgb(227, 234, 241);
    border-color: rgb(0, 114, 255) !important;
}


.StoreOrderGift .input {
    border: 1px solid #0c3e6f;
    color: #9dcefd;
    background-color: rgba(16, 66, 119, 0.24);
}
.StorePayIcon {
    width: 100px;
    height: 60px;
    position: absolute;
    background-size: 300px 180px;
    background-image: url(https://f.top4top.net/p_8181m27w8.png);
    background-repeat: no-repeat;
}
.DashStoreRedirect, .DashStoreGMPay {
    background-color: rgba(0, 59, 136, 0.2);

}

#GMOrder_Confirm .input {
    border: 1px solid #1f1f1f;
    background-color: rgba(0, 0, 0, 0.19);
}

.CancelOrderButton {
    background-color: #1b4771 !important;
}

.SubmitOrderButton {
    background-color: #225029 !important;
}
.SubmitOrderInfo {
    color: #cacaca;
}
.button {
    color: #c7c7c7;
}
/*---<Help>---*/

#Dash_Section.help {
    background-color: rgba(255, 255, 255, 0.7);
}
.DashSectionHelp .standby_container {
    border: 2px solid #005bb1;
}
.DashSectionHelp .standby {
    border-top: 4px solid #005bb1;
}
/*=========<leftBlock>==========*/
/*<mainManu>*/
#RoomMenuContainer {
    border-radius: 5px;
    color: #ffffff;
    background-color: #123454;
}
.size3 #RoomMenuContainer {
    background-color: #123454;
}
.desktop #RoomMenuContainer:hover {
    background-color: #1a4c7b;
}
.Switch_Room #RoomMenuContainer {
    color: #ffffff;
    background-color: #123454;
}
.desktop .Switch_Room #RoomMenuContainer:hover {
    background-color: #1c4b77;
}
.OpenList #RoomMenuContainer {
    color: #ffffff;
    background-color: #123454 !important;
}
.OpenList #RoomMenuContainer:hover {
    background-color: #1a4c7b !important;
}
.leftBlock {
    border-radius: 0px;
    background-color: rgb(3, 19, 35);
}
.RoomCategoryItem {
    background-color: #0c1f31;
    border-radius: 3px;
    margin-bottom: 3px;
}
.desktop .RoomCategoryItem:hover {
    background-color: #15304a !important;
    border-radius: 3px;
}
.desktop .list_scroller::-webkit-scrollbar-track-piece {
    background-color: #000b17;
}
.desktop .list_scroller::-webkit-scrollbar-thumb {
    background-color: #0c223a;
}
.desktop .list_scroller::-webkit-scrollbar {
    background-color: #000b17;
}
/*<SubManu>*/
#CategoryBack {
    background-color: #1d4d7b;
    border-radius: 5px 0px 0px 5px;
}
#CategoryBack:hover {
    background-color: #2d76bb !important;
    border-radius: 5px 0px 0px 5px;
}
.RoomItem {
    border-radius: 3px;
    background-color: #0c1f31;
}
.desktop .RoomItem:hover {
    background-color: #15304a;
    box-shadow: 0px 0px 2px rgba(72, 100, 127, 0.5);
}
.RoomItem_users {
    border-radius: 100px;
    color: #c1dcf5;
    text-shadow: 0px 1px 2px rgb(0, 25, 49);
    background-color: #0b2944;
    border: solid 1px rgb(50, 76, 101);
    box-shadow: 0px 0px 0px rgb(8, 26, 43);
}
.RoomItem_icon {
    opacity: 0.7;
}
/*<UsersList>*/

#RoomQuit {
    background-color: #bf404c;
}
#RoomQuit:hover {
    background-color: #da4452 !important;
}
#RoomUserNum {
    width: 39px;
    height: 39px;
    color: #6ca0d2;
    background-color: #03192d;
    border-radius: 40px;
    font-size: 15px;
}
.UserItem {
    background-image: url(https://f.top4top.net/p_820t05hd3.png);
    background-color: #091f35;
    border-radius: 0px;
    margin-bottom: 3px;
}
.size3 .leftBlock {
    border-radius: 0px;
    background-color: rgb(22, 22, 22);
    background-image: url(https://f.top4top.net/p_820t05hd3.png);
}
.UserBG {
    border-radius: 0px;
    background-color: #0c253e;
    background-blend-mode: luminosity;
    border: solid 1px rgba(0, 0, 0, 0.5);
}
.UserAvatar {
    background-color: #132a3e;
}
.UserAvatarContainer {
    border-radius: 2px;
}
.UserGroup,.UserNick {
    float:left;
    position:relative;
    left: 85px;
}
.UserNick {
    width:180px;
    overflow:hidden;
}
.UserNick:before{
    content: url(https://a.top4top.net/p_818c760n9.png);
}
.UserFlag {
    z-index: 1;
}
.UserName {
    top: 30px;
    left: 92px;
}
.UserScore {
    color: #4ca76a;
    font-weight: 900;
}
.UserItemSB {
	background-image: url(https://d.top4top.net/p_818at0vz6.gif);
    border-radius: 0px;
}
.UserActSB {
    width:27px;
    height:27px;
    transition-property: transform;
    transition-duration: 0.1s;
     animation-name: rotate; 
    animation-duration: 2s; 
    animation-iteration-count: infinite;
    animation-timing-function: linear;
 }
@keyframes rotate {
    from {transform: rotateZ(0deg);}
    to {transform: rotateZ(360deg);}
}

/*=========<RightBlock>==========*/

.rightBlock {
    border-radius: 0px;
    background-color: #143250;
}
#TabContainer {
    background-color: #00182f;
    border-radius: 0px;
}
#PanelRight {
    -moz-box-shadow: 0px 0px 0px 0px #d4ccff, 0px 0px 0px rgba(0, 0, 0, 0.5);
    -webkit-box-shadow: 0px 0px 0px 0px #d4ccff, 0px 0px 0px rgba(0, 0, 0, 0.5);
}
.Tab_Home #MenuTab_Home,
.Tab_Chat #MenuTab_Chat,
.Tab_Profile #MenuTab_Profile,
.Tab_Settings #MenuTab_Settings {
    background-color: #143250 !important;
}
/*---<ProfileBG>---*/

.Profile_BGImage {
    background-color: #0c253e;
    background-blend-mode: luminosity;
}
.Profile_BGInfo {
    background-color: #0c253e;
}
/*---<Profile>---*/

#ProfileContent .SectionArea {
    background-color: #0c253e;
    background-blend-mode: luminosity;
}
.Profile_MottoInput input {
    background-color: rgba(36, 21, 78, 0);
}
.Profile_StatusHighlighter {
    background-color: #434246;
}
.desktop .sec_scroller::-webkit-scrollbar {
    display: none;
}
.bg_select {
    color: #d4d4d4;
    background-color: rgb(3, 22, 41);
    background-image: url(https://f.top4top.net/p_818lxe211.png);
}
.bg_select option {
    background-color: rgb(3, 22, 41);
}
.Profile_ZPGroup {
    background-color: #1d1533;
    border-left: 4px solid #1a1529;
    border-right: 4px solid #1a1529;
}
.Profile_ZPValue {
    color: #a49fb1;
}
.Profile_ZPContent {
    color: #a49fb1;
}
.Profile_PremiumGroup {
    color: #eed4ff;
    background-color: #4d1b6d;
}
.Profile_PremiumContent,
.Profile_ZPContent {
    color: #eed4ff;
}
.Info_StatsLeft {
    background-color: #1f7334;
    border-left: 4px solid #3c6f3a;
    border-right: 2px solid #3c6f3a;
}
.Info_StatsRight {
    background-color: #de9624;
    border-right: 4px solid #e69c26;
    border-left: 2px solid #e69c26;
}
/*---<Options>---*/

.SectionArea {
    background-color: #00182f;
}
.SettingsCheckboxRow {
    color: #daecfd;
}
.SettingsRow select {
    color: #d4d4d4;
    background-color: rgb(3, 22, 41);
    background-image: url(https://f.top4top.net/p_818lxe211.png);
    border-color: rgb(24, 59, 93);
}
.SettingsRow option {
    color: #d4d4d4;
    background-color: rgb(3, 22, 41);
}
/*---<Chat>---*/

#RoomChatUpperLine {
    height: 4px;
    background-color: #143250;
}
#Block_Chat {
    background-color: #0b253e;
    background-image: url(https://f.top4top.net/p_820t05hd3.png);
    background-repeat: repeat;
    border-radius: 0px;
}
.chat_ava {
    background-color: #0a2846;
}
.room_chat_item,
.game_chat_item {
    background-color: rgb(5, 22, 39);
}
.chat_content {
    color: #e8e8e8;
}
.room_chat_item.inprivate,
.game_chat_item.inprivate {
    background-color: rgba(20, 75, 132, 0.25);
}
.room_chat_item.inprivate .chat_content,
.game_chat_item.inprivate .chat_content {
    color: #caeeff;
}
.room_chat_item.outprivate,
.game_chat_item.outprivate {
    background-color: rgba(185, 25, 79, 0.25);
}
.room_chat_item.outprivate .chat_content,
.game_chat_item.outprivate .chat_content {
    color: #fbcdf9;
}
.desktop .chat_scroller::-webkit-scrollbar-thumb {
    background-color: #4e4e4e;
}
.desktop .chat_scroller::-webkit-scrollbar-track {
    background-color: rgba(37, 37, 37, 0.5);
}
#RoomChatControls {
    background: #101023;
    background: -webkit-linear-gradient(left, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: -o-linear-gradient(right, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: -moz-linear-gradient(right, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: linear-gradient(to right, rgb(7, 29, 51), rgb(34, 78, 119), rgb(47, 60, 72));
}
#RoomChatPanel {
    background: #101023;
    background: -webkit-linear-gradient(left, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: -o-linear-gradient(right, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: -moz-linear-gradient(right, rgb(0, 0, 0), rgb(19, 19, 19), rgb(23, 23, 23));
    background: linear-gradient(to right, rgb(7, 29, 51), rgb(34, 78, 119), rgb(47, 60, 72));
    -webkit-transform: translate3d(0, 0, 0);
    box-shadow: 0px -1px 2px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: 0px -1px 2px rgba(0, 0, 0, 0.2);
    -webkit-box-shadow: 0px -1px 2px rgba(0, 0, 0, 0.2);
}
#RoomChatInput {
    background-color: rgba(20, 14, 25, 0);
    border-top: 1px solid #0b2844;
    border-radius: 0px;
}
#RoomChatButton {
    background-color: rgba(50, 34, 94, 0);
}
#RoomPMInput, #RoomVMInput {
    color: #fffde7;
    background-color: rgba(11,11,11, 0) !important;
}
.RoomChatExtras {
    background-color: rgba(14, 44, 76, 0.93);
    border: 1px solid #245382;
}
.PM_Post {
    border-top: 1px solid #2f5f92;
}

.PrivateMessage {
    background-color: rgba(14, 44, 76, 0.93);
    border: 1px solid #245382;
    box-shadow: 0px 2px 5px rgba(29, 78, 134, 0.75);
    -moz-box-shadow: 0px 2px 5px rgba(29, 78, 134, 0.75);
    -webkit-box-shadow: 0px 2px 5px rgba(29, 78, 134, 0.75);
}
#RoomPMInput,
#RoomVMInput {
    color: #fffde7;
    background-color: rgba(33,33,33, 0) !important;
}
.PM_Button {
    background-color: rgba(33,33,33,0);
}
.VideoMessage_Header {
    color: #ababab;
}
#videoplayer_id {
    color: #cacaca;
    border-bottom: 1px solid #cacaca;
}
#videoplayer_header {
    color: #a0a0a0;
}
.VideoPlayerContainer {
    background-color: rgba(14, 44, 76, 0.93);
}
input {
    background-color: rgba(00,00,00,0);
}
.ChatExtra_Video .PM_Button,
.ChatExtra_Invitation .PM_Button,
