css:
@-moz-document domain("www.resetera.com") {



/* Fonts */

body {
font-size: /*[[fntsize]]*/rem !important;
font-family: '/*[[fntfamily]]*/', 'Courier New','Source Code Pro', 'Consolas', 'Arial', 'Helvetica Condensed Black', 'Helvetica Neue Condensed','Helvetica','Arial', sans-serif !important;
}

.itemPageNav a, .itemPageNav span, .discussionListItem .secondRow, .discussionListItem .title, #pageDescription, .navTabs, .node .nodeLastPost, .idstats dl, .discussionListItem .lastPostInfo .dateTime, .PageNav, .sectionFooter, .pageNavLinkGroup, .node .nodeDescription, .node .subForumList li .nodeTitle, .messageText, .messageUserBlock .userBanner, .message .postDate, .message .editDate, .bbCodeBlock .type, .bbCodeQuote .quoteContainer, .xenTooltip, .bbCodeQuote .quoteContainer .quoteExpand, .footer .pageContent, .breadcrumb, .pageNavHeader, .PageNav, .pageNavHeader > span
{
font-size: inherit !important;
font-family: inherit !important;;
}




/* Minimal */

#headerProxy, .nodeList.sectionMain,.footerLinks, #navigation .pageContent, #searchBar, .nodeListNewDiscussionButton
{
display: none !important;
}

#logo
{
display: none !important;
}


.listBlock.stats.pairsJustified, .major, .minor, .lastPost, .postDate, #content > div > div > div.discussionList.section.sectionMain > form.DiscussionList.InlineModForm > dl, #pageDescription, .messageMeta.ToggleTriggerAnchor, .userBanner.bannerHidden.wrapped, .sectionFooter.InlineMod.SelectionCountContainer, #DiscussionListOptionsHandle, .threadListSeparator, .fa.fa-circle, .fa.fa-gamepad, .fa.fa-comments, .fa.fa-arrows-alt, span.video, span.videob, span.startDate, .posterDate > a:first-child, span.itemPageNav span:first-child, .linkGroup > span, .fa.fa-arrow-down, #navigation, .samCodeUnit.aboveTopBreadcrumb
{
display: none !important;
}

.discussionListItem.visible.sticky, #content > div > div > div:nth-child(5) > div:nth-child(1) > label > a
{
display: none !important;
}



div.titleText h3.title > a:nth-child(2)
{
display: none !important;
}

.breadcrumb
{
position: relative !important;
left: -.3rem !important;
}

.titleBar {
display: none !important;
}

div.titleText
{
    padding-left: 0px !important;
}

.secondRow, .titleText h3 {
display: inline-block !important;
vertical-align: top !important;
}

body .itemPageNav a
{
    padding-bottom: 0px !important;
}

.videob
{
    padding-left: 0px !important;
}

.secondRow
{
    line-height: 24px !important;
    padding-left: .6em !important;
}

.discussionList.section.sectionMain
{
    margin-top: 20px !important;
}

.titleBar
{
    margin-bottom: 0px !important;
}

div.titleText h3.title > a:first-child::after
{
content: " -";
padding-left:.6em !important;
}


ol.discussionListItems > li
{
margin-bottom: 0.7rem !important;
border-bottom: 0px solid rgba(0,200,0,1) !important;
}

.messageList .message {
  border-bottom: 2px dashed rgba(0,200,0,1) !important;  
}

.pageWidth
{
width: 65% !important;
}

.titleText
{
display: flex !important;
}

.itemPageNav a, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, .node .nodeLastPost
{
border-style: none !important;
}

.section
{
    margin: 0px !important;
}


#content > div > div > div:nth-child(5) > div.PageNav > nav > a.text:nth-child(7)
{
    position: relative;
    left: 15px;
}

#content > div > div > div:nth-child(5) > div.PageNav > nav > span > span > a
{

    margin: 0 5px 0 0;
}

#content > div > div > div:nth-child(5) > div.PageNav > nav > span

{
    width: 160px ;
}

#content > div > div > div:nth-child(5) > div.PageNav > nav > a:nth-child(5)

{
    width: 40px ;
}

#content > div > div > div:nth-child(5) > div.PageNav > nav > span > span > a, #content > div > div > div:nth-child(5) > div.PageNav > nav > a:nth-child(6)

{
    width: 30px ;
}


#content > div > div > div:nth-child(5) > div.PageNav > nav > span > span

{
    width: 200px ;
}


#content > div > div > div:nth-child(7) > div.PageNav > nav > span
{
    width: 200px ;
}


#content > div > div > div:nth-child(7) > div.PageNav > nav > span > span > a, #content > div > div > div:nth-child(7) > div.PageNav > nav > a.currentPage
{
    width: 30px ;
}

#content > div > div > div:nth-child(5) > div.linkGroup.SelectionCountContainer > a:nth-child(1)
{
    display: none;
}

.bbCodeBlock.bbCodeQuote > aside, .bbCodeBlock.bbCodeQuote
{
    display: block;
    width: /*[[quoteblockwidth]]*/vw;
}


.PageNav a {
    width: 30px ;
}

#content > div > div > div:nth-child(4) > div.PageNav > nav > span {
width: 180px;
}

.PageNav .pageNavHeader {
margin-left: 5px;
    margin-right: 20px;
}







/* Colors */

body, #content .pageContent, .titleBar h1, .messageText, .bbCodeQuote .attribution, .sectionFooter, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, .pairsRows, .pairsColumns, .pairsJustified, .pageNavLinkGroup, .pageNavHeader, .PageNav
{   
    color: #8da !important;
}

html, #header, .discussionListItem, .discussionListItem .posterAvatar, .discussionListItem .stats, #content .pageContent, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, .navTabs, .messageList .message, .messageUserInfo, .node .nodeLastPost, .sectionFooter, .PageNav a.currentPage, body .itemPageNav a, .breadcrumb, .threadListSeparator, .discussionList .sectionHeaders, .secondaryContent, .nodeList .categoryStrip, .sidebar .section .primaryContent   h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .footer .pageContent, #DiscussionListOptionsHandle a, .sidebar
{
   background-color: transparent !important;
    /*
    background-color: rgb(15,15,15) !important;
*/
}

.bbCodeQuote, .bbCodeQuote .attribution, .bbCodeQuote .quoteContainer, .bbCodeBlock, .bbCodeBlock .type, .bbCodeBlock pre
{
	background-color: rgb(0,55,00) !important;
}

a[href]:hover, a:link, a:visited, .PageNavNext, .PageNavPrev  {   
    color: #99dd13 !important;
    font-weight: bold !important;
}

a[href]:hover, a[href]:focus
{   
    color: #000 !important;
    background-color: rgb(0,200,0) !important;
   } 
    
.discussionListItem {
    border-right-color: rgb(15,15,15) !important;
    border-bottom-color: rgb(15,15,15) !important;
    border-left-color: rgb(15,15,15) !important;
}

.PageNav a
{
border: 0px solid rgb(100,100,100) !important;
    background-color: transparent;
    border-radius: 0px;
}

.PageNav a.currentPage {
color: #000  !important;
background-color: rgba(0,200,0,1) !important;
}

.itemPageNav a, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, .node .nodeLastPost
{
border-style: none !important;
}

.section
{
    margin: 0px !important;
}

#messageList > li > div.messageUserInfo > div > div > a > img
{
-webkit-filter: grayscale(100%)  sepia(100%) hue-rotate(50deg) saturate(200%)  contrast(100%) brightness(50%);
filter: grayscale(100%)  sepia(100%) hue-rotate(50deg) saturate(300%) brightness(55%) contrast(220%) ;
}

.messageText img
{
-webkit-filter: grayscale(100%)  sepia(100%) hue-rotate(50deg) saturate(100%) contrast(150%);
filter: grayscale(100%)  sepia(100%) hue-rotate(50deg) saturate(100%) contrast(150%);
}


/*

-webkit-filter: grayscale(100%)  sepia(100%) hue-rotate(50deg) saturate(200%)  contrast(100%) brightness(50%);
filter: grayscale(100%)  sepia(100%) hue-rotate(50deg) saturate(300%) brightness(55%) contrast(220%) ;

*/


/*
body {
background: linear-gradient(#010 50%, #000 50%);
  background-size: 100% 8px;
  background-repeat: repeat-y;
  opacity: 1;
}
*/
body {
background-color: #000;
}


body:after
{
    content:"";
     display: block;
    height: 100%;
    min-height: 100%;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
        z-index: 100000;
background: linear-gradient(rgba(0, 100, 0, .1) 70%, rgba(0, 0, 0, .8) 80%);
  background-size: 100% 4px;
  background-repeat: repeat-y;
  opacity: 1;
pointer-events: none;
}


#content > div > div > div.discussionList.section.sectionMain > form.DiscussionList.InlineModForm > ol > li > div.listBlock.main > div  a,

a[href]:hover, a:link, a:visited, .pageNavHeader, .PageNav a, .currentPage {
text-shadow: 0 0 1px rgba(100,255,100,.2) , 0 0 2px rgba(0,255,0,.3) , 0 0 3px rgba(0,255,0,.3) , 0 0 4px rgba(0,255,0,.4) , 0 0 7px rgba(0,255,0,.5) , 0 0 8px rgba(0,0,0,.2) , 0 0 10px rgba(0,0,0,1) ;
}

.messageText {
text-shadow: 0 0 1px rgba(100,255,100,.2) , 0 0 2px rgba(0,255,0,.3) , 0 0 3px rgba(0,255,0,.3) , 0 0 4px rgba(0,255,0,.4) , 0 0 7px rgba(0,255,0,.5) , 0 0 8px rgba(0,0,0,.2) , 0 0 10px rgba(0,0,0,1) ;
}



.userBanner.bannerSilver {
background-color: rgba(0,255,0,.4);
    border: none;
}

.pageWidth {
    width: /*[[pagewidth]]*/% !important;
    margin: 0 0% !important;
}

.pageNavHeader  {
font-weight: 700;
}

.bbCodeQuote .quoteContainer .quoteExpand {
    color: rgba(0, 200, 0, 1);
background-image: linear-gradient(rgba(0, 100, 0, 0) 0%, rgb(0, 100, 0) 80%);
}

.bbCodeQuote {

border: 1px groove rgb(0,200,0);
}

.PageNav a:hover, .PageNav a:focus {
    color: rgb(0,0,0) !important;
    text-decoration: none;
    background-color: rgb(0,200,0) !important;
    border-color: rgb(0,200,0);
}


}
