css:
@-moz-document url-prefix("https://workflowy.com") {
body,
.page {
    background-color: #eaeae5;
    border: none;
}
#documentView {
    font-size: 18px;
}
.selected .project>.name>.content {
    line-height: 25px;
}
.selected>.children>.project .project>.name>.bullet {
    margin-top: 6px;
}
.selected>.children>.project>.name>.bullet {
    margin-top: 7px;
}
.project.task>.name>.bullet,
.project.open>.name>.bullet,
.noHalo .bullet {
    opacity: .2;
    background-color: transparent;
}
.bullet,
#bulletBucket .bulletBucketBullet {
    opacity: .6;
    background-color: transparent;
}
.content .contentTag,
.nameAnimated .contentTag {
    color: rgba(91, 157, 196, 1);
}
.content .contentMatch {
    background-color: inherit;
    font-weight: bolder;
}
.noted>.notes>.content {
    font-size: 12px;
    color: #666666d1;
}
.children {
    border-left: 1px solid #d6d6cf;
}
}
