css:
@-moz-document domain("gwern.net") {
article {
	overflow: auto;
	padding: 0 0 0 8px;
}
#markdownBody {
	max-width: 38em;
	-moz-hyphens: auto;
	hyphens: auto;
	text-align: justify;
	font-size: 1.25rem;
	overflow: hidden;
}
#markdownBody blockquote {
	margin: 20px 30px 22px 30px;
	padding: 10px;
	border: 1px solid #ccc;
}
#markdownBody blockquote q {
	background-color: inherit;
}
div#TOC {
	min-width: 120px;
	position: relative;
	margin: 0 30px 0 0;
	padding: 30px 8px 8px 9px;
}
div#TOC::before {
	content: "Contents";
	display: block;
	position: absolute;
	top: 8px;
	left: 8px;
	width: calc(100% - 2 * 8px);
	font-size: 1.1em;
	font-weight: bold;
	color: #777;
	border-bottom: 1px solid #ccc;
	padding-bottom: 1px;
}
article > section {
	display: block;
	margin-left: 150px;
	margin-right: 150px;
}
article > section:last-of-type {
	margin-bottom: 75px;
}
article > section:nth-of-type(1),
article > section:nth-of-type(2) {
	float: left;
	max-width: 22em;
}
article > section:nth-of-type(1) {
	margin-right: 0;
}
article > section:nth-of-type(2) {
	margin-left: 25px;
}
article > section:nth-of-type(n+3) ul {
	padding-left: 0;
}
article > section:nth-of-type(n+3) > ul {
	columns: 21em auto;
	margin: 0.5em auto;
	padding: 0.5em 0.25em;
	border: 1px solid #eee;
}
article > section:nth-of-type(n+3) > ul > li {
	text-indent: -1.5em;
    padding-left: 1.5em;
}
article > section:nth-of-type(n+3) ul ul li {
	text-indent: 0;
	padding-left: 0;
}
article > section:nth-of-type(n+3) ul li {
	max-width: calc(22em - 40px);
	margin-left: 40px;
	page-break-inside: avoid;
}
article > p {
	margin-left: 150px;
	clear: both;
}
article > section:nth-of-type(n+3) h1 {
	text-align: center;
	margin: 1.5em 0 0.5em 0.6em;
    padding: 0;
}
article > section a:hover {
	color: #aaa;
}
figcaption {
	font-size: 0.9em;
	padding-left: 0.5em;
}
article p img {
	margin: 1.5em auto;
    border: 1px solid #000;
    width: calc(100% - 8px);
    height: auto;
    display: block;
}
figure img {
	border: 1px solid #000;
    box-sizing: border-box;
}
figure {
	margin: 1.5em auto;
	display: table;
}
article table {
	margin: 1.5em auto;
}
.sourceCode {
	font-size: 0.75em;
}
}
