css:
@-moz-document domain("byui.brightspace.com") {
iframe {
    /*box-shadow: 1px 1px 20px green;*/
    padding: 0px 10px;
    min-height: 900px !important;
    border-radius: 5px;
}
.d2l-navigation-s.d2l-navigation-s-has-branding.d2l-navigation-s-has-title {
    display: flex;
    align-items: flex-end;
    align-content: flex-end;
    height: 50px;
    box-shadow: 0 0 10px 0px;
    transition: all .5s;
    flex-flow: row wrap;
    /* overflow-y:hidden; */
}
.d2l-navigation-s.d2l-navigation-s-has-branding.d2l-navigation-s-has-title:hover {
    height: 150px;
    /* overflow-y:visible; */
}
body {
    overflow-y: scroll !important;
}
d2l-dropdown-menu {
    display: block;
    position: fixed;
    z-index: 9999;
    overflow: visible;
}
.vui-typography.d2l-typography.daylight.dbd_frame.d2l-flag-us91146-fetch-cache-token-localstorage.d2l-flag-daylight-dialog-scrolling {
    height: 50px !important;
}
.vui-typography.d2l-typography.daylight.dbd_frame.d2l-flag-us91146-fetch-cache-token-localstorage.d2l-flag-daylight-dialog-scrolling * {
    /*   height: 100%; */
}
.vui-typography.d2l-typography.daylight.dbd_frame.d2l-flag-us91146-fetch-cache-token-localstorage.d2l-flag-daylight-dialog-scrolling div.dco {
    max-height: none !important;
}
body>header {
    position: fixed;
    top: 0;
    z-index: 600;
}
body {
    padding-top: 60px !important;
}
.d2l-navigation-s-shadow-gradient {
    display:none;
}
}
