label.iconic > input + i::before {
    font: normal normal normal 14px/1 FontAwesome;
    font-size: inherit;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-size: 120%;
    height: .9em;
    color: #eb4747;
    vertical-align: -.1em;
    visibility: visible;
    text-align: left;
}

.overlay-title {
    font-family: 'Open sans condensed', 'Arial', sans-serif;
    font-weight: bold;
    background: #a02f2f url('https://realitygaming.fr/styles/RealityGaming/background/pattern4-ts.png') repeat-x bottom;
    color: white;
    text-shadow: 1px 1px rgba(0, 0, 0, 0.7);
}

.block-textHeader {
    margin: 3px 0;
        margin-top: 3px;
    padding: 0;
    font-weight: 400;
    font-size: 17px;
    color: #eb4747;
}

.blockLink:hover {
    background: #fdeded;
    text-decoration: inherit;
}

.memberTooltip-header {
    display: table;
    table-layout: fixed;
    width: 100%;
    padding: 6px;
    background: #ffe1e1;
    border-bottom: 1px solid #a1a1a1;
}

.message-cell.message-cell--user, .message-cell.message-cell--action {
    position: relative;
    background: #f5f5f5;
    border: 1px solid #b1252e;
    min-width: 0;
}

.message-userArrow::after {
    position: absolute;
    top: -9px;
    right: -10px;
    content: "";
    border: 9px solid transparent;
    border-left-width: 0;
    border-right-color: #b1252e;
}

.message-cell .js-quickEditTarget {
    background: white;
    box-shadow: 1px 1px 2px rgba(0,0,0,0.25);
    border: 1px solid #b1252e;
    
}

.input:focus, .input.is-focused {
    outline: 0;
    background: #fdeded;
}

.input {
    font-size: 15px;
    color: #141414;
    background: #fff6f6;
}

.pageNav-jump {
    display: inline-block;
    background: linear-gradient(0deg, #fdeded, #fafcfe);
    color: #b12525;
}

.pageNav-page {
    color: #b12525;
    font-size: 13px;
    white-space: nowrap;
}

.XenStickyBg {
    background: #fdeded;
}
  
