css:
@-moz-document domain("hashrefinery.com") {
body { background-color: #474848 !important }
a, a:visited { color: #3db5e5 !important }

/* Header */ 
.page .tabmenu-out {
    background-image: none !important;
	background-color: #D3E5FC;
    height: 26px !important;
    color: #000 !important;
    box-shadow: none !important;
}

/* Header elements */ 
.page .tabmenu-inner {
    font-size: 1em !important;
    padding: 5px !important;
}
.page .tabmenu-inner a {
    padding: 0 10px !important;
    color: #000;
}
.page .tabmenu-inner .selected {
    border: 3px solid #bcd6f7 !important;
    background-image: none !important;
    background: #bcd6f7;
}

/* Content */ 
.page .content-out { 
	padding: 26px 0px 0px 0px !important;
	color: #BDBDBD;
}
.page .content-inner { 
	background: #474848;
	padding: 8px !important; 
}
table {
    -webkit-border-horizontal-spacing: 0px !important; 
    -webkit-border-vertical-spacing: 0px !important; 
}

/* Motd */ 
.motd {
    background-color: #4e4e4e !important;
    padding: 4px !important;
	margin: 0 7px 6px 7px;
    border: none !important;
    border-radius: 0 !important;
    font-size: 13px !important;
	font-weight: normal !important;
}

/* Boxes */ 
.main-left-box {
    border: none !important; 
    border-radius: 0 !important; 
    background: #4e4e4e;
	margin: 0 6px;
}
.main-left-title {
    padding: 6px;
    font-size: 13px !important;
    background-color: #4e4e4e !important;
    font-weight: normal !important;
    border-bottom: 1px solid #a09f9f !important;
    color: #3DB5E5 !important;
}

/* Boxes tables */ 
table {
    -webkit-border-vertical-spacing: 0px !important;
    -webkit-border-horizontal-spacing: 0px !important;
}
table.dataGrid2 { color: #BDBDBD !important; }
table.dataGrid2 tbody { border: none !important; }
table.dataGrid2 th, table.dataGrid2 td { 
	padding: 0.2em !important;
	font-size: 12px;
}
table.dataGrid2 td { border-bottom: 1px solid #a09f9f !important }
.ssrow:hover { background-color: #696969 !important }

/* Boxes tables > Wallet */ 
input[type="checkbox"] {
    position: relative;
    margin: 0 !important;
    top: 3px;
}
table.dataGrid2 td > label {
	position: relative;
	top: -49px;
}

/* Graphics */ 
.jqplot-series-shadowCanvas, jqplot-series-shadowCanvas { background: #4e4e4e }

/* Box Search Wallet */
.main-text-input {
	border-radius: 1px !important;
    font-size: 12px !important;
    padding: 3px !important;
	margin-bottom: 8px;
}
.main-submit-button {
	border-radius: 1px !important;
    font-size: 12px !important;
    padding: 3px !important;
}

/* Box Last 50 Earnings */
span.block.immature {
    color: #d6c2b1 !important;
    background-color: #967648 !important;
}
span.block.exchange, span.block.confirmed {
    color: #b1d6b8 !important;
    background-color: #598659 !important;
}
}
