css:
@-moz-document url-prefix("https://realitygaming.fr/") {
.p-header,
html,
.p-pageWrapper {
    background: #101010;
}
.p-nav,
.menu-header,
.menu-content {
    background: #131313;
}
.p-nav-list .p-navEl.is-selected,
.p-sectionLinks,
.p-sectionLinks-list .p-navEl.is-menuOpen,
.p-nav-list .p-navEl.is-menuOpen,
.p-navgroup-link.is-menuOpen,
.menu-footer,
.menu-row.menu-row--clickable:hover {
    background: #222222;
}
a.p-navEl-link,
.p-nav-list .p-navEl,
.p-navEl-splitTrigger:after,
a,
.p-nav,
.menu[data-href="/conversations/popup"] h3,
.menu[data-href="/account/alerts-popup"] h3,
.p-title-value {
    color: grey;
}
a.p-navEl-link:hover,
a:hover {
    color: white;
}
.menu-content {
    border-top: 3px solid #9e9e9e;
}
.menu-arrow {
    border-bottom-color: #9e9e9e;
}
.p-sectionLinks {
    border-bottom: none;
}

.block-container {
    border-radius: 0px !important;
    border: 1px solid #d2d2d2 !important;
    box-shadow: 0 1px 2px rgba(255, 255, 255, 0.06);
}

.block-container {
    color: #e2e2e2;
}

.block-container {
    
    border: 3px solid #fff !important;
    border-radius: 5px;
}

.block-container {
    border-radius: 5px !important;
    border: 3px solid white !important;
    box-shadow: 0 1px 2px rgba(255, 255, 255, 0.06);
    background: #373737e6 !important;
}

h3.block-minorHeader {
    background: #2f78a0 url('https://realitygaming.fr/styles/RealityGaming/background/pattern4-ts.png') repeat-x bottom;
    border-bottom: 2.5px solid #fff !important;
    font-size: 13pt;
    font-family: 'Open sans condensed','Arial',sans-serif;
    color: #fcfcff;
    font-weight: bold;
    text-shadow: 1px 1px rgb(0, 0, 0);
    border-radius: 0px !important;
}

.avatar img:not(.cropImage) {
    text-indent: 100%;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal;
    display: block;
    border-radius: 50px;
    width: 100%;
    height: 100%;
    border: 2px solid white;
}

.username--style4 {
    color: #009dbf;
    font-weight: bold;
}

a {
    
    color: white !important;
    
}

a:hover {
    
    text-decoration: underline;
}

.username--style4::before {
    font-family: "FontAwesome";
    font-size: 90%;
    content: "\f0e3";
    color: #009dbf;
    padding-right: 3px;
    display: inline-block;
}

.menu-row.menu-row--highlighted {
    background: #333 !important;
    color: white;
}

.menu-linkRow.is-selected, .menu-linkRow:hover, .menu-linkRow:focus {
    color: #fff !important;
    text-decoration: none;
    background: #000;
    border-left-color: rgb(255, 255, 255);
}

.input::-moz-placeholder {
    color: rgb(0, 0, 0);
}

.input {
    font-size: 15px;
    color: #000;
    background: #eaeaeae6 !important;
    border-width: 1px;
    border-style: solid;
    border-top-color: #cbcbcb;
    border-right-color: #dfdfdf;
    border-bottom-color: #dfdfdf;
    border-left-color: #cbcbcb;
    border-radius: 4px;
    padding: 6px;
    display: block;
    width: 100%;
    line-height: 1.4;
    text-align: left;
    word-wrap: break-word;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
}

#siropuChatNotice {
    color: #c9c9c9 !important;
}

.siropuChatConversation.siropuChatUsers > li[data-conv-id].siropuChatActiveConversation {
    background: #f7f7f7;
    border-bottom: 2px solid #2f78a0;
}

#siropuChatEditor .fr-wrapper {
    background: #7c7a7a;
    border-width: 0;
    border-style: solid;
}

.fr-box.fr-basic .fr-element {
    color: #fff;
}

.fr-desktop .fr-command, .fr-desktop .fr-command:focus {
    outline: 0;
    color: #141414 !important;
    background: rgba(124, 122, 122, 0.04);
    border-bottom: 0.5px solid white;
}

a.p-navEl-link:hover, a:hover {
    color: white !important;
    background: #d0d0d0 !important;
}

.block-header {
    background: #131313 url('https://realitygaming.fr/styles/RealityGaming/background/pattern4-ts.png') repeat-x bottom;
}

h3.block-minorHeader {
    background: #131313 url('https://realitygaming.fr/styles/RealityGaming/background/pattern4-ts.png') repeat-x bottom;
}

.p-navgroup {
    background: #222 !important;
    color: white !important;
}

.username--style55 {
    font-weight: bold;
    color: #f700ff;
}

.username--style55::before {
    font-family: "FontAwesome";
    font-size: 90%;
    content: "\f040";
    color: #f700ff;
    padding-right: 4px;
    display: inline-block;
}

.block-container {
    color: #8e8e8e;
}

.block-tabHeader {
    padding: 0;
    margin: 0;
    font-weight: 400;
    font-size: 15px;
    color: #fff;
    background: #222;
    border-bottom: 1px solid #d8d8d8;
}

.tooltip--member .tooltip-content {
    color: #a0a0a0;
    text-align: left;
}

.memberTooltip-blurb span.userTitle {
    color: white;
}

.p-breadcrumbs {
    border: 1px solid #d2d2d2 !important;
    box-shadow: 0 1px 2px rgba(0,0,0,0.1);
    background: #5f5f5f;
    border-radius: 2px;
    overflow: hidden;
    zoom: 1;
    line-height: 30px;
    height: 30px;
    margin-top: 10px;
}

.block-footer {
    background: #131313 url('https://realitygaming.fr/styles/RealityGaming/background/pattern4-ts.png') repeat-x bottom;
    color: white;
}
}
