css:
@-moz-document domain("scenegames.goodolddownloads.com") {
body,
.modal-small .modal-inner,
.modal-big .modal-inner {
	background: #3c3c3c;
}
.topbar-btn.menu-dropdown > ul,
.pageseperator,
table thead {
	background: #212121;
}
.menu-dropdown > ul > li,
.block-bottominfo .game-title {
	color: #808080;
}
.menu-dropdown > ul::before {
	border-bottom: 11px solid #212121;
}
.game-blocks .block,
.progresslog,
#searchbar::before {
	background: #212121;
}
#game-content .game-release .toggle:checked + .toggle-label,
#game-content .game-release:target > .toggle + .toggle-label,
#game-content .game-release .toggle-content,
.game-card .card-bottom .card-releases .release .details-wrap,
#game-content .game-release .toggle-label:hover {
	background: #263238;
}
#game-content .game-release:last-child {
	border-bottom: 1px solid #263238;
}
#game-content .game-release {
	border-top: 1px solid #263238;
}
#game-header .container .header-info,
#game-header .container .header-info .game-links > a,
.game-card .card-bottom .card-releases .release .details-wrap .details > .name a,
.game-card .card-bottom .card-releases .release .details-wrap .details > .name #game-header .container .header-info .game-links > a,
#game-header .container .header-info .game-card .card-bottom .card-releases .release .details-wrap .details > .name .game-links > a,
.game-card .card-bottom .card-releases .release .details-wrap .details > .name,
.game-card .card-bottom .card-releases .release .details-wrap .details > .info-small .date,
.float-left,
.toggle-label,
.game-card .card-gamebuttons a,
.game-card .card-gamebuttons #game-header .container .header-info .game-links > a,
#game-header .container .header-info .game-card .card-gamebuttons .game-links > a {
	color: #a7c0cd;
}
.game-card .card-title .game-name,
.game-card .card-bottom .card-releases .release {
	color: #000;
}
.modal .modal__inner,
#game-cards-container {
	background: #0000;
}
.modal .modal__inner .modal__close,
#game-details {
	background-color: #000;
}
.game-details-bg {
	filter: brightness(65%);
	background-color: #ccc;
}
.block-buttons > span.dateblock > span {
	color: white;
}
.menu-dropdown > ul > li:hover {
	background-color: #263238;
}
.orderDir > div.selected,
.orderDir > div:hover {
	background: #263238;
}
.modal-small .modal-inner > .modal-content,
.modal-content,
table thead {
	color: grey;
}
.game-block:hover {
	background: #0009;
}
.nfo-container pre.nfo {
    line-height: 13px;
    font-size: 14px;
}
.modal .modal__inner {
	padding: 0px;
	padding-bottom: 13%;
	-webkit-transform: translateX(-49%);
	transform: translateX(-49%);
}
.modal .modal__inner .modal__close,
#game-details {
	background-color: #333;
}
.date {
	color: #ffd943;
}
#game-content .game-release .toggle-label {
	background: #4f5b6280;
}
#game-content .game-release:target {
	padding-bottom: 10px;
	background: #2f2020;
}
.modal .release-links a,.modal .release-links{
	color: #8ff;
}
.modal .release-links li:hover{
	color: #ffd943;
}
}
