css:
@-moz-document domain('priusfreunde.de') {
.header,
#header_logo,
.fb_forum-pathway,
.fb_list_top,
.fb-msgview-right-b,
.fb_sth,
.viewcover,
.fb_list_moderators,
.fb_credits,
#footer,
.fb_forum-headerdesc {
    display: none;
}
#Kunena span.fb_avatar img {
    display: none;
}
#Kunena td.fb-msgview-left {
    width: 20px !important;
    min-width: 20px !important;
}
#Kunena td.fb-msgview-right {
    width: 250px !important;
    min-width: 250px !important;
}
#Kunena span.msgtitle,
#Kunena span.msgtitle_new {
    font-size: 10px;
}
#Kunena .fb_sectiontableentry1_stickymsg {
    background-color: #e5e5e5;
}
#Kunena tr.fb_sectiontableentry2 td img,
#Kunena tr.fb_sectiontableentry1 td img {
    display: none;
}
#Kunena .fb_title,
#Kunena a.fb_title {
    font-weight: normal;
    color: grey;
    font-size: 10px;
}
/* Aktives Element im Header*/

ul#navlist #active {
    background-color: #0E155F;
}
ul#navlist #active a {
    background-image: none;
    color: white;
}
#Kunena #Kunena_tab a:hover,
#Kunena #Kunena_tab li.Kunena_item_active a {
    background-color: #0E155F;
}
.fb_blocktable {
    margin-left: 0px !important;
    min-width: 1000px;
}
#widecontent {
    margin-left: -167px;
    left: 0px;
    min-width: 1000px;
    margin-top: -90px;
}
#navhorzcontainer {
    background-color: white;
    border: 1px solid grey;
    box-shadow: 0px 0px 10px grey;
    margin-top: -60px !important;
    margin-left: -10px;
}
.maincontainer a {
    color: black !important;
}
#Kunena a.fb-topic-title {
    font-weight: normal !important;
}
table td {
    border: 1px solid transparent !important;
}

/* COLOURING + POSITIONING ELEMENTE */
html {
	background-color: grey;
}

div #Kunena {
 	border: 2px solid black;
    border-radius: 5px;
}
#navhorzcontainer {
 	width: auto;   
    left: 80px;
}

#widecontent {
    width: auto;
}
}
