css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://fantasticcontraption.com/index.php"),
url-prefix("http://fantasticcontraption.com/levels"),
url-prefix("http://fantasticcontraption.com/mydesigns"),
url("http://fantasticcontraption.com/"),
url("http://fantasticcontraption.com/editprofile"),
url-prefix("http://fantasticcontraption.com/design?"),
url-prefix("http://fantasticcontraption.com/user?"),
url-prefix("http://fantasticcontraption.com/userlevels?"),
url-prefix("http://fantasticcontraption.com/mylevels"),
url-prefix("http://fantasticcontraption.com/designs/"),
url-prefix("http://fantasticcontraption.com/designs?"),
url-prefix("http://fantasticcontraption.com/userdesigns?"),
url-prefix("http://fantasticcontraption.com/level?"),
url("http://fantasticcontraption.com/contest.php"),
url-prefix("http://fantasticcontraption.com/?"),
url("http://fantasticcontraption.com/dashboard"),
url-prefix("http://fantasticcontraption.com/search") {

/* This modifys the FC++ script, you can change it if you're screen isn't tall enough to have it float in the middle */
#fcplusplus-controls {

  margin-left: 50% !important;
  margin-right: 50% !important;
  position: fixed !important;
  width: 110px !important;
  margin-bottom: 25px !important;
  background: none !important;
  }

a[id="fcplusplus-lock-button"] {

  -moz-border-radius: 15px 0 0 15px;
  background: white !important;
  }

a[id="fcplusplus-expand-button"] {

  -moz-border-radius: 0 15px 15px 0;
  background: white !important;
  }

a[id="fcplusplus-expand-button"]:hover,
a[id="fcplusplus-lock-button"]:hover {

  background: grey !important;
  }

a[id="fcplusplus-expand-button"] img {

  padding-top: 2px !important;
  margin-left: -2px !important;
  }

div[id="footer"],
div[id="bottombar"],
div[id="adright"],
div[id="global"],
#fc2fanpreview,
div[class="topad"],
script[src="http://rotator.adjuggler.com/"],
div[id="rightcontent"] {

  display: none !important;
  }

div[id="message"] {

  font-size: 20px !important;
  }

div[id="accountbar"] {

  -moz-border-radius: 6px !important;
  padding: 0 !important;
  }

div[id="accountbar"] a {

  font-size: 16px !important;
  
  }

div[id="rightbar3"] {

  -moz-border-radius: 6px !important;
  background-color: #E9ECF3 !important;
  }

div[id="maincontent"],
div[id="container"],
div[id="rightbar"],
div[id="flash"],
div[id="mainbar"] {

  background-color: #13234C !important;
  }

h1,
h3 {

  color: #FFF !important;
  }

#rightbar3 .block ul {

  margin: 11px 0 5px !important;
  }

div[id="enter"] {

  background-color: transparent !important;
  }

body, html {

  background-image: none !important;
  background-color: #13234C !important;
  color: #FFF !important;
  }

.pages .c {

  -moz-border-radius: 2px !important;
  }

table.own-listing th {

  color: #FFF !important;
  border-right: 2px solid #3E4D6F !important;
  }

table.info td {

  border: 2px solid #7083B4 !important;
  background: #CFD7EA !important;
  color: #000 !important; 
  }

table.info th {

  background: #CFD7EA !important;
  color: #000 !important;
  }

.pages a:link, .pages a:visited {

  color: #FFF !important;
  background: #7083B4 !important;
  -moz-border-radius: 2px !important;
  }

.pages a:hover {

  color: #EEE !important;
  background: #663300 !important;
  }

.barnav {

  background: #6F78A4 !important;
  -moz-border-radius: 3px !important;
  }

legend {

  -moz-border-radius: 2px !important;
  }

#accountbar .accounthead {

  -moz-border-radius: 4px 4px 0 0 !important;
  }

a[href="../logout.php"] {

  font-size: 10px !important;
  }

table[class="conTable"] {

  color: #000 !important;
  -moz-border-radius: 3px !important;
  }

div[class="toptable"] {

  -moz-border-radius: 6px !important;
  }


div[class="rate-box"] {

  -moz-border-radius: 6px;
  background: #13234C !important;
  color: #FFF;
  }

ul[class="actions"] a {

  color: #FFF !important;
  }

fieldset {

  -moz-border-radius: 6px;
  }

#accountbar ul li {

  background-color: #CFD7EA !important;
  margin-left: 0 !important;
  }

#tools h2 {

  color: #EEE !important;
  }

#tools h2 a {

  color: #2F8EFF !important;
  }
  
#tools h2 a:hover {

  color: #9FCBFF !important;
  }
  
#accountbar ul {

  padding: 11px 5px 5px 0 !important;
  }

legend {

  background: #7083B4 !important;
  }
}
