css:
@-moz-document url(https://web.whatsapp.com/) {

/* --- MAIN LAYOUT --- */
.web {background-image: linear-gradient(to bottom, #54898c, #455a64);}
.app._3dqpi.two, .app._3dqpi.three {top:50px;}
.app-wrapper.app-wrapper-web.app-wrapper-main::after {display: none;}
.app-wrapper-web ._3dqpi {height: calc(100% - 138px);top: 19px;max-width: 1200px;}

/* --- CHAT --- */
#main {background: #fff;}
#main .pane-chat-tile {background: #fff;}

/* --- MESSAGES --- */
.message {padding:5px;font-size: .8em;border-radius: 5px;width: 100%;}
.message strong {color:#fff;}
.msg.msg-continuation {margin-bottom: 5px;}
.tail-container {display: none;}
.tail.message-out, .message-out {background: #128C7E;color:#fff;border-top-left-radius: 20px;}
html[dir=ltr] ._1i1U7._2DNgV {background: linear-gradient(to right, rgba(220, 248, 198, 0) 0%, #128C7E 50%);}
.tail.message-in, .message-in {background: #f0f0f0;border-top-right-radius: 20px;}
html[dir=ltr] ._1i1U7.jZ4tp {background: linear-gradient(to right, rgba(255, 255, 255, 0) 0%, #f0f0f0 50%);}
.message-out ._1DZAH {color:rgba(255,255,255,.6);}
.message-out .status-icon svg path {fill:rgba(255,255,255,.6);}
.message-in ._1DZAH {color:rgba(0,0,0,.6);}
.message-system {background: rgba(0,0,0,.4);border: 0;border-radius: 2px;box-shadow: none;width: auto;color:#fff;padding:5px 20px;text-shadow: none;}

/* --- CHAT UI --- */
.block-compose {border-top: 1px solid #ccc;background: #fff;padding:20px;box-shadow: 0 0 10px rgba(0,0,0,.1);}
.block-compose .input-container {border-radius: 3px;border-color:#ccc;}
.block-compose ._1UWg0 button {padding:12px 0;}

/* -- LEFT PANEL: NOTIFICATION MESSAGE --- */
html[dir] ._3YewW {background: #128C7E;}
.U0cj3, ._21zbN {color:#fff;}

}
