css:
@-moz-document domain("nicehash.com") {
body {
    background-color: #474848 !important;
    color: #BDBDBD !important;
}
h3 {
	font-weight: 400 !important;
    color: #bdbdbd !important;
    font-size: 14px !important;
    margin: 0px !important;
    padding: 8px !important;
	letter-spacing: 0 !important;
}

/* ~~~~Шапка~~~~ */
header.loggedin nav.top { background: linear-gradient(to right,#e67e3f 0%,#e0ae3a 100%) !important }
header.loggedin nav.top a:link, header nav.top a:visited, header nav.top a:active,
header nav.top a:link, header nav.top a:visited, header nav.top a:active{ 
	color: #f5f5f5 !important; 
	font-size: 13px !important;
	padding: 7px 40px 7px 0 !important;
}
header.loggedin nav.top #languages #sel_lang,
header nav.top #languages #sel_lang{ 
	color: #f5f5f5 !important;
    font-size: 13px !important;
	padding-top: 8px !important;
    background-position: right -162px !important;
}

/* ~~~~Меню~~~~ */
header.loggedin, header {
    padding-top: 32px !important;
    height: 54px !important;
	background: #525252;
    border-bottom: 1px solid #a09f9f !important;
}
header.loggedin .logo, header .logo {
    background-size: 220px 220px !important;
    width: 70px !important;
    height: 18px !important;
	filter: invert(80%) hue-rotate(180deg) brightness(150%);
}
header.loggedin nav.main a:link, header.loggedin nav.main a:visited, header.loggedin nav.main a:active, header.loggedin nav.main a:first-child,
header nav.main, header nav.main .one { margin: 0 !important }
header nav.main a:link, header nav.main a:visited, header nav.main a:active{ 
	padding: 2px 12px !important; 
	font-size: 13px !important; 
	color: #BDBDBD !important;
}

/* Элементы меню "Покупателям", "Продавцам" */
header.loggedin nav.main #buyers_button, header.loggedin nav.main #sellers_button {
	color: #BDBDBD !important;
	font-size: 13px !important; 
    padding: 2px 10px !important; 
}

/* Элементы меню "Покупателям", "Продавцам" - стрелки */
header.loggedin nav.main #buyers_button .arrow, header.loggedin nav.main #sellers_button .arrow {
	filter: invert(50%) hue-rotate(180deg) brightness(150%);
	background-position: -226px -452px !important;
}

/* Элементы меню "Покупателям", "Продавцам" - выпадающее меню */
header.loggedin nav.main #buyers_button .divider, header.loggedin nav.main #sellers_button .divider { display: none !important }
header.loggedin nav.main #buyers_button #buyers_dropdown a, header.loggedin nav.main #buyers_button #buyers_dropdown a:link,
header.loggedin nav.main #sellers_button #sellers_dropdown a, header.loggedin nav.main #sellers_button #sellers_dropdown a:link { 
	padding: 8px 10px !important;
	width: 100% !important;
}
header.loggedin nav.main #buyers_button #buyers_dropdown, header.loggedin nav.main #sellers_button #sellers_dropdown {
	top: 17px !important;
	border-radius: 0 2px 2px 2px !important;
	border-top: none !important;
	background: #525252 !important;
}

/* ~~~~Встречающая страница~~~~ */
#intro.index { filter: invert(80%) hue-rotate(170deg) brightness(150%) !important }
#indexstats h4, #indexstats .stat_info .price, #indexstats .news_box .title, #indexstats .stat_info .name { color: #bdbdbd !important }


/* ~~~~Авторизация~~~~ */
#registerintro { background: #4E4E4E !important }
#register { filter: invert(80%) hue-rotate(170deg) brightness(150%) !important }


/* ~~~~Страница "Кабинет"~~~~ */
/* Блоки "Ваши воркеры/ордеры", "Последние депозиты/выводы" */
.dashboard_first_visit_box {
    border: 1px dashed #a09f9f !important;
    border-radius: 2px !important;
    padding: 8px !important;
}

/* Блок "Текущий баланс" */
#dashboard h2 { margin: 12px auto 10px 5px !important } 
#dashboard ul.tabs {
	padding: 4px 0 0 0 !important;
    border-bottom: 1px solid #a09f9f !important;
}
#dashboard ul.tabs li {
	border-bottom: 2px solid #fff !important;
    padding: 0 4px 4px 4px !important;
	font-size: 14px !important;
}

/* Убираем иконку биткоина и выравниваем баланс */
#dashboard .right .box .image_transp { background: none !important }
#dashboard .right .box .data { 
	font-size: 14px !important;
    padding: 0 8px !important;
	min-height: 40px !important;
}

#dashboard .right .box .addr { padding: 0 8px 8px 8px !important }

/* Кнопка "Кошелёк" */
#dashboard .right .box .button {
	border-radius: 2px !important;
    padding: 4px 0 !important;
    font-size: 14px !important;
    margin: 4px !important;
}

/* ~~~~Страница "Покупателям > Мои ордеры"~~~~ */
.page.orders {
    background: none !important;
    padding: 0 !important;
}
.dashboard_first_visit_button {
	font-weight: 600 !important;
    font-size: 14px !important;
    padding: 4px 25px !important;
	margin: 10px auto !important;
	border-radius: 2px !important;
}
#dashboard .first_visit .box { padding: 8px !important }
	
/* ~~~~Страница "Покупателям > Мои пулы"~~~~ */
.page.pools input.search {
    border-radius: 2px !important;
    font-size: 13px !important;
    padding: 4px !important;
    background: #525252 !important;
}
.btn {
	padding: 4px !important;
    font-size: 14px !important;
	line-height: 1 !important;
    border-radius: 2px !important;
}

/* ~~~~Страница "Покупателям > Онлайн-рынок"~~~~ */
.page .empty {
	margin: 8px 0 0 0 !important;
	border: 1px dashed #a09f9f !important;
    border-radius: 2px !important;
    padding: 8px !important;
}
.page .empty p {
	color: #bdbdbd !important;
    font-size: 14px !important;
}

/* Кнопка "Включить ордеры" */
.marketplace .empty .button {
	border-radius: 2px !important;
    margin: 8px auto 0 auto !important;
    font-size: 13px !important;
    padding: 2px 18px !important;
}

.marketplace ul.legend a:link, .marketplace ul.legend a:visited, .marketplace ul.legend a:active { color: #bdbdbd !important }

/* ~~~~Страница "Продавцам > Моя статистика"~~~~ */
.page, .page.profile { margin: 8px 0 !important }
.frame { 
	margin: 0 8px !important;
	width: calc(100% - 15px) !important;
}

h1 {
    font-size: 14px !important;
    padding: 0 !important;
    margin: 0 !important;
	color: #BDBDBD !important;
}

/* Списки элементов (селекторы) */
.page .select, .page .select.right {
	border-radius: 2px !important;
    padding: 0px !important;
	margin-top: 0px !important;
	font-size: 13px !important;
}
.page .select select { font-size: 13px !important }

/* Разделители */
.page .divider {
	margin: 8px 0 8px 0 !important;
	border-top: 1px solid #a09f9f !important;
}
.page.profile .row { margin-bottom: 8px !important }

/* ~~Блоки "Прибыльность", "Эффективность", "Воркеры", "Невыплаченный баланс"~~ */
.frame .col_4 { background: #4E4E4E  !important; }
.page.profile .row.overflow #stats .col_4 h2 {
	background: none !important;
	border-bottom: 1px solid #a09f9f !important;
    border-radius: 2px 2px 0 0 !important;
    font-size: 12px !important;
    padding: 5px 0px !important;
}
.page.profile .row.overflow #stats .col_4 .content {
    border: none !important;
    border-radius: 0 0 2px 2px !important;
    padding: 4px 0px !important;
    height: 36px !important;
	color: #BDBDBD !important;
    font-size: 13px !important;
}

/* Заголовки блоков */
.page.profile h2, .page span.title_connector { 
	font-size: 13px !important;
    color: #BDBDBD !important;
}

/* ~~Блок "Статистика майнинга"~~ */
.page table.light {
	margin: 8px 0 !important;
    border-radius: 2px !important;
}
.page table.light tr th {
	padding: 6px 6px !important;
    border-bottom: 1px solid #a09f9f !important;
}
.page.profile .col_2_3 { margin-bottom: 0px !important }
.page.profile .col_2_3 table.light tr td {
	font-size: 13px !important;
    padding: 4px 4px !important;
}
.page.profile .col_2_3 table.light tr td.results { color: #BDBDBD !important }

/* ~~Блок "Прогноз выплат"~~ */
.page.profile .col_1_3 .payout { 
	margin: 8px 0 0 0 !important;
	padding: 6px !important;
	background: #4e4e4e;
}
.page.profile .col_1_3 .payout .payout_header { 
	background: none !important;
    background-image: none !important;
	border-radius: 2px 2px 0 0 !important;
    padding: 0px 4px 2px !important;
    font-size: 12px !important;
}
div.line div { color: #BDBDBD !important }
.page.profile .col_1_3 .payout .payout_content {
	background: none !important;
	border-radius: 0 0 2px 2px !important;
    padding: 6px 6px !important;
    font-size: 12px !important;
	border-top: 1px solid #a09f9f !important;
    border-bottom: 1px solid #a09f9f !important;
    border-left: none !important;
    border-right: none !important;
}
.page.profile .col_1_3 small { 
	font-size: 9px !important;
    line-height: 1.0 !important;
}

/* ~~Блок "Подробная статистика майнинга"~~ */
.page.profile .row.farther { 
	margin-top: 0px !important;
	    background: #4e4e4e !important;
}
.page span.title_connector { 
	margin: 8px 5px !important;
	font-weight: bold;
}
.page #stats { 
	border-radius: 2px !important;
	border: none !important;
}
.page #stats h3 {
	font-size: 13px !important;
    padding: 6px 0 4px 6px !important;
	color: #BDBDBD !important;
}
.page #stats ul { padding: 0 !important }
.page #stats ul li {
	padding: 4px !important;
    width: 90px !important;
    font-size: 12px !important;
	border-bottom: none !important;
}
.page #stats ul li.selected { border-bottom: 2px solid #fba342 !important }
.page #stats .chart { margin: 8px !important }

/* График */
.page #stats .chart { filter: invert(80%) hue-rotate(180deg) brightness(170%) }

/* ~~Блок "История выплат"~~ */
.page table { 
	margin: 8px 0px !important;
    border-radius: 2px 2px 0 0 !important;
	background: #4e4e4e !important;
    border: none !important;
}
.page table tr { border-bottom: 1px solid #a09f9f !important }
.page table tr th, .page table tr td {
    background: #4e4e4e !important;
    padding: 4px 4px !important;
    font-size: 9px !important;
	color: #bdbdbd !important;
}
.page table tr th:first-child { border-radius: 2px 0 0 0 !important }
.page table tr th:last-child { border-radius: 0 2px 0 0 !important }

/* ~~~~Страница "Кошелёк"~~~~ */
#dashboard, .page.pools {
	background: none !important;
    padding: 8px 0 8px 0 !important;
}

/* Уменьшаем кнопку "Скачать" */
.wallet .button_download {
	margin: -6px 0 4px 0 !important;
    background: #525252 !important;
    border: 1px solid #a09f9f !important;
    border-radius: 2px !important;
}
.button_download .icon {
	background-size: 500px 500px !important;
    background-position: -255px -422px !important;
	width: 20px !important;
    height: 20px !important;
    margin: 2px !important;
}

/* ~~Блок "Ваш кошелек NiceHash"~~ */
#dashboard.wallet .col_1_3 .box, #dashboard.wallet .col_2_3 .box, #dashboard.wallet .col_3_3 .box {
	background: #4E4E4E !important;
	border-radius: 2px !important;
    border: none !important;
	font-size: 12px !important;
	color: #BDBDBD !important;
    padding: 5px 0px !important;
	height: 95px !important;
}

/* Убираем символ биткоина и выравниваем баланс */
#dashboard.wallet #balance .box .image { background: none !important }
#dashboard.wallet #balance .box .data {
	padding: 8px !important;
	font-size: 12px !important;
	color: #BDBDBD !important;
	min-height: 30px !important;
}

/* Кнопки "Вывод", "Депозит" */
#dashboard.wallet #balance .box .button {
	border-radius: 2px !important;
    padding: 5px 0 !important;
}

#dashboard.wallet #pending .box .pending h3 {
	font-size: 13px !important;
	padding: 4px 0 4px 8px !important;
}
#dashboard.wallet #pending .box .pending p {
	font-size: 13px !important;
	margin: 0 8px 0 31px !important;
}
#dashboard.wallet #address .box h3 { 
	font-size: 13px !important;
	color: #bdbdbd !important;
    padding: 8px !important;
}
#dashboard.wallet #pending .box .divider {
    margin-top: 4px !important;
    border-top: 1px solid #a09f9f !important;
}
#dashboard.wallet #address .box .addr {
	font-size: 13px !important;
    font-weight: 400 !important;
    padding: 8px !important;
}
#dashboard.wallet #address .box small { padding: 8px !important }

/* Оформление блоков */
#dashboard .box {
	background: #4e4e4e !important;
    border: none !important;
    border-radius: 2px !important;
    margin: 0 0 8px 0 !important;
}

.wallet_no_transactions {
	color: #bdbdbd !important;
    font-size: 14px !important;
	padding: 8px !important;
}

/* ~~~~Страница "Настройки"~~~~ */
#dashboard h4 {
	color: #bdbdbd !important;
    font-size: 14px !important;
    margin: 8px 0 !important;
}

/* ~~Меню слева~~ */
#dashboard .box.settings ul.tabs.vertical li { padding: 8px !important }

/* ~~Контент~~ */
.settings .tab.active { padding: 8px !important }

/* Заголовки */
#dashboard .box.settings h2 {
    font-size: 14px !important;
    margin: 0 0 8px 0 !important;
    color: #bdbdbd !important;
}

/* Заголовки пунктов настроек */
#dashboard .box.settings .row { margin: 0 0 8px 0 !important }
#dashboard .box.settings .row label {
	margin: 8px 0 !important;
    font-size: 13px !important;
}

#dashboard .box.settings .divider {
	border-top: 1px solid #a09f9f !important;
    padding-top: 8px !important;
}

#dashboard .box.settings p {
	font-size: 13px !important;
	margin: 0 0 8px 0 !important;
}

.settings .box2 { 
	border: 1px solid #a09f9f !important;
    padding: 8px !important;
    margin-bottom: 8px !important;
}

/* ~~~~Оповещение о хранении куки~~~~ */
#cookie_message {
    background: #585858 !important;
    border-top: 1px solid #a09f9f !important;
}
#cookie_message p {
    color: #bdbdbd !important;
    padding: 10px 40px !important;
}
#cookie_message .button {
    padding: 4px 8px !important;
    border-radius: 2px !important;
    font-size: 11px !important;
    margin: 6px 0 0 6px !important;
}
#cookie_message a:link, #cookie_message a:active, #cookie_message a:visited { 
	padding: 8px 4px;
    margin: 1px 0 0 10px;
    text-decoration: none;
}
#cookie_message .icon { background-image: none !important }

/* ~~~~Подвал~~~~ */
footer { background: #474848 !important }
footer > div.frame > div.row > div.col_4 { 
	padding: 10px !important;
	margin-top: 20px;
}
footer h4 { margin: 0 0 10px 0 !important } 


}
