css:
@-moz-document domain("snaptools.io") {
div.wrapper,
.footer/*,
.price.scrollme .head*/{
    background: #21211f;
}
.price.scrollme .head{
    background: #f1cc02;
}
ul.check li{
    color: #ddd;
}
div .head h2,
.price .head p span{
    color: black !important;
}
blockquote, .price{
    background: #151414;
}
section#info.fw-main-row.section,
h2, h3, h6, p{
    color: #ddd;
}
#accordion .ui-accordion-content p,
#accordion h3{
    background: #151414;
    color: #ddd;
}

