css:
@-moz-document url(https://www.okex.com/spot.html) {
*::-webkit-scrollbar {
    width: 10px !important;
    height: 10px !important;
    background: #14161a !important;
    border: 1px solid #282C34 !important;
}
*::-webkit-scrollbar-button {
    display: none !important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 !important;
    box-shadow: none !important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px !important;
    background: #282C34 !important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #14161a !important;
}
body {
    color: #bec2ca;
    background: #21252B;
}
.top-content .top-bar .line {
    background: #21252B;
}
.top-content .top-bar {
    background: #21252B;
    border: 1px solid #21252B;
}
.spot-trade {
    border: solid 1px #21252B;
}
.sell-trade, .buy-trade {
    border-right: solid 1px #21252B;
}
.market-header {
    border-bottom: 2px solid #21252B;
}
.top-content .top-bar .choose-coin-type dl dt {
    color: #E3E5E8;
}.top-content .top-bar .trade-coin-info dl dt {
    color: #E3E5E8;
}
.ex-head-container {
    background: #282C34;
    box-shadow: 0 0 6px 0 rgba(0, 0, 0, 0.14), inset 0 -1px 0 0 #21252b;
}
.ex-nav .ex-nav-list li a {
    color: #bec2ca;
}
.user-account {
    background-color: #282C34;
}
.user-account .user-account-info ul li dl {
    color: #6c7384;
}
.top-content .top-bar .trade-coin-info dl dd {
    color: #6c7384;
}
.l-tt-transcation {
    background: #282C34;
    border-bottom: 1px solid #282C34;
}
.realtimebox .realtimeleft .solid .box .real-left {
    background: #41454F;
    border: 0;
}
.realtimebox .realtimeleft .solid .box .real-right {
    background: #41454F;
    border: 0;
}
.li-ct-transcation {
    background: #14161a;
    border: 0;
    color: #b9bdc6;
}
.li-ct-transcation:hover {
    background: #282C34;
}
.realtimebox .realtimeright .solid .box .real table.transaction th {
    border-bottom: 1px solid #282C34;
    background: none repeat scroll 0 0 #282C34;
    color: #bec2ca;
}
.realtimebox .realtimeright .solid .box .real {
    background: transparent;
    border: 0;
}
.realtimebox .realtimeright .solid .box .real table.transaction td {
    border-bottom: 0
}
.updateTime,
.trade-ratio,
.entrust-type,
.entrust-operation,
.entrust-price,
.tradeSliderBar .percentNumm{
    color: #6c7384;
}
.price-des,
.nodeal-ten,
.banlance,
.money-des {
    color: #bec2ca;
}
.service-charge a {
    color: #6c7384;
}
.recharge {
    background-color: transparent;
}
input {
	background-color: #14161a;
    color: #6c7384;
    padding: 2px;
    border: 0;
}
.spot-password, .spot-trade-type {
	background-color: #14161a;
    color: #6c7384;
    padding: 2px;
    border: 0;
}
.nodeal-operation-des {
    border: 0;
	background-color: #282C34;                                            
}
.buy-trade, .sell-trade{
	min-height: 360px;
    height: auto;
}
.spot-trade {
    min-height: 380px;
    height: auto;
}
#allFooter {
    background: none repeat scroll 0 0 #282C34;
}
.foot {
    background: none repeat scroll 0 0 #282C34;
}
.bubble {
    background-color: #21252B;
    color: #bec2ca;
    border: solid 1px #21252B;
}
.bubble a {
    color: #0096df
}
.updateTime .merge-level .depth-list li {
    color: #bec2ca;
}
.banner-coin-listbox {
    background-color: #1f2228;
    box-shadow: 0 1px 1px 1px #1f2228;
}
.banner-coin-listbox .banner-coin-titles {
    width: 816px;
    box-shadow: inset 0 -1px 0 0 #ebeff5;
}
.banner-coin-table tr td {
    border: none;
    border-top: 1px solid #1f2228;
}
.banner-coin-table tr td:nth-of-type(1) {
    color: #E3E5E8;
    font-size: 14px;
}
.banner-coin-table th, .banner-coin-table td {
    color: #6c7384;
}
.banner-coin-listbox .coin-title {
    color: #bec2ca;
}
.banner-coin-listbox:after {
    border-left: 1px solid #1f2228;
    border-bottom: 1px solid #1f2228;
    background: #1f2228;
}
.banner-coin-search input[type="text"] {
    border: solid 1px #14161a;
}
.banner-coin-listbox .banner-coin-titles {
    box-shadow: inset 0 -1px 0 0 #1f2228;
    background-color: transparent;
}
.banner-coin-table thead {
    background: #282C34;
}
.market-header ul li a {
    color: #6c7384;
}
.top-content .top-bar .choose-coin-type dl dd {
    font-size: 12px;
    color: #6c7384;
}
.li-ct-transcation .sellspan {
    background: #ec1313;
}
.spot-market .realtimeright .red {
    color: #ec1313;
}
.box-ct-transcation .sell {
    color: #ec1313;
}
.ex-head .ex-logo img{
background: url(https://i.imgur.com/BtgOyYE.png) left top no-repeat;
    width: 136px;
    height: 54px;
    padding-left: 136px;
    box-sizing: border-box;
    background-size: contain;
}}
