css:
@-moz-document regexp("https://manga.madokami.al/reader/.*") {
#reader-page > img {
    position: fixed;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    margin: auto;
    max-height: 100%;
    max-width: 100%;
    /*cursor: default;*/
    z-index: 3;
}

#reader::after {
    position: fixed;
    bottom: 0;
    left: 8ch;
    right: 50%;
    height: 3ex;
    word-break: break-all;
    content: attr(data-path);
    font-family: monospace;
    background: white;
    color: #333;
    z-index: 1;
}

#reader::before {
    position: fixed;
    bottom: 0;
    left: 0;
    height: 3ex;
    content: 'Reading\00a0';
    font-family: monospace;
    background: white;
    color: #333;
    z-index: 2;
}

@media all and (orientation: portrait) {
  #reader::after {
     right: 0;
  }
}
}

@-moz-document regexp("https://manga.madokami.al/reader/%252FAdmin%2520cleanup%252F.*") {
#reader::after {
    margin-left: -15ch;
}
}

