css:
@-moz-document domain("client.work-zilla.com") {
.decor,
.footerHr,
.footerHr + div,
.footer_join_cont,
.footer,
#nav_Vacancy,
#nav_Partner,
.div_friends {
    display: none;
}
body {
    background: #fff;
}
body:before {
    content: '';
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-image: url("http://rdesign-studio.ru/videos/background.jpg");
    background-repeat: no-repeat;
    backface-visibility: hidden;
    opacity: 0.4;
    background-position: 50% 180%;
}
.section {
    padding-bottom: 30px;
    padding-top: 110px;
}
.header {
    background: #fff;
    box-shadow: 0 0 15px rgba(0, 0, 0, .5);
    padding: 15px 0;
}
#ajaxLoadProgress {
    bottom: -17px;
    top: auto;
    position: absolute;
}
.nav {
    border: none;
}
.nav ul li a {
    border-radius: 5px;
    padding-left: 5px;
    padding-right: 5px;
}
.nav ul li {
    border: none;
}
.nav ul li:not(:first-child) {
    margin-left: 10px;
}
.nav ul {
    display: flex;
    width: 263px;
}
.aside_friends {
    left: auto;
    right: 0;
}
.article {
    margin-right: 200px;
    margin-left: auto;
}
#liMenuAutotasks {
    display: none;
}
.div_users,
.customer_top {
    padding: 0;
    margin-bottom: 15px;
    background-color: #fff;
}
.div_users ul,
.customer_top ul {
    display: flex;
    justify-content: center;
    padding: 0;
}
.article .div_users > a,
.article .div_users ul li a,
.customer_top ul li a {
    font-weight: 400;
    text-decoration: none !important;
    display: block;
    padding: 15px;
    position: relative;
    transition: color 300ms, background-color 300ms;
}
.article .div_users > a {
    left: 0;
    position: absolute;
}
.div_users,
ul li.active a,
.customer_top ul li.active a {
    color: #00c6f5;
}
.div_users ul li.active a:after,
.customer_top ul li.active a:after {
    content: '';
    display: block;
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0;
    height: 2px;
    background: #00c6f5;
}
}
