@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(lwn.net) {
.not-print { display: none !important; }

body > #menu { display: none !important; }
body > div.topnav-container { display: none !important; }
body > br { display: none !important; }
body > center { display: none !important; }

body > div.pure-grid { padding-left: 0em; }
body > div.pure-grid > div.lwn-u-1 { width: 100%; }
body > div.pure-grid > div.lwn-u-1 > div.PageHeadline { max-width: none; }
body > div.pure-grid > div.lwn-u-1 > div.ArticleText { max-width: none; padding: 0em 0.5em 0em 0.5em; }
body > div.pure-grid > div.lwn-u-1 > div.ArticleText > center { display: none !important; }
body > div.pure-grid > div.lwn-u-1   div.CommentBox { max-width: none; }

@media screen and (min-width:48em) { body > div.pure-grid > div.lwn-u-1 > div.ArticleText > div.pure-u-md-1-2 { width: calc(50% - 0.5em); } }
@media screen and (min-width:48em) { body > div.pure-grid > div.lwn-u-1 > div.ArticleText > div.pure-u-md-11-24 { width: calc(50% - 0.5em); } }
}
