css:
@-moz-document url-prefix("https://vk.com") {
body { font-family: 'PT Sans',sans-serif; background-color: #E8EAED; }
h1,h2,h3,h4,h5,h6 { font-family: 'Open Sans',sans-serif; font-weight: 300; margin: 10px 0 }
.top_home_link .top_home_logo { background: url(http://vk.com/images/logo.png) no-repeat !important; height: 25px !important; width: 135px !important; margin: 8px 10px 0 0 !important; }
#groups_filters_wrap { visibility: hidden; display: none; }
.ui_zoom { visibility: hidden; display: none; }
#friends_possible_block { visibility: hidden; display: none; }
.friends_photo_img {border-radius: 0px;}
.people_cell_ava img {border-radius: 0px !important;}
.cell_img {border-radius: 0px !important;}
.people_cell img { transition: all .3s ease; -webkit-transition: all .3s ease; -moz-transition: all .3s ease; }
.people_cell:hover img { opacity: 0.7; border-radius: 100px; }
.page_photo:hover img {opacity: 0.7;}
.page_photo img { transition: all .3s ease; -webkit-transition: all .3s ease; -moz-transition: all .3s ease; }
.friends_photo_wrap:hover img { opacity: 0.7; border-radius: 100px; }
.friends_photo_wrap img { transition: all .3s ease; -webkit-transition: all .3s ease; -moz-transition: all .3s ease; border-radius: 0px; }
.im_grid:hover img { -ms-transform: scale(-1.1, 1.1); -webkit-transform: scale(-1.1, 1.1); transform: scale(-1.1, 1.1); }
.im_grid img { transition: all .3s ease; -webkit-transition: all .3s ease; -moz-transition: all .3s ease; }
.im_grid,.nim-peer,._online,._im_peer_online,.nim-peer--photo-w {border-radius: 0px !important;}
div.im_grid {border-radius: 0px !important;}
	div.im_grid img {border-radius: 0px !important;}
div.nim-peer--photo {border-radius: 0px !important;}
	div.nim-peer--photo img {border-radius: 0px !important;}
.group_row_photo:hover img { opacity: 0.7; border-radius: 100px; }
.group_row_photo img { transition: all .3s ease; -webkit-transition: all .3s ease; -moz-transition: all .3s ease; border-radius: 0px; }
#audio_layer_tt {border-radius: 8px;}
#top_notify_wrap {border-radius: 8px;}
#top_notify_count {border-radius: 8px;}
#page_header_cont .back {background: #406187 !important;}
a {text-decoration: none !important;}
	a:hover {text-decoration: none !important;}
.group_link {color: #7EAE22 !important;}
.box_title_wrap {background: #406187 !important;}
.left_count_wrap { background: #7EAE22 !important; color: #ffffff !important; border-radius: 100px !important; border-color: #7EAE22 !important; border-style: solid !important; border-width: 2px 2px 2px 2px; }
	.left_count_wrap:hover { background: #739E1F !important; color: #ffffff !important; border-radius: 100px !important; }
.button_blue button.hover, .button_blue button:hover, .flat_button.hover, .flat_button:hover {background-color: #7EAE22 !important;}
.box_title {color: #fff!important;}
.ui_rmenu_item:hover, .ui_rmenu_subitem:hover, .ui_rmenu_item_sel, .ui_rmenu_item_sel:hover, em.fc_clist_hl, .ui_gridsorter_cont .ui_gridsorter_moveable, .ui_suggester_results_list ul li.active, .ui_suggester_results_list ul li:hover,  .media_selector .ms_items_more .ms_item:hover, .div.fc_tab_txt, .docs_choose_upload_area:hover, .pv_comments_header:hover, .nim-dialog.nim-dialog_muted .nim-dialog--unread, em.ts_clist_hl, .gift_tt_show_all:hover, .page_actions_item:hover, .button_gray button:hover, .flat_button.im-create--btn.button_disabled._im_confirm_creation, .wl_replies_header:hover,  .mv_desc .can_edit:hover, .olist_item_wrap:hover, .top_profile_mrow:hover, .tabbed_box .summary_tab .summary_tab3:hover, .tabbed_box .summary_tab_sel .summary_tab3:hover, .tabbed_box .summary_tab_sel a:hover, .tabbed_box .summary_tab a:hover, .tabbed_box .summary_tab_sel .summary_tab2, .flat_button, .pv_data, .nim-dialog.nim-dialog_selected,  .result_list ul li,  .im-to-end,  #pv_box, .wl_post_reply_form_forbidden,  .wall_module .page_media_thumbed_link, .album_module .page_album_nocover, .apps_settings_icons .apps_settings_payments, .apps_settings_icons .apps_settings_stats, a.wk_photo_no_border img, a.wk_photo img, img.wk_photo, img.wk_photo_no_border, .dev_mlist_item, .graffiti_tools, .graffiti_wrap, em.highlight, .search_filters_minimized_text, .slider_hint, .settings_menu_rows .olist_item_menu .olist_item_photo, .login_about_mobile,  .wdd_add2, .im-mess.im-mess_gift, .photos_edit_selection_header, .photos_container_edit_grid .photos_photo_edit_row_desc_cont, .im_stickers_my_tip, .tt .side_sh, .video_plb_wrap,   .button_blue button, .button_blue button, .page_media_poll .page_poll_percent, .group_wiki_hider:hover, #mv_comments_header, .apps_i_wrap,.apps_i_policy,.app_rate_count,.ui_tab_group_items,.im-page a.im-page--back-button:hover,.ui_ownblock:hover, .flist_item_wrap:hover, .top_notify_cont .top_notify_cont  .highlight, .im-page .im-page--top-date-bar-inner,.pv_author_block,.mv_subscribe_block .mv_subscribe_btn_count,.pv_can_edit:hover,.im-page .im-page--history-new-bar>span,.im-page .im-page--top-date-bar,.im-to-end:hover .im-to-end--label,.im-to-end .im-to-end--label,.nim-dialog.nim-dialog_classic.nim-dialog_unread-out .nim-dialog--inner-text, .nim-dialog.nim-dialog_classic.nim-dialog_unread-out.nim-dialog_muted .nim-dialog--inner-text,.im-page-pinned,.pv_narrow_column_wrap,.pe_selected,.pe_editor .pe_sticker_pack_tab:hover { background-color: #406187 !important; color: #fff !important; }
.ui_box_header_link,.toggle,.ui_crumb_count {color: #fff !important;}
.flat_button {border-radius: 2px !important;}
.profile_more_info_link { background: #FFFFFF !important; color: #406187 !important; border-radius: 100px !important; border-color: #BECEDB !important; border-style: solid !important; border-width: 1px 1px 1px 1px; }
	.profile_more_info_link:hover { background: #406187 !important; color: #fff !important; border-radius: 100px !important; border-color: #BECEDB !important; border-style: solid !important; border-width: 1px 1px 1px 1px; }
.page_actions_dd_label {color: #fff!important;}
.scrollbar_inner, .scrollbar_inner:hover {background-color: #4C6B8A !important;}
input.text.ts_input { background: #fff url(http://new.vk.com/images/icons/dev_head_magglass.png) no-repeat left !important; background-position: left 8px !important; }
.page_block, .eltt, #top_notify_wrap, .chat_onl_wrap, #page_header_cont, .fc_tab_wrap, .page_actions_wrap, #ts_cont_wrap,.im-page.im-page_classic .im-page--dialogs-search,
.im-chat-input.im-chat-input_classic,
.im-page.im-page_classic .im-page--chat-body-abs,
.im-page.im-page_classic .im-page--header-inner,.flist_sel,
.im-page_classic .im-page--chat-header-in .ui_tabs .clear_fix .page_tabs_hidden { -webkit-box-shadow: 0 0 0px 0 rgba(0, 0, 0, 0.1) !important; -moz-box-shadow: 0 0 0px 0 rgba(0, 0, 0, 0.1) !important; box-shadow: 0 0 0px 0 rgba(0, 0, 0, 0.1) !important; border-color: transparent !important; border-radius: 0px; }
#ads_left {display: none !important;}
::-webkit-scrollbar { width: 3px; height: 3px; }
::-webkit-scrollbar-track {background-color: #406187;}
::-webkit-scrollbar-track-piece {background-color: #ffffff;}
::-webkit-scrollbar-thumb { height: 50px; background-color: #406187; border-radius: 3px; }
::-webkit-scrollbar-corner {background-color: #406187;}
::-webkit-resizer {background-color: #406187;}
.wall_reply_text {color: #000 !important;}
.pv_bottom_info,.pv_author_block {background: #406187 !important;}
.page_video_thumb,.video,.fl_l {border-radius: 0px !important;}
}
