css:
@-moz-document domain("lds.org") {
.narrow #secondary,
#secondary {
    width: 29.5%;
}
.narrow .two-col #primary {
    padding-left: 15px;
    padding-right: 15px;
}
.narrow #platform-canvas-nav {
    width: 15%;
}
.narrow #platform-canvas-content {
    max-width: 85%;
}
.wrapper {
    max-width: 100%;
    width: 90%;
}
.pf-responsive #platform-canvas {
    max-width: 100%;
    width: 90%;
}
h3 {
    font: 20px/22px "Helam Slab", "Roboto Slab", Calibri, Helvetica, Arial, sans-serif;
}
.hero {
    min-width: 100%
}
#feature #carousel, #feature #carousel>li {
    margin: 0 auto;
}
#teasers img {
    width: 80%;
}
#teasers h2 br {
    display: none;
}
.video-gallery .video-list2 li {
    width: 25% !important;
}
.sidebyside-list h2 img {
    width: 100%;
}
@media (min-width: 1500px){
    .lumen-page-scan>.lumen-content-block, .lumen-page-scan>.lumen-media-block {
    	width: 90%;
    	margin-left: 5%;
	}
    .lumen-content .lumen-button[class], .lumen-content button, .lumen-content input[type=submit], .lumen-content input[type=reset], .lumen-content input[type=button]{
		padding: 15px 30px;
	}
    .lumen-media-block .lumen-media-block__caption {
    	line-height: 1.8;
    }
    .spark-feature__text[class] {
        padding-left: 7.1vw;
        padding-right: 7.1vw;
    }
}
}
