css:
@-moz-document domain("9anime.is"), domain("9anime.to"), domain("9anime.ru"), domain("9anime.ch") {
[id^="MarketGidComposite"],
[id^="BB_SK_"],
[id^="BB_SA_"],
[id^="BB_S_"] {
    display: none!important;
    visibility: hidden!important;
}
[class^="widget comment"],
[class^="widget crop"] {
    display: none!important;
    visibility: hidden!important;
}

:host ::ng-deep [class^="p"][style^="z-index: 2000;"] {
    display: none !important;
    visibility: hidden !important;
    width: 0 !important;
    height: 0 !important;
    border: 0 !important;
    border: none !important;
    z-index: -2000;
}

#disqus_thread,
#social {
    display: none!important;
    visibility: hidden!important;
}
.col-lg-17,
.col-md-17 {
    width: 100%;
}
}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=9anime-to&t_i=") {
#comment-policy.comment-policy.padding-double {
    display: none !important;
}
}

@-moz-document domain("mycloud.to"), domain("mcloud.to") {
/*player ad*/

#jwa {
    display: none !important;
    visibility: hidden !important;
    width: 0 !important;
    height: 0 !important;
    border: 0 !important;
    border: none !important;
}
.iframe {
    width: 0 !important;
    height: 0 !important;
    border: 0 !important;
    border: none !important;
}
/*player bar*/

.jw-skin-stormtrooper:not(.jw-flag-time-slider-above) .jw-controlbar,
.jw-skin-stormtrooper.jw-flag-ads-googleima .jw-controlbar {
    height: 2.5em;
}
.jw-skin-stormtrooper .jw-background-color {
    background: rgba(33, 33, 33, .8)!important;
}
.jw-skin-stormtrooper:not(.jw-flag-time-slider-above) .jw-icon-inline,
.jw-skin-stormtrooper.jw-flag-ads-googleima .jw-icon-inline,
.jw-skin-stormtrooper:not(.jw-flag-time-slider-above) .jw-icon-tooltip,
.jw-skin-stormtrooper.jw-flag-ads-googleima .jw-icon-tooltip,
.jw-skin-stormtrooper:not(.jw-flag-time-slider-above) .jw-text-elapsed,
.jw-skin-stormtrooper.jw-flag-ads-googleima .jw-text-elapsed,
.jw-skin-stormtrooper:not(.jw-flag-time-slider-above) .jw-text-duration,
.jw-skin-stormtrooper.jw-flag-ads-googleima .jw-text-duration,
.jw-skin-stormtrooper:not(.jw-flag-time-slider-above) .jw-text-countdown,
.jw-skin-stormtrooper.jw-flag-ads-googleima .jw-text-countdown {
    height: 2.5em;
    line-height: 2.5em;
}
.jw-skin-stormtrooper .jw-button-color {
    color: rgba(143, 79, 175, .6);
}
.jw-skin-stormtrooper:not(.jw-flag-touch) .jw-button-color:hover,
.jw-skin-stormtrooper .jw-button-color:focus {
    outline: 0;
    color: #8f4faf;
}
.jw-skin-stormtrooper .jw-text {
    color: #ccc;
}
.jw-skin-stormtrooper .jw-slider-horizontal .jw-rail,
.jw-skin-stormtrooper .jw-slider-horizontal .jw-buffer,
.jw-skin-stormtrooper .jw-slider-horizontal .jw-progress {
    height: .3em;
    background-color: rgba(255, 255, 255, .2);
}
.jw-skin-stormtrooper .jw-rail,
.jw-skin-stormtrooper .jw-buffer,
.jw-skin-stormtrooper .jw-progress,
.jw-skin-stormtrooper .jw-knob {
    border: none;
    border-radius: 0px;
}
.jw-skin-stormtrooper .jw-slider-horizontal {
    height: .3em;
}
.jw-skin-stormtrooper .jw-buffer {
    background: hsla(0, 0%, 100%, .3);
}
.jw-skin-stormtrooper .jw-slider-horizontal .jw-progress {
    background: #8f4faf!important;
}
.jw-skin-stormtrooper .jw-slider-horizontal .jw-knob {
    top: -0.15em;
    background: #8f4faf;
    width: .6em;
    height: .6em;
    border-width: 1px;
    border-radius: 3px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, .4);
    opacity: 0;
}
.jw-skin-stormtrooper .jw-slider-horizontal:hover .jw-knob {
    opacity: 1;
}
.jw-skin-stormtrooper:not(.jw-flag-time-slider-above) .jw-controlbar .jw-overlay,
.jw-skin-stormtrooper.jw-flag-ads-googleima .jw-controlbar .jw-overlay {
    bottom: 2.5em;
}
.jw-skin-stormtrooper:not(.jw-flag-time-slider-above):not(.jw-flag-small-player) .jw-time-tip {
    bottom: 1.25em;
}
.jw-skin-stormtrooper .jw-time-tip .jw-text,
.jw-skin-stormtrooper .jw-dock-button .jw-text {
    color: #ccc;
}
.jw-skin-stormtrooper .jw-time-tip,
.jw-skin-stormtrooper .jw-volume-tip,
.jw-skin-stormtrooper .jw-menu,
.jw-skin-stormtrooper .jw-skip {
    border-radius: .0em;
}
.jw-skin-stormtrooper .jw-volume-tip,
.jw-skin-stormtrooper .jw-menu {
    bottom: .0em;
}
.jw-skin-stormtrooper .jw-option.jw-active-option {
    color: #fff;
    background-color: #8f4faf;
}
.jw-skin-stormtrooper .jw-rail {
    background: rgba(255, 255, 255, .2);
}
.jw-skin-stormtrooper .jw-slider-vertical .jw-progress {
    background: #8f4faf;
}
.jw-skin-stormtrooper .jw-slider-vertical .jw-rail,
.jw-skin-stormtrooper .jw-slider-vertical .jw-progress {
    width: .3em;
}
.jw-skin-stormtrooper .jw-slider-vertical .jw-knob {
    width: .6em;
    height: .6em;
    background: #8f4faf;
    left: -0.15em;
    box-shadow: 0 2px 5px rgba(0, 0, 0, .4);
}
.jw-skin-stormtrooper .jw-rail,
.jw-skin-stormtrooper .jw-buffer,
.jw-skin-stormtrooper .jw-progress,
.jw-skin-stormtrooper .jw-knob {
    border-radius: 4px;
}
.jw-skin-stormtrooper .jw-slider-vertical .jw-knob {
    margin-bottom: -0.3em;
    opacity: 0;
}
.jw-skin-stormtrooper .jw-slider-vertical:hover .jw-knob {
    margin-bottom: -0.3em;
    opacity: 1;
}
.jw-skin-stormtrooper:not(.jw-flag-touch) .jw-option:hover {
    color: #fff;
}
}

@-moz-document domain("www.rapidvideo.com") {
.jw-skin-tube .jw-controlbar.jw-background-color {
    background-color: rgba(33, 33, 33, .8)!important;
    background-image: none!important;
}
.jw-skin-tube .jw-button-color {
    color: rgba(143, 79, 175, .6);
}
.jw-skin-tube .jw-button-color:focus,
.jw-skin-tube .jw-button-color:hover,
.jw-skin-tube:not(.jw-flag-touch) .jw-button-color:hover {
    color: #8f4faf;
}
.jw-skin-tube .jw-progress {
    background: #8f4faf;
}
.jw-skin-tube .jw-slider-horizontal .jw-knob {
    background-color: #8f4faf;
    box-shadow: 0 2px 5px rgba(0, 0, 0, .4);
    opacity: 0;
}
.jw-skin-tube .jw-slider-horizontal:hover .jw-knob {
    opacity: 1;
}
.jw-toggle {
    color: rgba(143, 79, 175, .6);
}
.jw-toggle:hover {
    color: #8f4faf;
}
.jw-knob {
    background-color: #8f4faf;
}
.jw-slider-vertical .jw-knob {
    box-shadow: 0 2px 5px rgba(0, 0, 0, .4);
    opacity: 0;
}
.jw-slider-vertical:hover .jw-knob {
    opacity: 1;
}
}
