css:
@-moz-document domain("strimi.pl") {
header {
    background-color: #616161;
    border-bottom: 1px solid #424242;
}
body {
    background-color: #212121;
    color: #f5f5f5;
}
a {
    color: #00C853;
}
.btn-header-menu.active,
.btn-header-menu:focus,
.btn-header-menu:hover {
    border-color: #00C853;
}
.btn-header-menu.tagActive {
    background-color: #00C853;
}
.btn-header-menu.add-content {
    border-color: #00C853;
}
.btn-header-login {
    border: 2px solid #00C853;
}
.btn-header-login:hover {
    background: #00C853;
}
.post-single .short-content h1,
.post-single .short-content h2 {
    color: #eee;
}
.sidebar.user-account ul li.username {
    color: #00C853;
}
.post-single .short-content .text {
    color: #f5f5f5;
}
.post-single .short-content .text:hover {
    color: #f5f5f5;
}
.post-single .short-content .meta-tags a {
    color: #2979FF;
}
