    color: #5C6BC0;
}
.comments-header-name {
    color: #ddd;
}
.comments-header-sorting .button-sorting-comments.active,
.comments-header-sorting .button-sorting-comments:hover {
    color: #00C853;
}
.comments-header-sorting .button-sorting-comments {
    color: #ddd;
}
.comments-header-name-count {
    border: none;
    background-color: #212121;
    color: #ddd;
}
.comments.form {
    background-color: #616161;
    border: 1px solid #424242;
}
.comments.post-comments {
    background-color: #616161;
}
.comments-single-header-transfer,
.comments-single-header-username {
    color: #00C853;
}
.btn-green {
    background-color: #00C853;
}
.comments-single-content {
    color: #f5f5f5;
}
.post-single .short-content h1 a,
.post-single .short-content h2 a {
    color: #EEE;
}
.post-single .short-content h1 a:hover,
.post-single .short-content h2 a:hover {
    color: #00C853;
}
.post-single {
    background-color: #616161;
}
.post-single .short-content .meta-content .added,
.post-single .short-content .meta-content .category,
.post-single .short-content .meta-content .comments,
.post-single .short-content .meta-content .domain,
.post-single .short-content .meta-content .from,
.post-single .short-content .meta-content .name,
.post-single .short-content .meta-content .time,
.post-single .short-content .meta-content .to,
.post-single .short-content .meta-content .user-reputation,
.post-single .short-content .meta-content .vote-list {
