}
.post-single .short-content .meta-content .added a,
.post-single .short-content .meta-content .category a,
.post-single .short-content .meta-content .comments a,
.post-single .short-content .meta-content .domain a,
.post-single .short-content .meta-content .from a,
.post-single .short-content .meta-content .name a,
.post-single .short-content .meta-content .time a,
.post-single .short-content .meta-content .to a,
.post-single .short-content .meta-content .user-reputation a,
.post-single .short-content .meta-content .vote-list a {
