}
.post-single .short-content .meta-content .added a:hover,
.post-single .short-content .meta-content .added:hover,
.post-single .short-content .meta-content .category a:hover,
.post-single .short-content .meta-content .category:hover,
.post-single .short-content .meta-content .comments a:hover,
.post-single .short-content .meta-content .comments:hover,
.post-single .short-content .meta-content .domain a:hover,
.post-single .short-content .meta-content .domain:hover,
.post-single .short-content .meta-content .from a:hover,
.post-single .short-content .meta-content .from:hover,
.post-single .short-content .meta-content .name a:hover,
.post-single .short-content .meta-content .name:hover,
.post-single .short-content .meta-content .time a:hover,
.post-single .short-content .meta-content .time:hover,
.post-single .short-content .meta-content .to a:hover,
.post-single .short-content .meta-content .to:hover,
