.post-single .short-content .meta-content .vote-list a:hover,
.post-single .short-content .meta-content .vote-list:hover {
    color: #00C853;
}
.post-single .short-content .meta-content .from-domain {
    color: #eee;
}
.post-single .short-content .meta-content .from-domain:hover {
    color: #00C853;
}
.post-single .short-content .post-icons ul li {
    color: #eee;
}
.post-single .short-content .post-icons ul li:last-child {
    color: #eee;
}
.post-single .image-content.blank {
    background-color: #616161;
}
.post-single .vote-content .voteUp .arrow {
    background-image: none;
    color: #00C853;
}
.post-single .vote-content .voteUp {
    background-color: #BDBDBD;
}
.post-single .vote-content .voteUp:hover {
    background-color: #00C853;
}
