}
.post-single .vote-preview {
    color: #ddd;
}
.sidebar {
    background-color: #616161;
    border: 1px solid #424242;
}
.sidebar-content-language ul li {
    color: #EEE;
}
.sidebar-content-tags ul li a .name,
.sidebar-content-users ul li a .name {
    color: #00C853;
}
.sidebar-content-tags ul li a .count,
.sidebar-content-users ul li a .count {
    color: #ddd;
}
.sidebar-content-language ul li.active,
.sidebar-content-language ul li:hover {
    color: #00C853;
}
.sidebar-content-exchange-cantor ul li .name {
    color: #00C853;
}
.sidebar-content-exchange-cantor ul li {
    color: #ddd;
}
.sidebar-title {
    color: #eee;
}
.sidebar-content-exchange-cantor .information {
